        }
        vec3 aa = vec3(a.xy * r, 0.);
        vec3 bb = vec3(b.xy * r, 0.);
        vec3 cc = vec3(c.xy * r, 0.);

        a = (aa + bb) / 2.;
        b = (bb + cc) / 2.;
        c = (cc + aa) / 2.;

    }

    fragColor = vec4(e ? 1. : 0., uv.xy + vec2(.5, .5), 1.0);
}

