void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const mat3 p = mat3(13.323122,23.5112,21.71123,21.1212,28.7312,11.9312,21.8112,14.7212,61.3934);
	vec2 uv = iMouse.xy/iResolution.xy + vec2(1.,iResolution.y/iResolution.x)*fragCoord.xy / iResolution.xy;
	vec3 acc = vec3(0.0);
	float dof = 5.*sin(iTime*.1);
	for (int i=0;i<LAYERS;i++) {
		float fi = float(i);
		vec2 q = uv*(1.+fi*DEPTH);
		q += vec2(q.y*(WIDTH*mod(fi*7.238917,1.)-WIDTH*.5),SPEED*iTime/(1.+fi*DEPTH*.03));
		vec3 n = vec3(floor(q),31.189+fi);
		vec3 m = floor(n)*.00001 + fract(n);
		vec3 mp = (31415.9+m)/fract(p*m);
		vec3 r = fract(mp);
		vec2 s = abs(mod(q,1.)-.5+.9*r.xy-.45);
		s += .01*abs(2.*fract(10.*q.yx)-1.); 
		float d = .6*max(s.x-s.y,s.x+s.y)+max(s.x,s.y)-.01;
		float edge = .005+.05*min(.5*abs(fi-5.-dof),1.);
		acc += vec3(smoothstep(edge,-edge,d)*(r.x/(1.+.02*fi*DEPTH)));
	}
	fragColor = vec4(vec3(acc),1.0);
}

#define TIME (iTime)

// returns average value from kaliset
vec3 kali_trees(in vec2 uv)
{
    uv = uv.yx / 100. - vec2(0.022,0.04);
    uv.x += 0.001*sin(TIME/33.);
    uv.y += 0.003*sin(TIME/17.3);
    vec3 p = vec3(uv, .03);

    vec3 col = vec3(0.);
    const int num_iter = 67;
    for (int i=0; i<num_iter; ++i)
    {
        p = abs(p) / dot(p, p);
        col += exp(-p*22.);
        p -= vec3(1.+0.005*sin(TIME/11.), 0.585, .03);
    }
    col /= float(num_iter);
    col *= 4.;

    //col = pow(clamp(col, 0., 1.), vec3(2.));

    return col;
}

vec3 kali_stars(in vec2 uv)
{
    uv = (uv+vec2(2.,1.)) / 14.;
    uv.x += sin(TIME/150.);
    vec3 p = vec3(uv, .03);

    vec3 col = vec3(0.);
    const int num_iter = 50;
    for (int i=0; i<num_iter; ++i)
    {
        p = abs(p) / dot(p, p);
        col += exp(-p*32.);
        p -= vec3(.285, .409, .874);
    }
    col /= float(num_iter);
    col *= 4.;

    //col = pow(clamp(col, 0., 1.), vec3(2.));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 suv = fragCoord.xy / iResolution.xy;
    vec2 uv = (fragCoord.xy - .5*iResolution.xy) / iResolution.y * 2.;

    vec3 kt = kali_trees(uv);
	vec3 ks = kali_stars(uv);

    vec3 col = mix(vec3(.9,.3+.2*cos(suv.y*8.),.1), vec3(.5,.2+ks.x, 1.), suv.y-ks.z);

    col *= smoothstep(0.0, .2, kt.y-.6-suv.y*.1);


    fragColor = vec4(col,1.0);
}

