float hash(vec2 p)
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}


float fbm(vec2 uv, float t)
{
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f;
    f  = 0.5000*noise( uv+vec2(+t, +t) ); uv *= m;
    f += 0.2500*noise( uv+vec2(+t, -t) ); uv *= m;
    f += 0.1250*noise( uv+vec2(-t, +t) ); uv *= m;
    f += 0.0625*noise( uv+vec2(-t, -t) ); uv *= m;
    return f;
}


//Scene Start

vec2 sim2d(
  in vec2 p,
  in float s)
{
   vec2 ret=p;
   ret=p+s/2.0;
   ret=fract(ret/s)*s-s/2.0;
   return ret;
}

vec3 stepspace(
  in vec3 p,
  in float s)
{
  return p-mod(p-s/2.0,s);
}

//Object
float obj(in vec3 p)
{ 
  vec3 fp=stepspace(p,2.0);;
  float d=fbm(fp.xz/20., iTime/2.)*3.;//sin(fp.x*0.3+iTime*4.0)+cos(fp.z*0.3+iTime*2.0);
  p.y=p.y+d;
  p.xz=sim2d(p.xz,2.0);
  float c2=length(p)-0.5;
  return c2;
}

//Object Color
vec3 obj_c(vec3 p)
{
  vec2 fp=sim2d(p.xz-1.0,4.0);
  if (fp.y>0.0) fp.x=-fp.x;
  if (fp.x>0.0) return vec3(1.0,0.0,0.0);
    else return vec3(1.0,1.0,1.0);   
}

