const float maxRender = 10e4; // Max distance to ray march
const float epsilon = 0.001; // Margin of error
const vec3 light = vec3(1.5, 1.5, -1.5); // Light position
const vec3 up = vec3(0, 1, 0);

// Spheres
int sphereCount = 2;
vec3 sphereCenters[2];
float sphereRadius[2];
float floorHeight = -2.0;

// Find distance between sphere and ray
float sphereDistance(vec3 p, vec3 center, float radius) {
    return distance(p, center) - radius;
}

// Find normal of sphere from hit location
vec3 sphereNormal(vec3 p, vec3 center) {
    return normalize(p - center);
}

// Get the hit location for a sphere and ray
float sphereHit(vec3 o, vec3 d, vec3 center, float radius) {
    // Keep track of the distance we have traveled
    float dist = 0.0;

    // Kill the marching if we go too far
    while(dist <= maxRender) {
        // Get distance to sphere
        float tempDist = sphereDistance(o, center, radius);

        // Add it to our distance counter and update the ray origin
        dist += tempDist;
        o += d * tempDist;

        // If we hit a sphere, then return the hit
        if(tempDist <= epsilon) return dist;
    }

    // We didn't hit anything
    return -1.0;
}

// Get the hit for the floor
float floorHit(vec3 o, vec3 d, float height) {
    float du = dot(d, up);
    if(du == 0.0) return -1.0;

    float dist = dot(vec3(0, height, 0) - o, up) / du;
    if(dist < 0.0) return -1.0;

    return dist;
}

vec2 findHit(vec3 origin, vec3 ray, int ignore) {    
    // Find a hit
    float hit = maxRender;
    int hitID = -1;

    for(int i = 0; i < sphereCount; i++) {
        if(i == ignore) continue;
    	float tempHit = sphereHit(origin, ray, sphereCenters[i], sphereRadius[i]);
        if(tempHit < hit  && tempHit >= 0.0) {
            hit = tempHit;
            hitID = i;
        }
    }

    if(ignore != -1) {
        // Check for hit with floor
        float tempHit = floorHit(origin, ray, floorHeight);
        if(tempHit < hit && tempHit >= 0.0) {
            hit = tempHit;
            hitID = -1;
        }
    }

    return vec2(hit == maxRender ? -1.0 : hit, hitID);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Create ray for current pixel
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ray = normalize(vec3(uv, 1.0));
    vec3 origin = vec3(0.0, 0.0, -3.0);

    // Initialize the spheres
    sphereCenters[0] = vec3(0.0);
    sphereCenters[1] = vec3(cos(iTime) * 1.8, sin(iTime) * 1.8, -0.8);
    sphereRadius[0] = 1.0;
    sphereRadius[1] = 0.5;

    // Find a hit
    vec2 tempHit = findHit(origin, ray, -10);
    float hit = tempHit.x;
    int hitID = int(tempHit.y);

    // If no hit, then use the sky color
    if(hit < 0.0) {
        fragColor = vec4(0.3, 0.5, 0.7, 1.0);
        return;
    }

    // Find hit point & light direction
    vec3 hitPoint = origin + (ray * hit);
    vec3 lightDir = normalize(light - hitPoint);

    // Find if we need a shadow
    vec2 shadowHit = findHit(hitPoint, lightDir, hitID);
    float shadow = shadowHit.y >= 0.0 ? 0.5 : 0.0;

    // Find the normal of the sphere and the diffuse lighting from the normal
    vec3 nml = hitID == -1 ? vec3(0, 1, 0) : sphereNormal(hitPoint, sphereCenters[hitID]);
    float diffuse = max(dot(nml, lightDir) + 0.4 - shadow, 0.0) * 0.95;

    fragColor = vec4(vec3(diffuse), 1.0);
}

// Number of plane waves
const int K = 5;

// Number of stripes per wave
const int NUM_STRIPES = 7;

const int NUM_FREQUENCIES = 10;

// The main (central) spatial frequency
float MEAN_FREQUENCY = 2.;

// The spread of the spatial frequency envelope
const float SIGMA = 2.;

const float PERIOD = 4.;

const float PI = 4.0 * atan(1.0);

float mean;

float gaussian(float x) {
    x -= mean;
    return exp(-x * x / 2.) / SIGMA;
}

// Adjust the  wavelengths for the current spatial scale
float wavelength(int i, float sc) {
    return pow(2., float(i)) * sc;
}

// Modulate each wavelength by a Gaussian envelope in log
// frequency, centered around aforementioned mean with defined
// standard deviation
float weight(int i, float sc) {
    return gaussian(log(wavelength(i, sc)));
}

// 7-th order smoothstep function:
// https://en.wikipedia.org/wiki/Smoothstep
// https://gist.github.com/kylemcdonald/77f916240756a8cfebef
float superSmooth(float x) {
	float xSquared = x * x;
	return xSquared * xSquared * (x * (x * (x * -20. + 70.) - 84.) + 35.);
}

float quasi(in vec2 uv, float rotation) {
	float scale = pow(0.5, fract(iTime / PERIOD));

    float weightSum = 0.;
    for (int l = 0; l < NUM_FREQUENCIES; l++) {
        weightSum += weight(l, scale);
    }

    // Cartesian coordinates
	vec2 coords = uv * 2. * PI * float(NUM_STRIPES);

    float c = 0.;  // Accumulator

    // Iterate over all k plane waves
    for (int t = 0; t < K; t++) {
    	float tScaled = ( float(t) / float(K) + rotation ) * PI;
        vec2 omega = vec2(cos(tScaled), sin(tScaled));

        // Compute the phase of the plane wave
        float ph = dot(coords, omega);

        // Take a weighted sum over the different spatial scales
        for (int l = 0; l < NUM_FREQUENCIES; l++) {
			c += cos(ph * wavelength(l, scale)) * weight(l, scale);
        }
    }
    // Convert the summed waves to a [0,1] interval
    // and then convert to color
    return superSmooth((c / (weightSum * float(K)) + 1.) / 2.);

}

void mainImage(out vec4 col, in vec2 coord) {
    vec2 uv = (coord.xy - iResolution.xy / 2.) / iResolution.x;
    vec2 dipole = vec2(.2,.0);
    float rot = iTime/17.;
    MEAN_FREQUENCY = 2.;
    mean = MEAN_FREQUENCY * log(2.);

    col =vec4(.0);
    col.r +=  quasi(uv-dipole,rot)*.8;

    MEAN_FREQUENCY *= sqrt(2.);
    mean = MEAN_FREQUENCY * log(2.);
    col.b += quasi(uv+dipole,-rot*sqrt(3./2.));

    col.g = (col.r+col.b)/2.;
}

