}


//Track current shape
int shapeId = -1;
void resetShape(){
  shapeId = -1;
}
float track(float D, float d, int index){
    if(D > d){
        D = d;
        shapeId = index;
    }
    return D;
}

//Building geomery
float sphere(vec3 p, float r) {
    return length(p) - r;
}
float box(vec3 p, vec3 b){
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

vec3 repeat(vec3 p, vec3 d){
    return mod(p,d)- d / 2.; 
}

float room(vec3 p){
  float d = 1.0e10;

  float walls =
    max(
      -box(p,vec3(40.0,7.,40.)),
       box(p,vec3(45.0,10.,45.))
    );

  float colls = box(
      repeat(p, vec3(13.)),
      vec3(0.8,10.,0.8)
  );

  d = track(d,walls,0);
  d = track(d,colls,1);
  d = track(d,sphere(p-vec3(0.,0.,5.0),1.5 * (1.5 + sin(1.0+p.y + 2.0*iTime)/2.)),2);

  return d;
}


float world(vec3 p){
    float d = 1.0e10;

    d = min(d,room(p));

    return d;
}

vec3 normal(vec3 p){
    vec2 e = vec2(0,0.01);
    float d = world(p);
    return normalize(
        vec3(
        	world(p + e.yxx) - d,
        	world(p + e.xyx) - d,
        	world(p + e.xxy) - d
            )
    );
}


float tracer(vec3 O, vec3 D,float N){

    for(int i = 0;i < 512;i++){
        float w = world(O + D*N);

        if(w < .005)
            return N;

        N += w * 0.5;
    }
    return -1.0;
}
//Lighting
vec3 applyLight(vec3 p, vec3 n,vec3 c, Light l){
  	vec3 toLight = l.pos - p;
    float emb = 0.04;
  	vec3 lightDir = normalize(toLight);
  	float d = length(toLight);

  	float k = dot(n,lightDir) * l.power;
    if(k < 0.)
    	return c * emb;

    //Shadow
    float lightTrace = tracer(p,lightDir,0.3);
    if(lightTrace != -1. && lightTrace <= d)
    	return c * emb;
    //

    float g = dot(vec3(k*k,k,1.0),1.0/(d*l.distKoef));

  	return c*l.color*g + c * emb;

}


vec3 lighting(vec3 p, vec3 n, vec3 c){

  vec3 color = vec3(0.0);

  for(int i = 0;i < LIGTHS;i++)
  	color += applyLight(p,n,c,getLight(i));

  return color;
}





//Cameras
vec3 camera(vec2 fragCoord){
    vec2 uv = 2.0 * (fragCoord.xy / iResolution.xy) - 1.;
    vec2 p = uv * vec2(1.,iResolution.y/iResolution.x);
    return normalize(vec3(p,1.0));
}

vec3 camera360(vec2 fragCoord){
    vec2 uv = (2.0 * fragCoord / iResolution.xy) - 1.;
    vec2 a = vec2(uv.x * PI,uv.y * PI/2.);
    vec3 vec = vec3(sin(a.x) * cos(a.y),sin(a.y),cos(a.y)*cos(a.x));
    return vec;
}



vec3 getColor(vec3 O, vec3 D , out vec3 pos , out vec3 out_n, out int shape){
    resetShape();
    float t = tracer(O,D,0.4);
    shape = shapeId;
    vec3 color = vec3(0.0);
    pos = O + D * t;
    vec3 n = normal(pos);

    out_n = reflect(D,n);

    float ttt = float(shape+1);
    if(t > 0.0)
        color = lighting(pos,n,getMaterial(shape).color);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 o = vec3(0.,1.,-3.);
    vec3 d = camera360(fragCoord);

    vec3 
        tN1 = vec3(0.0),
        tN2 = vec3(0.0),
        tP1 = vec3(0.0),
        tP2 = vec3(0.0),
        color = vec3(0.0);

    tN1 = d;
    tP1 = o;

    float k = 1.;
    int shape = -1;
    for(int i = 0;i < 2;i++){
        color += getColor(tP1,tN1,tP2,tN2,shape) * k;
        k *= getMaterial(shape).reflectionFactor;
        if(k == 0.) break;


        tP1 = tP2;
        tN1 = tN2;
    }



	fragColor = vec4(sqrt(color),1.0);
}

// inspired by https://www.facebook.com/photo.php?fbid=10213540067038752&set=pcb.10213540068238782&type=3&theater

#define TAU 6.28318531
#define hash(x) fract(sin(x)*1e4)
#define h2(x,m) hash(dot(mod(x,m),vec2(3,5)))
#define h3(x) hash(dot(mod(x,20.),vec3(3,5,7)))

float n2(vec2 x, vec2 m) {
    vec2 i=floor(x),
        f=smoothstep(0.,1.,fract(x));
    vec2 oi=vec2(0.,1.);
    return mix(
        mix(h2(i+oi.xx,m),h2(i+oi.yx,m),f.x),
        mix(h2(i+oi.xy,m),h2(i+oi.yy,m),f.x),
        f.y);
}

float n3(vec3 x) {
    vec3 i=floor(x),
        f=smoothstep(0.,1.,fract(x));
    vec2 oi=vec2(0.,1.);
    return mix(
        mix(
            mix(h3(i+oi.xxx),h3(i+oi.yxx),f.x),
            mix(h3(i+oi.xyx),h3(i+oi.yyx),f.x),
            f.y),
        mix(
            mix(h3(i+oi.xxy),h3(i+oi.yxy),f.x),
            mix(h3(i+oi.xyy),h3(i+oi.yyy),f.x),
            f.y),
        f.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    float ac=atan(uv.y,uv.x),
        lc=length(uv);
    uv+=(n3(vec3(uv*30.,iTime))-.5)*.02;
    float a=atan(uv.y,uv.x),
        l=length(uv);
    a+=sin(iTime+l*10.)*.2;
    vec3 c=mix(vec3(0.,.4,1.),vec3(0.,.8,1.),smoothstep(.6,.62,n2(vec2(a/TAU*80.,l*5.-iTime*.5),vec2(80.,1e4))+.2-l*.3));
    c-=smoothstep(.6,.62,n2(vec2(a/TAU*20.,l*30.-iTime*3.),vec2(20.,1e4))+l*.2)*.5;
    vec3 cc=mix(vec3(.9,.7,.1),vec3(1.,1.,.4),smoothstep(.5,.52,n3(vec3(ac/TAU*20.+sin(iTime*2.+lc*50.)*1.,lc*100.-iTime*1.,iTime))));
    c=mix(cc,c,smoothstep(.1,.11,lc));
	fragColor = vec4(c,1.);
}

float dents = 13.0; // Nombre de dents de la roue
float cirgen = .25;  // Epaisseur du "pneu" La valeur doit tre gale ou suprieure  .14
float tdents = .055; // Longueur des dents
float biseaudents = .75;  // Forme des dents (arrondi le bout des dents, les coupe  une certaine valeur)
float rayroue = 6.0; 
float raytrou = .09;
float cirtrou = .6;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                -sin(_angle),-cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
	vec2 p = vec2(.9,.5)-uv;
    p*=rotate2d( -iTime);
    vec3 c = vec3 (1.0);
    float r = cirgen+tdents*clamp((cos(atan(p.x,p.y)*dents)),0.0,biseaudents);
    r=min(min(r,step(.03,length(p))),step(raytrou+.04*(sign(cos((atan(p.x,p.y)*rayroue)))),length(p))) ;
    c*=min(step(r,length(p)),step(.05,length(p)));
	fragColor = vec4(c,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    //rotate center
    p -= vec2(sin(iTime) / 1.5, cos(iTime) / 1.5);

    float angle = atan(p.y, p.x);
    float distance = length(p);
    float color = sin(angle + distance * 15.0 + 30.0 / exp2(sin(iTime)));
    float color2 = sin(angle + distance * 15.0 + 30.0 / exp2(sin(iTime)) + 3.14);
    color = 1.0 - color;
    color = clamp(color, 0.0, 0.6);
    color2 = 1.0 - color2;
    color2 = clamp(color2, 0.0, 0.6);



    vec3 pixel = vec3(color, color2, color);
   	fragColor = vec4(pixel, 1.0);
}

