#define PI 3.141592653
vec2 polar(vec2 uv)
{
    float angle = atan(uv.y,uv.x);
    float radius = length(uv);

    return vec2(radius, angle);
}

float polarCircle(vec2 uv, float r, float angle, float size)
{
    float dist = uv.x*uv.x+r*r-2.0*uv.x*r*cos(angle-uv.y);
    //return 0.00002/dist;
    return smoothstep(size/iResolution.x,0.0,dist);

}


vec2 color2(vec2 uv)
{
    vec2 col= vec2(0.0);
    float r = fract(iTime)*0.4+0.1;

    for(float i = 0.0; i <2.0*PI; i+=0.1*PI)
    {
    col +=vec2(polarCircle(uv,r,i,0.05));
    }
    col.y = smoothstep(0.0,1.0,1.0-fract(iTime)*1.2);
    col.x *= col.y;
    return col;
}

vec2 color3(vec2 uv, float angle, float dir, float start)
{
    vec2 col = vec2(0.0);
    float r =0.04;
    vec2 offset = vec2(0.5*dir*pow((fract(iTime)-0.2)*2.0,3.0),0.0);

    vec2 pos = vec2(uv.x*cos(uv.y),uv.x*sin(uv.y));
    vec2 diff = pos+(offset);
    vec2 polardiff = polar(diff);

    for(float i = start*2.0; i <6.0; i+=2.0)
    {
        col += vec2(polarCircle(polardiff,r*i,angle,0.8));
    }
    col.y = smoothstep(0.0,0.3,pow(1.0-fract(iTime),2.0));
    col.x *= col.y;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float mx = max(iResolution.x, iResolution.y);
	vec2 uv = polar((2.0*fragCoord.xy - iResolution.xy)/mx);    

    fragColor.y=1.0;

    vec2 col= color2(uv);
    col += color3(uv,0.25*PI,1.0,0.0);
    col += color3(uv,-0.25*PI,1.0,1.0);
    col += color3(uv,1.25*PI,-1.0,0.0);
    col += color3(uv,PI-0.25*PI,-1.0,1.0);
    col.x = smoothstep(0.0,1.0,col.x);
	fragColor = vec4(col.x);
}

