float EngDf (vec3 p, float dMin)
{
  vec3 q;
  float wRad, d, aw, sx;
  wRad = 0.8;
  q = p - vec3 (0., -0.2, 0.5);
  d = max (PrCapsDf (q, 1., 2.), - (q.z + 1.7));
  if (d < dMin) { dMin = d;  idObj = ID_Eng; }
  q = p;  q.z = abs (q.z - 0.85);  q -= vec3 (0., -0.2, 1.8);
  d = PrCylDf (q, 1.05, 0.05);
  if (d < dMin) { dMin = d;  idObj = ID_Band; }
  q = p - vec3 (0., -1.3, -0.25);
  d = PrBoxDf (q, vec3 (1., 0.1, 3.2));
  if (d < dMin) { dMin = d;  idObj = ID_Base; }
  q = p - vec3 (0., -1.4, 3.);
  d = PrBoxDf (q, vec3 (1.1, 0.2, 0.07));
  if (d < dMin) { dMin = d;  idObj = ID_Base; }
  q.x = abs (q.x);  q -= vec3 (0.6, 0., 0.1);
  d = PrCylDf (q, 0.2, 0.1);
  if (d < dMin) { dMin = d;  idObj = ID_Roof; }
  q = p - vec3 (0., 0.01, -1.75);
  d = max (max (PrBoxDf (q, vec3 (1., 1.4, 0.6)),
     - PrBoxDf (q - vec3 (0., 0., -0.2), vec3 (0.95, 1.3, 0.65))),
     - PrBoxDf (q - vec3 (0., 0.7, 0.), vec3 (1.1, 0.4, 0.5)));
  q.x = abs (q.x);  q -= vec3 (0.4, 1., 0.4);
  d = max (d, - PrBoxDf (q, vec3 (0.35, 0.15, 0.3)));
  if (d < dMin) { dMin = d;  idObj = ID_Cabin;  qHit = q; }
  q = p - vec3 (0., -2.4, -1.75);
  d = max (PrCylDf (q, 4., 0.65), - (q.y - 3.75));
  if (d < dMin) { dMin = d;  idObj = ID_Roof; }
  q = p - vec3 (0., -0.5, -3.15);
  d = PrBoxDf (q, vec3 (1., 0.7, 0.3));
  if (d < dMin) { dMin = d;  idObj = ID_Coal;  qHit = q; }
  q = p - vec3 (0., -1.4, -3.5);
  d = PrCylDf (q.xzy, 0.4, 0.03);
  if (d < dMin) { dMin = d;  idObj = ID_Cpl; }
  q = p;  q.xz = abs (q.xz);  q -= vec3 (trkWid - 0.12, -1.4, 1.1);
  d = min (max (min (PrCylDf (q.zyx, wRad, 0.1),
     PrCylDf (q.zyx - vec3 (0.,0., -0.07), wRad + 0.05, 0.03)),
     - PrCylDf (q.zyx, wRad - 0.1, 0.12)), PrCylDf (q.zyx, 0.15, 0.10));
  if (d < dMin) { dMin = d;  idObj = ID_Wheel; }
  if (! isShad) {
    q = p;  q.x = abs (q.x);  q -= vec3 (trkWid - 0.17, -1.4, 1.1 * sign (q.z));
    aw = - (trVel / wRad) * tRun;
    q.yz = Rot2D (q.yz, aw);  
    q.yz = Rot2D (q.yz, 2. * pi * floor ((atan (q.y, q.z) + pi) * 8. /
       (2. * pi) + 0.5) / 8.);
    q.z += 0.5 * wRad;
    d = PrCylDf (q, 0.05, 0.5 * wRad);
    if (d < dMin) { dMin = d;  idObj = ID_Spoke; }
    q = p;
    sx = sign (q.x);
    q.x = abs (q.x);  q -= vec3 (trkWid + 0.08, -1.4, 0.);
    aw -= 0.5 * pi * sx; 
    q.yz -= 0.3 * vec2 (cos (aw), - sin (aw));
    d = PrCylDf (q, 0.04, 1.2);
    if (d < dMin) { dMin = d;  idObj = ID_Crod; }
    q.z = abs (q.z);  q -= vec3 (-0.1, 0., 1.1);
    d = PrCylDf (q.zyx, 0.06, 0.15);
    if (d < dMin) { dMin = d;  idObj = ID_Crod; }
    q = p;  q.z = abs (q.z);  q -= vec3 (0., -1.4, 1.1);
    d = PrCylDf (q.zyx, 0.1, trkWid - 0.1);
    if (d < dMin) { dMin = d;  idObj = ID_Axle; }
    q = p - vec3 (0., 1.1, 2.15);
    d = PrCylDf (q.xzy, 0.3, 0.5);
  }
  if (d < dMin) { dMin = d;  idObj = ID_Funl; }
  q = p - vec3 (0., 1.5, 2.15);
  d = max (PrCylDf (q.xzy, 0.4, 0.15), - PrCylDf (q.xzy, 0.3, 0.2));
  if (d < dMin) { dMin = d;  idObj = ID_Funt; }
  q = p - vec3 (0., 0.8, 0.55);
  d = PrCapsDf (q.xzy, 0.3, 0.2);
  if (d < dMin) { dMin = d;  idObj = ID_Funt; }
  q = p;  q.x = abs (q.x);  q -= vec3 (1., -0.2, 0.85);
  d = PrBoxDf (q, vec3 (0.05, 0.1, 1.8));
  if (d < dMin) { dMin = d;  idObj = ID_Stripe; }
  q = p;  q.x = abs (q.x);  q -= vec3 (1., -0.2, -1.75);
  d = PrBoxDf (q, vec3 (0.05, 0.1, 0.6));
  if (d < dMin) { dMin = d;  idObj = ID_Stripe; }
  q = p;  q.x = abs (q.x);  q -= vec3 (1., -0.2, -3.15);
  d = PrBoxDf (q, vec3 (0.05, 0.1, 0.3));
  if (d < dMin) { dMin = d;  idObj = ID_Stripe; }
  q = p - vec3 (0., -0.2, 3.5);
  d = PrCylDf (q, 0.2, 0.1);
  if (d < dMin) { dMin = d;  idObj = ID_Lamp; }
  return dMin;
}

float CarDf (vec3 p, float dMin)
{
  vec3 q;
  float wRad, d;
  wRad = 0.35;
  q = p;
  d = max (max (PrBoxDf (q, vec3 (1.3, 1.4, 2.8)),
     - PrBoxDf (q, vec3 (1.2, 1.3, 2.7))), - PrBoxDf (q, vec3 (0.5, 1., 2.9)));
  q.z = abs (q.z);  q -= vec3 (0., 0.6, 1.2);
  d = max (d, - PrBoxDf (q, vec3 (1.4, 0.7, 1.1)));
  if (d < dMin) { dMin = d;  idObj = ID_Car; qHit = q; }
  q = p;  q.y -= -2.35;
  d = max (PrCylDf (q, 4., 2.8), - (q.y - 3.75));
  if (d < dMin) { dMin = d;  idObj = ID_Roof; }
  q = p;  q.z = abs (q.z);  q -= vec3 (0., -0.2, 2.75);
  d = PrCylDf (q.zyx, 0.05, 0.5);
  if (d < dMin) { dMin = d;  idObj = ID_Roof;  qHit = q; }
  q = p;  q.y -= -1.6;
  d = PrBoxDf (q, vec3 (0.8, 0.3, 2.));
  if (d < dMin) { dMin = d;  idObj = ID_Base; }
  q = p;  q.z = abs (q.z);  q -= vec3 (0., -1.4, 2.9);
  d = PrCylDf (q.xzy, 0.4, 0.03);
  if (d < dMin) { dMin = d;  idObj = ID_Cpl; }
  q = p;  q.xz = abs (q.xz);  q -= vec3 (trkWid - 0.12, -1.85, 1.1);
  d = min (min (PrCylDf (q.zyx, wRad, 0.1),
     PrCylDf (q.zyx - vec3 (0.,0., -0.07), wRad + 0.05, 0.03)),
     PrCylDf (q.zyx, 0.15, 0.10));
  q.x -= 0.1;
  d = max (d, - PrCylDf (q.zyx, 0.2, 0.05));
  if (d < dMin) { dMin = d;  idObj = ID_Wheel; }
  q = p;  q.z = abs (q.z);  q -= vec3 (0., -1.85, 1.1);
  d = PrCylDf (q.zyx, 0.1, trkWid - 0.15);
  if (d < dMin) { dMin = d;  idObj = ID_Axle; }
  q = p;  q.x = abs (q.x);  q -= vec3 (1.3, -0.2, 0.);
  d = PrBoxDf (q, vec3 (0.05, 0.1, 2.8));
  if (d < dMin) { dMin = d;  idObj = ID_Stripe; }
  return dMin;
}

float TrackDf (vec3 p, float dMin)
{
  vec3 q;
  float d, dbs, dbc, nt, gap;
  q = p;
  if (onTrk) {
    if (! showObj[SH_TrkSt]) dbs = PrBox2Df (q.xz, vec2 (trkRad + 1.4 * trkWid, 0.6 * trkLin));
    if (! showObj[SH_TrkCv]) dbc = PrBox2Df (vec2 (q.x, abs (q.z) - 0.6 * trkLin - 0.5 * trkRad - trkWid),
       vec2 (trkRad + 1.4 * trkWid, 0.5 * trkRad + 1.4 * trkWid));
    q.z -= 0.5 * trkLin * clamp (p.z / (0.5 * trkLin), -1., 1.);
    q.x = abs (length (q.xz) - trkRad);
    q -= vec3 (trkWid - 0.03, - gHt + 0.45, 0.);
    d = length (max (abs (q.xy) - vec2 (0.07, 0.13), 0.));
    if (! showObj[SH_TrkSt]) d = max (d, - dbs);
    if (! showObj[SH_TrkCv]) d = max (d, - dbc);
  } else {
    q.x = abs (q.x);  q -= vec3 (trkWid - 0.03, - gHt + 0.45, 0.);
    d = max (length (max (abs (q.xy) - vec2 (0.07, 0.13), 0.)),
       abs (q.z) - 0.5 * trkLin);
  }
  if (d < dMin) { dMin = d;  idObj = ID_Rail; }
  q = p;
  if (onTrk) q.x = abs (q.x) - trkRad;
  q -= vec3 (0., - gHt + 0.2, 0.);
  nt = 2.;
  gap = trkLin / nt;
  q.z = mod (q.z + 0.5 * gap, gap) - 0.5 * gap;
  d = PrBoxDf (q, vec3 (trkWid + 0.5, 0.2, 0.4));
  q = p;  q.y -= - gHt + 0.2;
  d = max (d, PrBoxDf (q, vec3 (trkRad + 3., 2., 0.5 * trkLin + 2.)));
  if (onTrk) {
    if (! showObj[SH_TrkSt]) d = max (d, - dbs);
  }
  if (d < dMin) { dMin = d;  idObj = ID_Tie; }
  if (onTrk) {
    q = p;  q.x = abs (q.x);  q -= vec3 (0., - gHt + 0.2, 0.5 * trkLin * sign (q.z));
    nt = 12.;
    q.xz = Rot2D (q.xz, 2. * pi * floor ((atan (q.x, q.z) + pi) *
       nt / (2. * pi) + 0.5) / nt);
    q.z -= - trkRad;
    d = PrBoxDf (q, vec3 (0.4, 0.2, trkWid + 0.5));
    q = p;  q.y -= - gHt + 0.2;
    d = max (d, - PrBoxDf (q, vec3 (trkRad + 3., 2., 0.5 * trkLin + 2.)));
    if (! showObj[SH_TrkCv]) d = max (d, - dbc);
    if (d < dMin) { dMin = d;  idObj = ID_Tie; }
    if (showObj[SH_Plat]) {
      q = p - vec3 (trkRad - trkWid - 2., - gHt + 0.6, 0.);
      d = 0.8 * max (PrBoxDf (q, vec3 (trkWid, 0.4, 14.)), 0.5 * (abs (q.z) - 8.) + q.y);
      q -= vec3 (-1.2, 1.9, 0.);
      d = min (d, PrBoxDf (q, vec3 (0.2, 1.8, 5.)));
      q.z = abs (q.z) - 2.4;
      d = max (d,  - PrBoxDf (q, vec3 (0.3, 1.3, 1.7)));
      if (d < dMin) { dMin = d;  idObj = ID_Plat;  qHit = q; }
    }
    if (showObj[SH_Sig]) {
      q = p - vec3 (trkRad - trkWid - 2.5, 0.8, 6.);
      d = PrCylDf (q.xzy, 0.15, 3.);
      if (d < dMin) { dMin = d;  idObj = ID_SigV; }
      q.y -= 3.;
      d = PrSphDf (q, 0.35);
      if (d < dMin) { dMin = d;  idObj = ID_Sig; } 
    }
    if (showObj[SH_Tun]) {
      q = p - vec3 (0., - gHt, trkRad + 0.5 * trkLin - 0.5 * trkWid);
      d = max (PrCylAnDf (q.yzx, 5.5, 0.2, 6.), - q.y);
      if (d < dMin) { dMin = d;  idObj = ID_Tun;  qHit = q; } 
    }
    q = p;  q.y -= - gHt;
    d = PrBoxDf (q, vec3 (trkRad + trkWid + 4., 0.1,
       trkRad + 0.5 * trkLin + trkWid + 4.));
    if (d < dMin) { dMin = d;  idObj = ID_Grnd;  qHit = q; } 
    if (showObj[SH_Tree]) {
      q = p;  q.xz = abs (q.xz) - vec2 (0.4 * trkRad, 0.7 * trkLin);  q.y -= - gHt + 3.;
      d = PrCylDf (q.xzy, 0.3, 3.);
      if (d < dMin) { dMin = d;  idObj = ID_Trunk;  qHit = q; } 
      q.y -= 2.5;
      d = SmoothMin (PrSphDf (q, 1.5), PrSphDf (q - vec3 (0., 1.4, 0.), 1.2), 0.5);
      if (d < dMin) { dMin = d;  idObj = ID_Tree;  qHit = q; } 
    }
  }
  return dMin;
}

void TrainCarPM (float s)
{
  float a, ca, sa;
  if (onTrk) {
    s = mod (s, 2. * (pi * trkRad + trkLin));
    if (s < trkLin) {
      trPos = vec3 (trkRad, 0., s - 0.5 * trkLin);
      ca = 1.;  sa = 0.;
    } else if (s < trkLin + pi * trkRad) {
      a = (s - trkLin) / trkRad;
      ca = cos (a);  sa = sin (a);
      trPos = vec3 (trkRad * ca, 0., 0.5 * trkLin + trkRad * sa);
    } else if (s < 2. * trkLin + pi * trkRad) {
      trPos = vec3 (- trkRad, 0., 1.5 * trkLin + pi * trkRad - s);
      ca = -1.;  sa = 0.;
    } else {
      a = (s - (pi * trkRad + 2. * trkLin)) / trkRad + pi;
      ca = cos (a);  sa = sin (a);
      trPos = vec3 (trkRad * ca, 0., - 0.5 * trkLin + trkRad * sa);
    }
  } else {
    trPos = vec3 (0., 0., 0.3 * trkLin + s);
    ca = 1.;  sa = 0.;
  }
  trMat = mat3 (ca, 0., - sa, 0., 1., 0., sa, 0., ca);
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  for (int k = 0; k < 3; k ++) {
    if (k == 0 && showObj[SH_Engn] || k > 0 && showObj[SH_Cars]) {
      q = p - trainPos[k];
      d = PrCylDf (q.xzy, 4., 2.);
      dMin = (isShad || d < 0.2) ? ((k == 0) ? EngDf (trainMat[k] * q, dMin) :
         CarDf (trainMat[k] * q, dMin)) : min (dMin, d);
    }
  }
  dMin = TrackDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  const float dTol = 0.001;
  float d;
  float dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < dTol || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy),
     ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.3;
    if (sh < 0.05 || d > dMax) break;
  }
  return sh;
}

vec3 BrickSurfCol (vec2 p) 
{
  vec2 q = p * (1. / 20.);
  vec2 i = floor (q);
  if (2. * floor (i.y / 2.) != i.y) {
    q.x += 0.5;
    i = floor (q);
  }
  q = smoothstep (0.02, 0.04, abs (fract (q + 0.5) - 0.5));
  return (1. + Noisefv2 (10. * p)) * (0.5 + 0.5 * q.x * q.y) *
     (0.3 + 0.2 * sin (2. * Hashfv2 (i) + vec3 (1., 1.2, 1.4)));
}

vec3 BrickCol (vec3 p, vec3 n)
{
  n = abs (n);
  p *= 60.;
  return BrickSurfCol (p.zy) * n.x + BrickSurfCol (p.xz) * n.y +
     BrickSurfCol (p.xy) * n.z;
}

vec4 ObjCol (vec3 n)
{
  const vec4 cLo = vec4 (0.4, 0.2, 0.1, 0.3), cHi = vec4 (0., 0.8, 0., 0.5),
     cBlk = vec4 (vec3 (0.01), 0.1), cCab = vec4 (0.2, 0.2, 0.8, 0.5),
     cRf = vec4 (0.8, 0.1, 0.1, 0.5);
  vec4 col4;
  if (idObj == ID_Grnd) col4 =
     vec4 (0., 0.2, 0., 0.) + 0.03 * Noisefv2 (10. * qHit.xz);
  else if (idObj == ID_Tun) col4 = vec4 (0.4, 0.3, 0.2, 0.03) *
     (1. - 0.4 * Noisefv2 (vec2 (500. * (1. + atan (qHit.y, qHit.z) / pi), 25. * qHit.x)));
  else if (idObj == ID_Eng) col4 = cHi;
  else if (idObj == ID_Cabin) col4 = (qHit.y > -1.3) ? cCab : cLo;
  else if (idObj == ID_Coal)
     col4 = (qHit.y > 0.3) ? ((n.y > 0.9) ? cBlk : cCab) : cLo;
  else if (idObj == ID_Base || idObj == ID_Band || idObj == ID_Axle)
     col4 = vec4 (0.3, 0.2, 0.2, 0.3);
  else if (idObj == ID_Car) col4 = (qHit.y > -0.8) ? cHi : cLo;
  else if (idObj == ID_Roof) col4 = cRf;
  else if (idObj == ID_Wheel || idObj == ID_Spoke) col4 = vec4 (0.5, 0.5, 0.6, 1.);
  else if (idObj == ID_Crod) col4 = vec4 (0.7, 0.7, 0.1, 1.);
  else if (idObj == ID_Funl) col4 = (n.y > 0.9) ? cBlk : cRf;
  else if (idObj == ID_Stripe || idObj == ID_Funt) col4 = vec4 (1., 1., 0., 0.5);
  else if (idObj == ID_Lamp)
     col4 = (isDark || mod (tCur, 2.) < 1.) ? vec4 (1., 1., 0.7, -1.) : vec4 (0.8, 0.8, 0.4, -1.);
  else if (idObj == ID_Rail) col4 = vec4 (0.8, 0.8, 0.8, 0.5);
  else if (idObj == ID_Tie) col4 = vec4 (0.4, 0.2, 0.1, 0.1);
  else if (idObj == ID_Plat)
     col4 = vec4 (vec3 (0.5, 0.3, 0.3) * BrickCol (0.3 * qHit, n), 0.1);
  else if (idObj == ID_Sig)
     col4 = sigStop ? vec4 (1., 0., 0., -1.) : vec4 (0., 1., 0., -1.);
  else if (idObj == ID_Cpl) col4 = vec4 (0.2, 0.1, 0.2, 0.2);
  else if (idObj == ID_SigV) col4 = vec4 (0.8, 0.4, 0.2, 0.5);
  else if (idObj == ID_Tree) col4 = vec4 (0., 0.4, 0., 0.) *
     (1. - 0.5 * Noisefv2 (vec2 (50. * (1. + atan (qHit.x, qHit.z) / pi), 20. * qHit.y)));
  else if (idObj == ID_Trunk) col4 = vec4 (0.4, 0.1, 0., 0.1) *
     (1. - 0.3 * Noisefv2 (vec2 (30. * (1. + atan (qHit.x, qHit.z) / pi), 10. * qHit.y)));
  return col4;
}

float FontTexDf (vec2 p)
{
  vec3 tx;
  float d;
  int ic;
  ic = GetTxChar (p);
  if (ic != 0) {
    tx = texture (txFnt, mod ((vec2 (mod (float (ic), 16.),
       15. - floor (float (ic) / 16.)) + fract (p)) * (1. / 16.), 1.)).gba - 0.5;
    qnFnt = vec2 (tx.r, - tx.g);
    d = tx.b + 1. / 256.;
  } else d = 1.;
  return d;
}

float ObjRayT (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  vec2 srd, h;
  float dHit, d;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  srd = - sign (rd.xy);
  rdi = 1. / abs (rd.xyz);
  dHit = 0.;
  ro.xy /= fntSize.x;
  rd.xy /= fntSize.x;
  for (int j = 0; j < 100; j ++) {
    p = ro + dHit * rd;
    h = rdi.xy * fract (srd * p.xy);
    d = max (min (fntSize.x, 1.) * FontTexDf (p.xy), abs (p.z) - 0.5 * fntSize.y);
    dHit += min (d, 0.01 + fntSize.x * min (h.x, h.y));
    if (d < 0.0001 || dHit > rngBlk) break;
  }
  if (d >= 0.0001) dHit = dstFar;
  return dHit;
}

float BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    dMin = dn;
    rngBlk = df - dn;
  }
  return dMin;
}

float DstTxt (vec3 ro, vec3 rd)
{
  vec3 q, qt;
  float db, d, dstTxt;
  rd.xz = Rot2D (rd.xz, - azRot);
  dstTxt = dstFar;
  for (int k = 0; k <= 7; k ++) {
    idTxt = -1;
    if (k == 0 && showObj[SH_Engn]) {
      qt = trainPos[k] + vec3 (0., 2.5, 0.);
      idTxt = k;
    } else if (k < 3 && showObj[SH_Cars]) {
      qt = trainPos[k] + vec3 (0., 2.5, 0.);
      idTxt = k;
    } else if (k == 3 && (onTrk && showObj[SH_TrkSt] || ! onTrk)) {
      qt = (onTrk) ? vec3 (- trkRad + trkWid + 5., - gHt + 1., 0.) : vec3 (trkWid + 1., -3., 0.);
      idTxt = k;
    } else if (k == 4 && onTrk && showObj[SH_Plat]) {
      qt = vec3 (trkRad - trkWid - 3., 3., 0.);
      idTxt = k;
    } else if (k == 5 && onTrk && showObj[SH_Sig]) {
      qt = vec3 (trkRad - trkWid - 3., 5., 6.);
      idTxt = k;
    } else if (k == 6 && onTrk && showObj[SH_Tun]) {
      qt = vec3 (0., 4., trkRad + 0.5 * trkLin - trkWid);
      idTxt = k;
    } else if (k == 7 && onTrk && showObj[SH_Tree]) {
      qt = vec3 (-0.4 * trkRad, - gHt + 1., -0.7 * trkLin + 3.);
      idTxt = k;
    }
    if (idTxt >= 0) {
      qt.y -= 0.5 * fntSize.x;
      q = ro - qt;
      q.xz = Rot2D (q.xz, - azRot);
      db = BlkHit (q, rd, vec3 (4. * fntSize.x, fntSize.x, 0.5 * fntSize.y));
      if (db < dstFar) {
        d = db + ObjRayT (q + db * rd, rd);
        if (d < dstTxt) {
          dstTxt = d;
          qtTxt = qt;
          qnTxt = qnFnt;
        }
      }
    }
  }
  return dstTxt;
}

void SetState ()
{
  float t;
  tBuild = 14.;
  t = mod (tCur, tPlay);
  onTrk = (t < 8. || t > tBuild);
  onTxt = (t < tBuild + 9.);
  if (t < 0.5 * tPlay) {
    showObj[SH_TrkSt] = (t > 2.);
    showObj[SH_TrkCv] = (t > 3.);
    showObj[SH_Plat] = (t > 4.);
    showObj[SH_Sig] = (t > 5.);
    showObj[SH_Tun] = (t > 6.);
    showObj[SH_Tree] = (t > 7.);
    showObj[SH_Engn] = (t > 9.);
    showObj[SH_Cars] = (t > 10.);
  } else {
    t = tPlay - t + 0.5;
    showObj[SH_TrkSt] = (t > 1.);
    showObj[SH_TrkCv] = (t > 2.);
    showObj[SH_Plat] = (t > 3.);
    showObj[SH_Sig] = (t > 3.);
    showObj[SH_Tun] = (t > 4.);
    showObj[SH_Tree] = (t > 5.);
    showObj[SH_Engn] = (t > 6.);
    showObj[SH_Cars] = (t > 7.);
  }
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, objCol, col, ds, ld;
  float dstHit, dstTxt, sh, lDist, f;
  bool isBg;
  isBg = false;
  dstTxt = (onTxt) ? DstTxt (ro, rd) : dstFar;
  isShad = false;
  dstHit = ObjRay (ro, rd);
  if (min (dstHit, dstTxt) < dstFar) {
    if (dstHit < dstTxt) {
      ro += rd * dstHit;
      vn = (idObj != ID_Grnd) ? ObjNf (ro) : vec3 (0., 1., 0.);
      col4 = ObjCol (vn);
      isShad = true;
      col = col4.xyz;
      if (col4.w >= 0.) {
        if (isDark) {
          ld = ro - (trainPos[0] + vec3 (0., -0.2, 3.6) * trainMat[0]);
          lDist = length (ld);
          ld = normalize (ld);
          f = pow (max (dot (ld, vec3 (0., 0., 1.) * trainMat[0]), 0.), 1.5);
          sh = (f > 0.) ? ObjSShadow (ro, - ld, lDist - 0.5) : 1.;
          col *= 0.2 + 0.2 * max (vn.y, 0.) + sh * f * min (1., 100. * pow (1. + lDist, -2.));
          if (f > 0.) col += sh * col4.w * pow (max (dot (normalize (- ld - rd), vn), 0.), 64.);
        } else {
          sh = (onTrk) ? 0.4 + 0.6 * ObjSShadow (ro, ltDir, dstFar) : 1.;
          col *= 0.2 + 0.1 * max (dot (vn, - normalize (vec3 (ltDir.x, 0., ltDir.z))), 0.) +
             sh * 0.8 * max (dot (vn, ltDir), 0.);
          col += sh * col4.w * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
        }
      } else col *= clamp (0.6 - 0.5 * dot (rd, vn), 0., 1.);
    } else if (dstTxt < dstFar) {
      ro += rd * dstTxt;
      ds = ro - qtTxt;
      ds.xz = Rot2D (ds.xz, - azRot);
      if (abs (ds.z) < 0.49 * fntSize.y) {
        col = vec3 (0.3, 0.3, 1.);
        vn = normalize (vec3 (qnTxt, 0.00001));
      } else {
        col = vec3 (0.7, 0.7, 1.);
        vn = vec3 (0., 0., sign (ds.z));
      }
      ld = normalize (vec3 (1., 1., -1.));
      col = col * (0.5 + 0.5 * max (dot (ld, vn), 0.)) +
         0.5 * pow (max (dot (normalize (ld - rd), vn), 0.), 64.);
    } else isBg = true;
  } else isBg = true;
  if (isBg) col = vec3 (0.1, 0.1, 0.2) * (0.6 + 0.4 * rd.y) * (isDark ? 0.5 : 1.);
  col = pow (clamp (col, 0., 1.), vec3 (0.8));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, dist, tCirc, tPause, tHalt, s;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  trkRad = 20.;
  trkLin = 20.;
  trkWid = 1.42;
  gHt = 2.8;
  trVel = 6.;
  tPlay = 120.;
  SetState ();
  fntSize = vec2 (0.8, 0.1);
  if (onTrk) fntSize *= 1.5;
  tCirc = 2. * (pi * trkRad + trkLin) / trVel;
  tPause = 0.1 * tCirc;
  tCirc += tPause;
  if (onTrk) {
    tRun = mod (tCur, tPlay);
    s = floor ((tRun - tBuild) / tCirc);
    isDark = (s == 1.);
    tRun = (tRun > tBuild) ? mod (tRun - tBuild, tCirc) : 0.;
    tHalt = trkLin / trVel;
    sigStop = (tRun < tHalt + 0.8 * tPause);
    if (tRun > tHalt + tPause) tRun = tRun - tPause;
    else if (tRun > tHalt) tRun = tHalt;
  } else {
    tRun = 0.;
    isDark = false;
  }
  for (int k = 0; k < 3; k ++) {
    s = trVel * tRun;
    if (k == 1) s -= 7.;
    else if (k == 2) s -= 13.4;
    TrainCarPM (s);
    trainPos[k] = trPos;
    trainMat[k] = trMat;
  }
  if (onTrk) {
    dist = 2. * (trkRad + 0.5 * trkLin + 10.);
  } else {
    dist = 1.5 * trkLin;
    trVel = 1.;
    tRun = tCur;
  }
  ro = vec3 (0., 0., - dist);
  rd = normalize (vec3 (uv, 3.2));
  el = 0.15 * pi;
  az = 0.5 * pi;
  if (mPtr.z > 0.) {
    el -= pi * mPtr.y;
    el = (onTrk) ? clamp (el, 0., 0.45 * pi) : clamp (el, -0.4 * pi, 0.4 * pi);
    az -= 2.3 * pi * mPtr.x;
  } else {
    el -=  0.05 * pi * sin (0.02 * pi * tCur);
    az -= -0.4 * pi + (4. * pi / tPlay) * tCur;
  }
  azRot = az;
  ltDir = normalize (vec3 (0.3, 0.4, -1.));
  rd.yz = Rot2D (rd.yz, el);
  ro.yz = Rot2D (ro.yz, el);
  rd.xz = Rot2D (rd.xz, az);
  ro.xz = Rot2D (ro.xz, az);
  ltDir.yz = Rot2D (ltDir.yz, el);
  ltDir.xz = Rot2D (ltDir.xz, az);
  if (onTrk) ro.y -= gHt + 0.2;
  dstFar = 150.;
  col = ShowScene (ro, rd);
  fragColor = vec4 (col, 1.);
}

