int GetTxChar (vec2 p)
{
  vec2 fp;
  ivec2 ip;
  float d;
  int _ic, _nc;
  fp = floor (p);
  ip = ivec2 (fp);
  _ic = 0;
  _nc = ip.x;
  if (ip.y == 0) {
    if (idTxt < 3) {
      CP(3);
      if (idTxt == 0) {
        _E _n _g _i _n _e
      } else if (idTxt == 1) {
        _C _a _r _SP _NUM _1
      } else if (idTxt == 2) {
        _C _a _r _SP _NUM _2
      }
    } else if (idTxt == 3) {
      CP(3);
      _T _r _a _c _k
    } else if (idTxt == 4) {
      CP(4);
      _P _l _a _t _f _o _r _m
    } else if (idTxt == 5) {
