}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, cHashA3.xy)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 i = floor (p);
  vec2 f = fract (p);
  f = f * f * (3. - 2. * f);
  vec4 t = Hashv4f (dot (i, cHashA3.xy));
  return mix (mix (t.x, t.y, f.x), mix (t.z, t.w, f.x), f.y);
}

#define PI 3.14159265358979

mat3 rotateX(float rad) {
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
        1.0, 0.0, 0.0,
        0.0,   c,   s,
        0.0,  -s,   c
    );
}

mat3 rotateY(float rad) {
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
          c, 0.0,  -s,
        0.0, 1.0, 0.0,
          s, 0.0,   c
    );
}

mat3 rotateZ(float rad) {
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
          c,   s, 0.0,
         -s,   c, 0.0,
        0.0, 0.0, 1.0
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 2.5 * fragCoord.xy / iResolution.yy - 1.25;
    p.x -= 1.0;
    float xx = p.x*p.x, yy = p.y*p.y;

    // Return a black pixel if we're outside of the sphere.
    if (xx + yy > 1.0) {
        fragColor = vec4(0.0);
        return;
    }

    fragColor = vec4(1.0);

    vec3 rotated = rotateY(iTime*2.0) * rotateX(iTime) * rotateZ(iTime/2.0)
        * vec3(p.x, p.y, sqrt(1.0 - xx - yy));

    float phi   = atan(rotated.x, rotated.y) + PI;         // 0 < phi < 2pi
    float theta = atan(rotated.z,   1.0    ) + PI / 4.0;   // 0 < theta < pi/2	

    // Transform the angles in to texture coordinates in [0,255].
	int u = int(floor( 256.0 *   phi / PI / 2.0 ));
    int v = int(floor( 256.0 * theta / PI * 2.0 ));

    // Look up the texture coordinates in a colored XOR texture. 
    vec4 rgba;
    fragColor.r = float(u ^ v);
    fragColor.g = 2.0*abs(128.0 - fragColor.r);
    fragColor.b = 255.0 - fragColor.r;
    fragColor.a = 255.0;

    // Compute the pixel's distance from the center of the lighting glare.
    float ldx = p.x + 0.5;
    float ldy = p.y - 0.5;
    float ld = sqrt( ldx*ldx + ldy*ldy ) + 0.4; // Constant added here for anti-glare effect. 0 = glare, 1 = no glare.
    ld *= ld;

    // Divide all colors by the squared distance from the glare center, and we're done.
    fragColor /= 255.0 * ld;
}

float cosNoise(in vec2 pos){
    return 0.5*(sin(pos.x) + sin(pos.y));
}

const mat2 m2 = mat2(0.8, -0.6,
                     0.6, 0.8); 

float map(in vec3 pos){// pos:
//  return length(pos - vec3(0.0,0.0,-2.0)) -1.0;
    vec2 q = pos.xz * 0.5; // 
    float h = 0.0;

    float s = 0.5;  // noise
    for(int i = 0; i<10; i++){
        h += s*cosNoise(q);   // xz
        s *= 0.5;            // noise 
        q = m2 * q * 1.7;    // 
    }

    h *= 3.0;   // 

    return pos.y - h;   // 
}



// 
vec3 calcNormal(in vec3 pos){
    vec3 nor;
    vec2 e = vec2(0.01,0.0);
    nor.x = map(pos + e.xyy) - map(pos - e.xyy);    //
    nor.y = map(pos + e.yxy) - map(pos - e.yxy);
    nor.z = map(pos + e.yyx) - map(pos - e.yyx);
    return normalize(nor);
}

float calcShadow(in vec3 ro, in vec3 rd){ // ro: rd:
    //
    float res = 1.0;
    float t = 0.1;
    for (int i = 0; i <64; i++){
        vec3 pos = ro + t*rd;
        float h = map(pos);
       res = min( res, max(h,0.0)*164.0/t );
        if(res< 0.0001) break;
        t+=h*0.5;
    }
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy; //
    vec2 q = -1.0 + 2.0*p;                  // -1~1
    q.x *= 1.777;                           // x1.777?

    vec3 ro = vec3(0.0, 1.65, -iTime);      // 
    vec3 rd = normalize( vec3(q, -1.0));    // 

    vec3 col = vec3(0.7, 0.8, 1.0);         // 
    col *= 1.0 -0.5 *rd.y;                  // 
    float tmax =40.0;                       // 
    float t = 0.0;                          // 

    for(int i=0; i<256; i++){               // 256 
        vec3 pos = ro + rd*t;               // 
        float h = map(pos);                 // 
        if(h < 0.001 || t > tmax) break;    // 0.001tmax

        t += h*0.5;                         // 
    }

    vec3 light = normalize(vec3(1.0,0.1,-0.5)); // 

    if(t < tmax){   // 
        vec3 pos = ro + t*rd;   // 
        vec3 nor = calcNormal(pos); // 
        float sha = calcShadow(pos + nor *0.1,light); // 

        float dif = clamp (dot(nor,light), 0.0,1.0);  // lumbert
        vec3 lig = vec3(2.0, 1.5,1.0)*dif *sha;       // **
        lig += vec3(0.1,0.2,0.3)*nor.y *2.0;          // 

        // 
        vec3 mate = vec3(0.2, 0.3, 0.3)*0.5; 
        mate = mix(mate, vec3(0.2,0.15,0.1), smoothstep(0.7,0.9,nor.y));
        col = lig * mate;

        float fog = exp(-0.002 *t * t); // fog
        col *= fog;
        col += ((1.0 - fog)*vec3(0.5,0.6,0.7));
    }

    // gamut for monitor
    col = sqrt(col);

    fragColor = vec4(col,1.0);
}

