    #define CutRing(ds, dl, dw, dr) (step(fract(angle+iTime*(ds)),dl)*(dw)/(dr))
    #define Radi(r) (abs(radius-(r)))
    float col = CutRing(0.3,0.5,0.005,mul(Radi(0.3)));

    col += CutRing(0.1,0.6,0.01,mul(Radi(0.45)))
        +CutRing(0.2,0.5,0.001,mul(Radi(0.55)))
        +CutRing(0.05,0.3,0.007,mul(Radi(0.65)))
        +CutRing(0.2,0.7,0.03,mul(Radi(0.8)))
        +CutRing(0.2,1.0,0.02,Radi(0.8));
    col /=6.0;
    fragColor = col*_Color;
}

