//////////////////////////////////////////////////////////////////////////////////

const float zoomSpeed			= -0.4;	// how fast to zoom (negative to zoom out)
const float zoomScale			= 0.08;	// how much to multiply overall zoom (closer to zero zooms in)
const float saturation			= 0.4;	// how much to scale saturation (0 == black and white)
const float turnSpeed			= 0.7;	// how fast to rotate (0 = no rotation)
const int   recursionCount		= 10;	// how deep to recurse
const float dotSize 			= 0.6;	// how much to scale recursion at each step
const float blur				= 5.0;	// how much blur
const float outline				= 0.12;	// how thick is the outline
const float timeWarp			= 1.0;	// time warp to add curvature

//////////////////////////////////////////////////////////////////////////////////

const float pi = 3.14159265359;
const float e = 2.718281828459;
float RandFloat(int i) { return (fract(sin(float(i)) * 43758.5453)); }
vec2 Rotate(vec2 p, float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return vec2((p.x*c - p.y*s), (p.x*s + p.y*c));
}
vec4 HsvToRgb(vec4 c) 
{
    float s = c.y * c.z;
    float s_n = c.z - s * .5;
    return vec4(s_n) + vec4(s) * cos(2.0 * pi * (c.x + vec4(1.0, 0.6666, .3333, 1.0)));
}

float GetFocusRotation(int i) 
{ 
    float theta = 2.0*pi*RandFloat(i);
    float s = mix(-1.0, 1.0, RandFloat(30+i));
    //s += ((s < 0.0)? -.5 : 0.5);
    return theta + turnSpeed*s*iTime; 
}

vec2 GetFocusPos(int i) 
{ 
    bool side = (RandFloat(50+i) < 0.5);
    vec2 p = vec2(0.0, side? -0.5 : 0.5); 
    return Rotate(p, GetFocusRotation(i));
}

//////////////////////////////////////////////////////////////////////////////////

float YinYang2( vec2 p, out float dotDistance, float co, float scale)
{
   	float b = blur*scale/min(iResolution.y, iResolution.x);
    float d = dotSize;

    float c = 1.0;
    float r;

    // bottom
    r = length(2.0*p + vec2(0, 1));
    if (p.x < 0.0)
	   c = mix(c, 0.0, smoothstep(1.0-b, 1.0+b, r));
    dotDistance = r;

    // top
    r = length(2.0*p - vec2(0, 1));
    if (p.x >= 0.0)
		c = mix(0.0, c, smoothstep(1.0-b, 1.0+b, r));
    if (p.y >= 0.0)
         dotDistance = r;

    // outline
    r = length(p);
    c = mix(c, co, smoothstep(1.0-b, 1.0, r));

    return c;
}

vec4 RecursiveYinYang(vec2 p, int iterations, float scale)
{
    // recursive iteration
    float co = 0.0;
    for (int r = 0; r < recursionCount; ++r)
    {
        // apply rotation
  		float theta = -GetFocusRotation(iterations + r);
        p = Rotate(p, theta);

        float dotDistance = 0.0;
        co = YinYang2(p, dotDistance, co, scale);

        if (dotDistance > dotSize || r == recursionCount)
        {
            float co2 = (p.y < 0.0)? 0.0 : 1.0;
   			float b = blur*scale/min(iResolution.y,iResolution.x);

            co = mix(co2, co, smoothstep(dotSize+outline,dotSize+outline+b,dotDistance));
            int i2 = (dotDistance < dotSize+outline+b && p.y > 0.0)? 1 : 0;
            float hue = 0.233*float(iterations + r + i2);
            return vec4(hue, saturation*co, co, 1.0); // stop if outside or reached limit
        }

		// check if top or bottom
        co = (p.y > 0.0)? 1.0 : 0.0;
        p.y += mix(0.5, -0.5, co);

        scale *= 2.0/dotSize;
        p *= 2.0/dotSize;		// apply recursion scale
        p = Rotate(p, -theta);	// cancel out rotation
    }
    return vec4(0);
}

//////////////////////////////////////////////////////////////////////////////////

void mainImage( out vec4 color, in vec2 uv )
{
    // fixed aspect ratio
	vec2 p = (2.0*uv-iResolution.xy)/min(iResolution.y,iResolution.x);
    vec2 p2 = p;

    // wander center
	p.x += 0.5*sin(0.234*iTime);
	p.y += 0.5*sin(0.2*iTime);

    // time warp
    float time = iTime + timeWarp*length(p);

	// get time 
	float timePercent = time*zoomSpeed;
	int iterations = int(floor(timePercent));
	timePercent -= float(iterations);

	// update zoom, apply pow to make rate constant
    const float recursionSize = 2.0 / dotSize;
	float zoom = pow(e, -log(recursionSize)*timePercent);
	zoom *= zoomScale;

	// get focus offset
	vec2 offset = GetFocusPos(iterations);
	for (int i = 0; i < 10; ++i)
		offset += (GetFocusPos(iterations+i+1) / recursionSize) * pow(1.0 / recursionSize, float(i));

    // apply zoom and offset
	p = p*zoom + offset;

    // make the yin yang
    color = RecursiveYinYang(p, iterations, zoom);

    // wander hue
    color.x += (0.1*p2.y + 0.1*p2.x + 0.05*time);

    // map to rgp space
    color = HsvToRgb(color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float px = fragCoord.x / iResolution.x;
    float py = fragCoord.y / iResolution.y;

    float width = 0.01;

    float vBlock = step(width, mod(px, width * 2.0));
    float hBlock = step(width, mod(py, width * 2.0));

    vec4 final = vec4(vBlock * hBlock);

    //add a sin wave
    float lineWidth = 0.02;
    float squareWidth = 0.1;
    float radius = 0.05;
    vec2 squarePosition = vec2(0.5, 0.5);

    if(final.r + final.g + final.b < 2.9) {
        //draw sin wave based on time
    	final.r = step(-lineWidth, -abs(sin((px * 2.0 * 3.14) + iTime) - ((py * 2.0) - 1.0)));
		//draw a square between x 0.4 and 0.6 and y 0.4, and 0.6
        final.g = step(-squareWidth, -abs(px - squarePosition.x))
            * step(-squareWidth, -abs(py - squarePosition.y));
        //Note: step is like an if statement, without being an if statement
       	//		conditionals make shaders slow, so step is used instead

        //draw blue near the mouse cursor
        final.b = step(-radius, -abs(px - iMouse.x / iResolution.x))
            * step(-radius, -abs(py - iMouse.y / iResolution.y));
    }
	fragColor = final;
}

#define TAU 6.28318530718

#define FAR 25.
#define ITER 128
#define QUA .001
#define NORK 5e-4

#define REF 0.56
#define RITER 16
#define RRFB .001

#define BRG 0.83

float map(vec3 p)
{
    float d = FAR;
	d = min(d, length(p-vec3(0.75,vec2(.0)))-1.);	// center ball
	d = min(d, 5. - abs(p.x));						// left & right wall
	d = min(d, 3. - abs(p.y - 2.));					// floor & ceil
	d = min(d, 5. - abs(p.z));						// front & back wall
	return d;
}

vec3 normal(vec3 p, float k)
{
	vec2 e = vec2(.0,k);
	return normalize(vec3(
		map(p) - map(p - e.yxx),
		map(p) - map(p - e.xyx),
		map(p) - map(p - e.xxy)
	));
}

float tracer(vec3 ro, vec3 rd)
{    
    float t = .0, d;
    for(int i=0; i < ITER; i++){        
        d = map(ro + rd*t);
        if (d < QUA*t || t > FAR) break;
        t += d;
    } 
    return t + QUA;
}

float hard_shadow(vec3 sp, vec3 lp, int iter)
{   
	vec3 p, ld = normalize(sp - lp);

	float t = .0;
	for(int i = 0; i < iter; i++)
	{
		p = lp + ld * t;
		float m = map(p);
		if (m < .0 || t > FAR) break;
		t += m;
	}

	return max(1. - distance(p, sp), 0.);
}

vec3 color(vec3 sp, vec3 sn)
{   
    vec3 lp = vec3(-1, 1, -2);
	vec3 ld = normalize(lp - sp);

    float diff = max(dot(sn,ld),0.);
    float shd = hard_shadow(sp, lp, 30);

    vec3 col = (sn.xyz+1.)/2.;
    return col * diff * shd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    if(fract((iTime-5.)/20.) < 0.5) uv.x *= -1.0;

	vec3 ro = vec3(abs(10. - mod(iTime-5.,20.)) - 5., .0, -3.);
    vec3 rd = normalize(vec3(uv,2.));

    float t = tracer(ro,rd);
	vec3 sp = ro + rd*t;
	vec3 sn = normal(sp,NORK);

    vec3 col = color(sp, sn);

    for(int i=0; i<RITER; i++)
    {
        if(t > FAR) break;

        rd = normalize(reflect(rd, sn));
        ro = sp + rd * RRFB;

        t = tracer(ro, rd);

        sp = ro + rd * (t - RRFB);
		sn = normal(sp,NORK);

        col += color(sp, sn) * pow(REF, float(i) + 1.);
    }

	fragColor = BRG * vec4(col,1.);
}

