#define PI 3.14159265359
#define NUM_PI_DIVS 8.0

#define PI_DIV PI/NUM_PI_DIVS
#define HALF_PI_DIV PI_DIV*0.5
#define THIRD_PI_DIV PI_DIV/3.0

#define SMOOTH_HALF_INTERVAL HALF_PI_DIV*0.5

#define SIN_AMP_MAX HALF_PI_DIV*3.0
#define SIN_AMP_TIME_AMP 3.0
#define SIN_FREQ_MAX 10.0
#define SIN_FREQ_TIME_AMP 0.5

#define SIN_TIME_AMP 10.0
#define MOD_TIME_AMP 1.5

float posSin(float x) {
  return (sin(x)/2.0 + 0.5);
}

float plot(float pct, vec2 st){
  return smoothstep(pct - SMOOTH_HALF_INTERVAL, pct, st.y)
      - smoothstep(pct, pct + SMOOTH_HALF_INTERVAL, st.y);
}

float computeAmp(float time_factor) {
  return SIN_AMP_MAX*(sin(iTime*time_factor*SIN_AMP_TIME_AMP)/2.0 + 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 st = vec2(fragCoord.x /iResolution.x, fragCoord.y/iResolution.y);
  st = (st - 0.5) * 2.0;
  st.x *= iResolution.x/iResolution.y;

  // Determine the polar coordinate
  float oTheta = atan(st.y, st.x);
  float r = length(st);

  float theta = mod(oTheta + iTime*MOD_TIME_AMP, PI_DIV) - HALF_PI_DIV;
  float theta2 = mod(oTheta - THIRD_PI_DIV + iTime*MOD_TIME_AMP*2.0/3.0, PI_DIV) - HALF_PI_DIV;
  float theta3 = mod(oTheta - 2.0*THIRD_PI_DIV + iTime*MOD_TIME_AMP/3.0, PI_DIV) - HALF_PI_DIV;

  float freq = SIN_FREQ_MAX*posSin(iTime*SIN_FREQ_TIME_AMP);

  float ampR = computeAmp(1.0/3.0);
  float ampG = computeAmp(2.0/3.0);
  float ampB = computeAmp(1.0);

  float polarPct = plot(ampR*sin(r*freq - iTime*SIN_TIME_AMP), vec2(theta));
  float polarPct2 = plot(ampG*sin(r*freq - iTime*SIN_TIME_AMP/3.0), vec2(theta2));
  float polarPct3 = plot(ampB*sin(r*freq - iTime*SIN_TIME_AMP*2.0/3.0), vec2(theta3));

  vec3 color = vec3(polarPct, polarPct2, polarPct3);
  // vec3 color = vec3(polarPct*posSin(u_time*2.0/3.0), polarPct2*posSin(u_time), polarPct3*posSin(u_time/3.0));

  fragColor = vec4(color, 1.0);
}

const float PI = 3.1415926;

/*
Counter based pseudorandom number generator Philox

It is introduced in this paper:
Parallel Random Numbers: As Easy as 1, 2, 3
by John K. Salmon, Mark A. Moraes, Ron O. Dror, and David E. Shaw

Philox is a modification of Threefish.
It is explained in 2.2 and 3.3 in this paper:
The Skein Hash Function Family
by Niels Ferguson, Stefan Lucks, Bruce Schneier, Doug Whiting, Mihir Bellare, Tadayoshi Kohno, Jon Callas, Jesse Walker

You can download original Random123 source code from here:
http://www.thesalmons.org/john/random123/

How to use:
uvec4 counter;
uvec2 key;
uintToFloat(philox4x32_7(counter, key))
returns pseudorandom float4 value where each components are [0, 1).
*/

//umulExtended is not available in
//umulExtended was added GLSL ES 3.1
//This function is based on _mulhilo_c99_tpl macro in Random123-1.09/include/Random123/philox.h in Random123 source code.
void umulExtended_(uint a, uint b, out uint hi, out uint lo) {
    const uint WHALF = 16u;
    const uint LOMASK = (1u<<WHALF)-1u;
    lo = a*b;               /* full low multiply */
    uint ahi = a>>WHALF;
    uint alo = a& LOMASK;
    uint bhi = b>>WHALF;
    uint blo = b& LOMASK;

    uint ahbl = ahi*blo;
    uint albh = alo*bhi;

    uint ahbl_albh = ((ahbl&LOMASK) + (albh&LOMASK));
    hi = ahi*bhi + (ahbl>>WHALF) +  (albh>>WHALF);
    hi += ahbl_albh >> WHALF; /* carry from the sum of lo(ahbl) + lo(albh) ) */
    /* carry from the sum with alo*blo */
    hi += ((lo >> WHALF) < (ahbl_albh&LOMASK)) ? 1u : 0u;
}

uvec2 philox4x32Bumpkey(uvec2 key) {
    uvec2 ret = key;
    ret.x += 0x9E3779B9u;
    ret.y += 0xBB67AE85u;
    return ret;
}

uvec4 philox4x32Round(uvec4 state, uvec2 key) {
    const uint M0 = 0xD2511F53u, M1 = 0xCD9E8D57u;
    uint hi0, lo0, hi1, lo1;
//    umulExtended(M0, state.x, hi0, lo0);
//    umulExtended(M1, state.z, hi1, lo1);
    umulExtended_(M0, state.x, hi0, lo0);
    umulExtended_(M1, state.z, hi1, lo1);

    return uvec4(
        hi1^state.y^key.x, lo1,
        hi0^state.w^key.y, lo0);
}

uvec4 philox4x32_7(uvec4 plain, uvec2 key) {
    uvec4 state = plain;
    uvec2 round_key = key;

    for(int i=0; i<7; ++i) {
        state = philox4x32Round(state, round_key);
        round_key = philox4x32Bumpkey(round_key);
    }

    return state;
}

float uintToFloat(uint src) {
    return uintBitsToFloat(0x3f800000u | (src & 0x7fffffu))-1.0;
}

vec4 uintToFloat(uvec4 src) {
    return vec4(uintToFloat(src.x), uintToFloat(src.y), uintToFloat(src.z), uintToFloat(src.w));
}


