// A barycentric triangle is a triangle on the plane given by 3 position vectors a, b, and c.
// By stealing everything from sea's shader, we render an antialiased barycentric triangle,
// which I was unable to find anywhere on Shadertoy...

#define ANTIALIAS 4.

// ------------------------------------------------------------------------------------------------
// This function, by @sea, allowed me to turn @sea's boolean barycentric triangle into an antialiased barycentric triangle!
bool draw_line(vec2 p, vec2 a, vec2 b){
    vec2 ab = normalize(b - a);
    vec2 ap = p - a;
    return length((a + ab * dot(ab, ap)) - p) < .01;
} 

// Find the intersection of vec_p with dvec_ab
vec2 intersect(vec2 vec_p, vec2 vec_a, vec2 vec_b){
    vec2 dvec_ab     = vec_b - vec_a;  // Displacement vector from vec_a to vec_b
    vec2 dvec_ap     = vec_p - vec_a;  // Displacement vector from vec_a to vec_p
    vec2 vproj_ab_ap = dvec_ab * dot(dvec_ab, dvec_ap) / dot(dvec_ab, dvec_ab);  // Standard linear algebra way of computing the vector projection!
    return vec_a + vproj_ab_ap;  // Intersection of vec_p with vec_ab
}

// By sea
float sdf_triangle2(vec2 vec_p, vec2 vec_a, vec2 vec_b, vec2 vec_c){
    vec2 a_intersect_bc = intersect(vec_a, vec_b, vec_c);  // Using vector projection, find the intersection of vec_a with dvec_bc
    float bary_x = dot(a_intersect_bc - vec_p, normalize(a_intersect_bc - vec_a)) / distance(a_intersect_bc, vec_a);

    vec2 b_intersect_ca = intersect(vec_b, vec_c, vec_a);  // Using vector projection, find the intersection of vec_b with dvec_ca
    float bary_y = dot(b_intersect_ca - vec_p, normalize(b_intersect_ca - vec_b)) / distance(b_intersect_ca, vec_b);

    float bary_z = 1. - bary_x - bary_y;  // The 3rd barycentric coordinate is easy!

    // Barycentric coordinates range over [0; 1]. Any value outside [0; 1] is not in the triangle!
    return min(min(bary_x, bary_y), bary_z);
}

// ------------------------------------------------------------------------------------------------
// Courtesy of Dave_Hoskins! Only works for certain configurations of vertices...!
float sdf_triangle1(vec2 vec_p, vec2 vec_a, vec2 vec_b, vec2 vec_c){
 	vec2 dvec_ap = vec_p - vec_a;
	vec2 dvec_bp = vec_p - vec_b;

	float r1 = (vec_b.x - vec_a.x) * dvec_ap.y - (vec_b.y - vec_a.y) * dvec_ap.x;
	float r2 = (vec_a.x - vec_c.x) * dvec_ap.y - (vec_a.y - vec_c.y) * dvec_ap.x;
   	float r3 = (vec_c.x - vec_b.x) * dvec_bp.y - (vec_c.y - vec_b.y) * dvec_bp.x;

    return min(min(r1, r2), r3);
}

