void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;  // The Fabrice map!
    uv = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * uv;

    //vec2 mouse_uv = (2. * iMouse.xy - iResolution.xy) / iResolution.y;
    //mouse_uv = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * mouse_uv;

    // Specify & draw triangle!
    vec2 triangle_a = vec2(-.4, -.5);
    vec2 triangle_b = vec2( .8,  .0);
    vec2 triangle_c = vec2( .0,  .8);  // mouse_uv
    float triangle = sdf_triangle1(uv, triangle_a, triangle_b, triangle_c);  // Try changing this to triangle2()!
    triangle = smoothstep(.0, ANTIALIAS / iResolution.y, triangle);

	fragColor.rgb = vec3(triangle);
}

vec3 pulsar(in vec3 p, in vec2 uv)
{
	float	str = 15.0,
        	accum = 0.5,
        	prev = mod((iTime+sin(iTime*20.0)*0.025)*10.1, 60.0),
        	tw = 1.0;

    p.z += sin(iTime*1.0)*0.01;

	for (int i = 0; i < 18; ++i)
    {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.42 - cos(iTime*0.2)*0.02, -1.75 + sin(iTime*0.2)*0.003);
		float w = exp(-float(i) / str);
		accum += w * exp(-str * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
    float t = clamp(5.0 * accum / tw - 0.9, 0.0, 1.0);
	return vec3(1.3*t*t*t*(1.0 - length(uv)*0.2) , 1.2*t*t, 1.0*t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset = iResolution.xy * 0.5 + (iMouse.z > 0.5 ? iMouse.xy : vec2(0.0));
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1. + offset/iResolution.xy - 0.5;
	vec3 p = vec3(uv * (iResolution.xy / iResolution.x) / 0.2, 0.0);

    vec3 noise = texture(iChannel0, uv*(3.0-mod(iTime*2.0,0.2))).rgb*0.1;

    vec2 vig_coord = fragCoord.xy / iResolution.xy - pow(fragCoord.xy / iResolution.xy,vec2(2.0));
    float vig_highlight = 1.0-pow(vig_coord.x*vig_coord.y*3.0, 0.05);

	fragColor = vec4((vec3(pulsar(p*0.99,uv).r, pulsar(p, uv).gb)-noise)+vig_highlight, 1.0) ;
}

#define TWOPI 6.28318530718

vec4 WATER_COLOR = vec4(0.0, 0.412, 0.58, 1.0); // sea blue
vec4 CREST_COLOR = vec4(1.0, 1.0, 1.0, 1.0);

// Goes 0 to 1 at period/2 and back to 0
float trianglefunction(float t, float period) {
    float ps = mod(t, period) / period; // 0 to 1 in period
    if (ps > 0.5)
        ps = 1.0 - ps;    
    return ps * 2.0;
}

// Returns 1 where the crest is strongest, can go <0 too though
float waterwave(vec2 uv,
                float t,
    			float period, 
                float horzSpeed, 
                float vertSpeed,
                float amplitude, 
                float crestWidth
               ) {
	// canvas moves
    float cx = uv.x;
    float cy = uv.y + vertSpeed * sin(t);

    // map area to [0..TWOPI, -1..1]
	float sx = cx * TWOPI;
    float sy = cy * 2.0 - 1.0;

    float ss = trianglefunction(t, period);
    ss = ss * 2.0 - 1.0;

    float f = sin(t * horzSpeed + sx) * ss * amplitude;

    float d = abs(sy - f) / crestWidth;
    return 1.0 - d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float w1 = waterwave(vec2(uv.x, uv.y - 0.2),
                        iTime,
        			    8.0, // period
                        1.0, // horzSpeed
                        0.2, // vertSpeed
                        0.5, // amplitude
                        1.5 // crestWidth
                        );

    float w2 = waterwave(vec2(uv.x, uv.y + 0.3),
                        iTime,
        			    5.0, // period
                        2.5, // horzSpeed
                        0.1, // vertSpeed
                        0.15, // amplitude
                        0.5 // crestWidth
                        );

    float waves = w1 + w2 * 0.25;

    fragColor = mix(WATER_COLOR, CREST_COLOR, waves);
}

