vec2 hex(vec2 v){
	float yc = abs(mod(v.x + floor((v.y*hm + 0.5))*(c30 / 2.0),c30) / c30 - 0.5);
	float y = floor(v.y*hm + yc);
	float x = floor(v.x/c30 + 0.5 + fract(y / 2.0))- fract(y / 2.0);
	return vec2(x*c30,y /hm + 1.0/(hm * 4.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy  / vec2(iResolution.y,iResolution.y);

    float rt = iTime * timeScale;
    vec2 gv = hex(uv*grid)/grid;
    vec2 mv = gv - 0.5;
    vec2 rv = vec2(((atan(mv.x,mv.y)/3.141526) + 1.0) / 2.0, length(mv));
    float fv = fract((rv.x- rt) * 2.0 + rv.y * 2.0);

    float dist = length(uv - gv) * grid;
    float dist2 = 1.0 - smoothstep(-smooth,0.0,dist - c30/2.);
    dist += fv;
    dist = smoothstep(0.5,0.5 + smooth,fract(dist));
    float finCol = float(0.5);
    finCol = mix(finCol, dist, dist2);
	fragColor = vec4(finCol);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec3 hsb2rgb( in vec3 c ){
  vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                           6.0)-3.0)-1.0,
                   0.0,
                   1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb);
  return c.z * mix(vec3(1.0), rgb, c.y);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
  vec2 i = floor(st);
  vec2 f = fract(st);

  vec2 u = f*f*(3.0-2.0*f);

  return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                   dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
              mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                   dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

