vec4 render(vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    uv -= .5;

    float maxRes = max(iResolution.x, iResolution.y);
    vec2 s = coord.xy / maxRes - vec2(0.5 * iResolution.x / maxRes, 0.5 * iResolution.y / maxRes);

    vec2 uvM = iMouse.xy / iResolution.xy - .5;


    vec2 rot = 5.*vec2(-uvM.x,-uvM.y);
    vec3 pos = -1.75 * float(WIDTH) * vec3(sin(rot.x) * cos(rot.y), sin(rot.y), cos(rot.x) * cos(rot.y)) + float(WIDTH) / 2.;

    vec3 d = vec3(2.0 * s.x * tan(d2r * FOV / 2.0), 2.0 * s.y * tan(d2r * FOV / 2.0), 1.0);
    d = normalize(d);

    //Rotations
    d = vec3(d.x, d.y * cos(rot.y) + d.z * sin(rot.y), d.z * cos(rot.y) - d.y * sin(rot.y));
    d = vec3(d.x * cos(rot.x) + d.z * sin(rot.x), d.y, d.z * cos(rot.x) - d.x * sin(rot.x));


    vec4 sky = vec4(0.5 - 0.5 * sin(d.y), 0.7 - 0.3 * sin(d.y),1.0,0.0);
    vec4 o = sky;


    vec3 bounderies = vec3(WIDTH) * (0.5 - 0.5 * sign(d));
    vec3 t = (bounderies - pos) / d;

    vec3 mask = vec3(greaterThanEqual(t.xyz, max(t.yzx, t.zxy)));

    if (dot(t, mask) < 0.)
        return sky;

    vec3 endPos = (1. - mask) * (pos + d * dot(t, mask))   +   mask * bounderies;

    //MAIN LOOP
    for (int j = 0; j < 10; j++) {

        bool plane = false;

        if ((endPos.x - float(WIDTH) / 2.)*sin(TIME) + (endPos.z - float(WIDTH) / 2.)*cos(TIME) + (endPos.y - float(WIDTH) / 2.)*sin(TIME/3.14) > (.25 + sin(TIME * 1.4) / 4.) * float(WIDTH)) {
            plane = true;
            float T = -(sin(TIME)*(pos.x - float(WIDTH) / 2.) + cos(TIME)*(pos.z - float(WIDTH) / 2.) + (pos.y - float(WIDTH) / 2.)*sin(TIME/3.14) - (.25 + sin(TIME * 1.4) / 4.) * float(WIDTH)) / (sin(TIME)*d.x + cos(TIME)*d.z + sin(TIME/3.14)*d.y);
            endPos = (pos + d*T);
        }

        if (max(endPos.x, max(endPos.y, endPos.z)) > float(WIDTH) || min(endPos.x, min(endPos.y, endPos.z)) < 0.)
            break;

        vec3 lastEntered = vec3(equal(endPos, floor(endPos)));
        ivec3 enter = ivec3(floor(endPos) - (.5 + .5 * -sign(d)) * lastEntered);

        bvec3 inf;
        int num = WIDTH / 3;
        for (int i = 0; i < ORDER; i++) {

            bvec3 thing = bvec3(!(bool(modint(enter.y / num - 1, 3)) || bool(modint(enter.z / num - 1, 3))), !(bool(modint(enter.x / num - 1, 3)) || bool(modint(enter.z / num - 1, 3))), !(bool(modint(enter.x / num - 1, 3)) || bool(modint(enter.y / num - 1, 3))));

            if (thing.x || thing.y || thing.z) {

                if (thing.x && thing.y && thing.z)
                    inf = bvec3(0);
                else
                    inf = thing;

                break;
            }

            num /= 3;
        }

        if (num == 0) {
            o = vec4(1);
            float dist = distance(endPos, pos);

            vec3 normal = sign(d) * (lastEntered);
            if (plane) normal = vec3(sin(iTime), 0, cos(iTime));

            vec3 lightPos = pos;

            o = vec4(0);
            o.xy = vec2(dot(normalize(-lightPos + float(WIDTH) / 2.), normal));
            o.xy = o.xy / 2. + .5;
            if (plane) o.xyz = vec3(1,0,0);

            o /= (dist * dist / float(WIDTH * WIDTH) / 4. + 1.);
            break;
        }

        ivec3 nextPlane = num * (enter / num) + num * ivec3(.5 + .5 * sign(d));
        //ivec3 nextPlane = enter + (ivec3(sign(d)) + 1) / 2;

        nextPlane += 10000 * ivec3(inf) * ivec3(floor(sign(d)));

        vec3 distToNext = (vec3(nextPlane) - endPos) / d;
        bvec3 mask = lessThanEqual(distToNext.xyz, min(distToNext.yzx, distToNext.zxy));
        float time = dot(distToNext, vec3(mask));

        endPos = vec3(mask) * vec3(nextPlane) + (endPos + d * time) * (1. - vec3(mask));

    }


    return o;
}


