float R2 = 0.5;
float TAU = 6.28318530718;
float F = 0.86602540378;

vec2 hash(vec2 p, float z) {
    vec3 p3 = vec3(p, z);
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    float t = TAU * fract((p3.x + p3.y) * p3.z);
    return vec2(cos(t), sin(t));
}

vec2 skew(vec2 p) {
    p.y /= F;
    p.x += 0.5 * p.y;
    return p;
}

vec2 unskew(vec2 q) {
    q.x -= 0.5 * q.y;
    q.y *= F;
    return q;
}

float surflet(vec2 q, vec2 p, float z) {
    vec2 h = hash(q, z);
    vec2 d = unskew(q) - p;
    float d2 = d.x * d.x + d.y * d.y;
    return pow(max(0.0, R2 - d2), 4.0) * dot(d, h);
}

float simplex(vec2 p, float z) {
    vec2 q = skew(p);
    vec2 qa = floor(q);
    vec2 qi = q - qa;
    vec2 qb = qa + vec2(1.0, 1.0);
    vec2 qc;

    if (qi.x < qi.y) {
		qc = qa + vec2(0.0, 1.0);
    } else {
		qc = qa + vec2(1.0, 0.0);
    }

    float ka = surflet(qa, p, z);
    float kb = surflet(qb, p, z);
    float kc = surflet(qc, p, z);
    return ka + kb + kc;
}

float lim(float x) {
    return max(0.0, min(1.0, x));
}

float noise(vec2 p, float l, float w) {
    float k = 0.0;
    float o = 1.0;
    float z;
    for (z = 0.0; z < l; ++z) {
        k += simplex(p / o, z + w) * o;
        o *= 2.0;
    }
    k += (l + 1.0 - z) * simplex(p / o, z + w) * pow(2.0, l);
    return lim(0.5 + 50.0 * k / pow(2.0, l));
}

vec4 terrain(float k) {
    k = lim(k);
    if (k < 0.5) {
        return vec4(0.0, k, 0.7 + 0.6 * k, 1.0);
    } else if (k < 0.55) {
        return vec4(1.0, 4.0 * (k - 0.5) + 0.8, 0.6, 1.0);
    } else if (k < 0.8) {
        return vec4(0, 0.5 * k + 0.5, 0.0, 0.0);
    } else if (k < 0.85) {
        return vec4(0, -9.1 * k + 8.18, 0.0, 1.0);
    }
    return vec4(0, (1.0 - k) * 0.3 + 0.4, 0.0, 1.0);
}

float grid(vec2 p, float cam, float size) {
    size *= 30.0;
    float cc = sqrt(0.25 * cam / size);
    vec2 d = abs(mod(p / size + 0.5, 1.0) - 0.5);
    float dd = min(d.x, d.y);
    if (dd < cc) {
        return 1.0 - dd / cc;
    }
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float zoom = 0.5 + 0.5 * sin(0.1 * iTime);
    float cam = 1.0 + 50.0 * zoom;
    vec2 p = (fragCoord - 0.5 * iResolution.xy) * cam;
    p += 1000.0 * vec2(10.0 * sin(iTime / 10.0), iTime);

    float l = noise(p / 100.0, 7.0, 0.0);
    float k = noise(p, 9.0 + 5.0 * l, 100.0);
    float m = noise(p, 16.0, 0.0);
    float h = 3.0 * m + 0.6 * k - 2.0;
    float r = 1.0 - abs(noise(p / 6.0, 12.0, 200.0) - 0.5) * 100.0;
    if (r > 0.0 && h > 0.5) {
        h = min(h, 0.55 - 0.1 * r);
    }
    fragColor = terrain(h);

    if (zoom < 0.25) fragColor += 0.1 * grid(p, cam, 1.0) * smoothstep(zoom, 0.25, 0.15);
    fragColor += 0.1 * grid(p, cam, 10.0);
}

#define PI 3.1415926535897932384626433832795
#define t iTime * 0.5

vec3 shade(float r, vec2 center, vec2 pos, vec3 l[2])
{
    float z = sqrt(r * r - pos.x * pos.x - pos.y * pos.y);
    vec3 n = normalize(vec3(pos.x, pos.y, z)); 		// Surface normal

    vec3 c_diff = vec3(0.9, 0.0, 0.7);				// Diffuse color    
    vec3 c_spec = vec3(0.0, 1.0, 1.0);				// Specular color

    float m = 11.0; 								// Surface Smoothness
    float El = 0.9; 								// Irradiance of light source
    vec3 Kd = c_diff.xyz / PI; 						// Diffuse term
    vec3 Ks = c_spec.xyz * ((m + 8.0) / (8.0 * PI));// Specular term

    vec3 Lo = vec3(0.0);
    for (int i = 0; i < 2; i++)
    {
        vec3 h = normalize(l[i] + n); // Half vector

        float cosTi = max(dot(n, l[i]), 0.0);
        float cosTh = max(dot(n, h), 0.0);
        Lo += (Kd.xyz + Ks.xyz * pow(cosTh, m)) * El * cosTi; // Outgoing radiance
    }  

    return Lo; 

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//	Sphere Definition
	vec2 center = iResolution.xy / 2.0;
	float r = iResolution.y / 3.0;
	vec2 pos = fragCoord.xy - center;

	//	Light vector
	vec3 l[2];
    l[0] = normalize(vec3(sin(t), sin(t), cos(t)));
    l[1] = normalize(vec3(-sin(t), cos(t), sin(t)));

    // Outgoing radiance
	vec3 Lo = shade(r, center, pos, l);

	fragColor = vec4(Lo, 1.0);
}

