// SDFs and fill function from http://pixelspiritdeck.com/ 
// simplifications and AA suggested by FabriceNeyret2 in the comments
// noise from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83

#define TAU 6.28318530718
#define PI 3.14159265359
#define HALF_PI 1.57079632679

float starSDF(vec2 st, int V, float s) {
	st = st*4.-2.;
    float a0 = atan(st.y,st.x),
         seg = a0 * float(V)/TAU,
           a = ((floor(seg) + 0.5) / float(V) + mix(s,-s,step(.5,fract(seg)))) * TAU;
    return cos(a-a0)*length(st);
}

float rectSDF(vec2 st, vec2 s) {
	vec2 a = abs ( (st*2.-1.) / s);
    return max(a.x,a.y);
}

float circleSDF(vec2 st) {
	return length(st -.5)*2.;
}

float fill(float x, float size) {
	return smoothstep(3./iResolution.y, 0., x-size);
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}


float wavephase(float x) {
    return noise(vec2(x + iTime*0.5))*4.3 + x*8. + iTime*6.223;
}

float wave(float x) {
    return cos(wavephase(x))*x*x*0.5;
}

float waveS(float x) {
    return sin(wavephase(x))*x*x*0.5;
}


float waveNoise(float x) {
	return wave(x) - wave(0.);
}

float waveNoiseS(float x) {
	return waveS(x) - waveS(0.);
}

vec3 yellow =  vec3(1.,0.8,0.);
vec3 blue =  vec3(0.004,0.,0.4);
vec3 red =  vec3(0.8,0.,0.004);
