vec3 white = vec3(1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy ) / iResolution.y;

    //note : malaysian flag ratio is 2:1

    uv *= 1.4;
    uv += vec2(1.,0.5);

    //wavy flag
    uv.y += waveNoise(-uv.x)*0.01;

    //a bit of noise
    uv += noise(uv*2. + iTime*3.)*0.008;

    //color base
    vec3 color = vec3(1.);

    //Crescent and star

    vec2 starPos = (vec2(uv) + vec2(0.05,-0.21)) ;
    float crescentC = fill(circleSDF(starPos),0.4);
    crescentC -= fill(circleSDF(starPos - vec2(0.05,0.)),0.35);
    crescentC = clamp(crescentC,0.,1.);

    starPos -= vec2(0.2,0.);

    float s = smoothstep(
        starSDF(starPos.yx,14,.18),
        starSDF(starPos.yx,14,.177),
        0.14);

    crescentC += s;

    //stripes
    float stripesC = clamp(-8.* sin( 6.27*uv.y*(14. * .5) ) , 0.,1.);

    //rect
    vec2 rectSize = vec2(1.0,8.05/14.);
    vec2 rectPos = uv + vec2(0.005,-3.005/14.0);
    float rectC = fill(rectSDF(rectPos,rectSize),1.);

    //shadow
    float shadowDistance = 0.06;
    float shadowSmooth = 0.06;
    float shadowC = smoothstep(1.,1.-shadowSmooth,rectSDF(
        uv - vec2(0.5+shadowDistance,-shadowDistance),
        vec2(2.,1.)));


    color = mix(color,red,stripesC);
    color = mix(color,blue,rectC);
    color = mix(color,yellow,crescentC);

    //highlight
    color *= (1.-waveNoiseS(-uv.x))* 0.10 + 0.9;

    //mask
    float maskC = fill(rectSDF(vec2(uv) - vec2(0.5,0.),vec2(2.,0.99)),1.);
    color = mix(yellow,color,maskC);


    float shadowOut = clamp(shadowC-maskC,0.,1.);
    color = mix(color,black, shadowOut * 0.05);

	fragColor = vec4(color,1.0);
}

#define MAXIMUM_STEPS 1290
#define DISTANCE_THRESHOLD .3
#define FAR_CLIP 120.0


float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float opDisplace( vec3 p, vec4 t )
{
    float d1 = sdPlane(p, t);
    float d2 = (sin(1.9*p.x+(sin(p.z)*2.))*0.9-1.)*.3;
    float d3 = cos(mod(p.z,133.))*0.3*sin(iTime*4.);
    return d1+d2+d3;
}

/* ROTATIONS */

void rX(inout vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    p.y = c * q.y - s * q.z;
    p.z = s * q.y + c * q.z;
}

void rY(inout vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    p.x = c * q.x + s * q.z;
    p.z = -s * q.x + c * q.z;
}

void rZ(inout vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    p.x = c * q.x - s * q.y;
    p.y = s * q.x + c * q.y;
}

float nearestSurface(vec3 rp) {

    float msd = 99999.;
    vec4 plo = vec4(.0,.2,.0,.0);

    float orientation = 0.2;
    msd = min(msd, opDisplace(rp, plo)); 

    return msd;
}

/* RAY MARCHING */

//march a single ray
vec4 marchRay(vec3 ro, vec3 rd) {

    float d = 0.0; //distance marched
    vec4 pc = vec4(0.); //pixel colour

    for (int i = 0; i < MAXIMUM_STEPS; ++i) {

        vec3 rp = ro + rd * d; //ray position
        float ns = nearestSurface(rp);
        d += ns;

        if (abs(ns) < DISTANCE_THRESHOLD*0.5) {
            rp.xyz += 4.;
            rp.xyz /= 8.;
            vec4 clr = vec4(1.);

            pc = vec4(sin(rp.x), sin(rp.y), sin(rp.z), 1)*clr;
            break;
        }

        if (d > FAR_CLIP) {
            pc = vec4(0.1, 0.3, 0.9, 1.);
            pc *= 1.-smoothstep(0.02, 0.33, rd.y) + 0.8;
            break;
        }
    }

    return pc;
}

