// Fork of https://www.shadertoy.com/view/lslXDf

// An antialiased barycentric triangle!

#define ANTIALIAS 4.

// Does this use Cramer's rule?
float sdf_triangle(vec2 vec_p, vec2 vec_a, vec2 vec_b, vec2 vec_c){
    vec2 dvec_ab = vec_b - vec_a;  // Displacement-vector
    vec2 dvec_ac = vec_c - vec_a;  // Displacement-vector
    vec2 dvec_ap = vec_p - vec_a;  // Displacement-vector

    float det_dvec_ab_dvec_ac = dvec_ab.x * dvec_ac.y - dvec_ac.x * dvec_ab.y;  // Determinant of the 2 x 2 matrix made of row vectors dvec_ab and dvec_ac
	float det_dvec_ap_dvec_ac = dvec_ap.x * dvec_ac.y - dvec_ac.x * dvec_ap.y;  // Determinant of the 2 x 2 matrix made of row vectors dvec_ap and dvec_ac
    float det_dvec_ab_dvec_ap = dvec_ab.x * dvec_ap.y - dvec_ap.x * dvec_ab.y;  // Determinant of the 2 x 2 matrix made of row vectors dvec_ab and dvec_ap
    float inv_det_dvec_ab_dvec_ac = 1. / det_dvec_ab_dvec_ac;  // Multiplicative-inverse of a determinant

    float bary_x = det_dvec_ap_dvec_ac * inv_det_dvec_ab_dvec_ac;  // Why does this work?
    float bary_y = det_dvec_ab_dvec_ap * inv_det_dvec_ab_dvec_ac;  // Why does this work?
    float bary_z = 1. - bary_x - bary_y;  // The last barycentric coordinate is easy!

    return min(min(bary_x, bary_y), bary_z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;  // The Fabrice map!
    uv = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * uv;

    //vec2 mouse_uv = (2. * iMouse.xy - iResolution.xy) / iResolution.y;
    //mouse_uv = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * mouse_uv;

    vec2 triangle_a = vec2(.0, -.8);
    vec2 triangle_b = vec2(.8,  .0);
    vec2 triangle_c = vec2(.0,  .8);//mouse_uv;
    float triangle = sdf_triangle(uv, triangle_a, triangle_b, triangle_c);
    triangle = smoothstep(0., ANTIALIAS / iResolution.y, triangle);

	fragColor.rgb = vec3(triangle);
}

