const int kNumPoints = 16;
int gMethod;

void generatePath( out vec2 path[kNumPoints], uint seed )
{
    const vec2 off = vec2(0.0,0.0);
    const vec2 sca = vec2(2.0,1.5);

    vec2 d = vec2(1000.0);
    vec2 q = vec2(0.0);           
    path[0] = sca*q + off;

    float dir = 0.0;
    for( int i=1; i<kNumPoints; i++ )
    {
        float h = float(i)/float(kNumPoints);
        dir += 1.7*(-1.0+2.0*hash( uint(i*7)+seed) );
        q += 0.16*vec2( cos(dir), sin(dir) );
        q = mix( q, path[0], pow(h,16.0) );
        path[i] = sca*q + off;
    }
}

vec2 computeTangent( in vec2 a, in vec2 b, in vec2 c )
{
    return (gMethod==0) ? c - a :
           (gMethod==1) ? normalize( c-a ) :
                          normalize( normalize(c-b) + normalize(b-a) );
}

void computeNormals( out vec2 norm[kNumPoints], in vec2 path[kNumPoints] )
{
    for( int i=0; i<kNumPoints; i++ )
    {
        vec2 n = computeTangent( path[(i-1+kNumPoints)%kNumPoints],
                                 path[(i+0+kNumPoints)%kNumPoints],
                                 path[(i+1+kNumPoints)%kNumPoints] );
        norm[i] = vec2(n.y, -n.x );
    }
}

vec3 drawPathAndNormals( in vec3 col, in vec2 p, in float e, in vec2 path[kNumPoints], in vec3 pathColor, in float normLength )
{
    vec3 d = vec3(1000.0);
    for( int i=0; i<kNumPoints; i++ )
    {
        vec2 a = path[(i-1+kNumPoints)%kNumPoints];
        vec2 b = path[(i+0+kNumPoints)%kNumPoints];
        vec2 c = path[(i+1+kNumPoints)%kNumPoints];

        vec2 n = computeTangent( a, b, c );

        n = normLength*normalize(vec2(n.y, -n.x ));

        d = min( d, vec3(sdSegmentSq(p,b,c), 
                         sdPointSq(p,b),
                         sdSegmentSq(p,b,b+n)) );
    }
    d = sqrt(d);

    col = mix( col, pathColor, 1.0-smoothstep(0.0,2.0*e,d.x) );
    col = mix( col, pathColor, 1.0-smoothstep(5.0*e,6.0*e,d.y) );
    col = mix( col, pathColor, 1.0-smoothstep(0.0,2.0*e,d.z) );

    return col;
}

vec3 drawNormals( in vec3 col, in vec2 p, in float e, in vec2 path[kNumPoints], in vec2 norm[kNumPoints], in vec3 pathColor, in float normLength )
{
    float d = 1000.0;
    for( int i=0; i<kNumPoints; i++ )
    {
        vec2 b = path[i];
        vec2 n = normLength*normalize(norm[i]);

        d = min( d, sdSegmentSq(p,b,b+n) );
    }
    d = sqrt(d);

    return mix( col, pathColor, 1.0-smoothstep(0.0,2.0*e,d) );
}

ivec2 letterOffsets[] = ivec2[]( 
    ivec2(13,11), ivec2(5,9), ivec2(4,8), ivec2(8,9),
    ivec2(15,9), ivec2(4,9), ivec2(0,5), ivec2(0,12) );


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // cycle through the three method to compute and stoer normals
    float h = mod( iTime, 12.0 );
         if( h>8.0 ) gMethod = 2;
    else if( h>4.0 ) gMethod = 1;
    else             gMethod = 0;

    letterOffsets[7].x = gMethod;

    // visualization        
    float e = 2.0/iResolution.y;
	vec2 p = (-iResolution.xy+2.0*fragCoord) / iResolution.y;

    // generate 3 shapes
    vec2 pathA[kNumPoints];
    vec2 pathB[kNumPoints];
    vec2 pathC[kNumPoints];
	generatePath(pathA, 1U);
	generatePath(pathB, 8U);
    generatePath(pathC,15U);

    // compute their normals
    vec2 normA[kNumPoints];
    vec2 normB[kNumPoints];
    vec2 normC[kNumPoints];
    computeNormals( normA, pathA );
    computeNormals( normB, pathB );
    computeNormals( normC, pathC );

    // interpolate shapes and normals
    float t = iTime*0.05;
    vec3  w = 0.5 + 0.3*sin(vec3(10.0,11.0,12.0)*t + vec3(0.0,3.0,2.0)) + 0.2*sin(vec3(21.0,19.0,20.0)*t + vec3(1.0,4.0,3.0));
    w /= (w.x+w.y+w.z);

    vec2 pathR[kNumPoints];
    vec2 normR[kNumPoints];
    for( int i=0; i<kNumPoints; i++ )
    {
        pathR[i] = w.x*pathA[i] + w.y*pathB[i] + w.z*pathC[i];
        normR[i] = w.x*normA[i] + w.y*normB[i] + w.z*normC[i];
    }

    // draw interpolated path, its real normals (in blue), and the interpolated normals (in red)
    vec3 col = vec3(1.0);

    col = drawNormals(         col, p, e, pathR, normR, vec3(1.0,0.5,0.0), 0.3 );
	col = drawPathAndNormals(  col, p, e, pathR,        vec3(0.0,0.3,0.8), 0.3 );

    // text
    {
        vec2 uv = (p-vec2(-0.5,0.8))*8.0;

        ivec2 iuv = ivec2(floor(uv));
        vec2 fuv = fract(uv);

        if( iuv.x>=0 && iuv.x<=7 && iuv.y==0 )
        {
            fuv = 0.5 + vec2(0.5,1.0)*(fuv-0.5);
            float te = textureLod( iChannel0, ( vec2(letterOffsets[iuv.x]*64) + fuv*64.0 )/1024.0, 0.0 ).w;
            te = smoothstep( 0.48, 0.51, te );
            col *= te;
        }
    }

    // vignette
    col *= 1.0 - 0.15*length(p);

	fragColor = vec4(col,1.0);
}

