vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void iterate(inout vec2 z, vec2 c) {
	z = vec2( z.x*z.x*z.x - 3.0*z.x*z.y*z.y, 3.0*z.x*z.x*z.y - z.y*z.y*z.y) + c;
    z = abs(z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	

    float scale = max(pow(0.9, iTime), 0.003);
    vec2 center = vec2(0.645, 0.417);

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv *= scale;
    uv.y = - uv.y;
    uv += center;
    //float theta = iTime*10.0;
    //mat2 rot = mat2 (cos(theta), sin(theta), -sin(theta), cos(theta));
    //uv *= rot;

    vec2 z = uv;
    vec2 c = uv;

    float iterations = -1.0;;
    for (float i = 0.0; i < 1000.0; i++) {
        iterate(z,c);
        if (dot(z,z) > 64.0) {
            iterations = i;
            break;
        }
    }

    if (iterations >= 0.0) {
        iterations = iterations + 2.0 - log(log(length(z)))/log(3.0);

    	float hue = pow(iterations/50.0,0.2);
    	hue += 0.2;

		fragColor = vec4(hsv2rgb(vec3(hue,1.0,1.0)) ,1.0);
    }
    else fragColor = vec4(vec3(0.0),1.0);
}

