void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float f,z,z0;
const float n = 40.;

vec2 xy0 = iResolution.xy/2.;
float time = iTime*0.25;
float h = iResolution.x/32.;
float r0 = iResolution.x/5.5;	// Step of waves
float r1 = iResolution.x/10.5;	// Height of waves
float phi = 35.;

if (iMouse.z > 0.) phi+=(1.-2.*iMouse.y/iResolution.y)*45.;

float pi = 3.14159265;
float p180 = pi / 180.;

float sf = sin(phi * p180);
float cf = cos(phi * p180);

mat2 trot = mat2(cos(time),sin(time),-sin(time),cos(time));

vec2 xy = vec2(0.);
vec2 r = vec2(0.);
vec3 col = vec3(0.);

xy.x = fragCoord.x - xy0.x;  	

z0 = -10000.;

for(float j=0.; j<=n; j++)
  {
  xy.y = abs(xy.x) - n * h + 2. * h * j;
  for(int i=0; i<=1; i++)
	{
	r = xy * trot;
	r.y += sign(r.x) * r0;
	f = cos(pi * length(r) / r0);

	z = xy.y * sf + f * r1 * cf;
	if(z > z0)
		{
		if( abs(xy0.y+z-fragCoord.y) < 2.-.5*abs(f) )
		col = smoothstep(.33, .66, abs(mod(vec3(f+.33,f-.33,f+1.),2.)-1.));
		z0 = z + 1.;
		}
	xy.y += mod(n * h - abs(xy.x), h) * 2.;	
	}
  if(j > n - abs(xy.x) / h) break;
  }
fragColor = vec4(col,1.);
}

// Attempting to implement clouds as described in "Real-Time Cloud Rendering" 
// paper by Mark Harris and Anselmo Lastra, 2001.
// I may or may have not implemented it correctly.... though clouds plausible imho. 
// It is also rather slow - probably scaling noise in a bad way or something. 

#define PI 3.141592653589793 

const float MAXT = 150.0f;
const float FOGHEIGHT = 0.40; // background fog height. 
const float FOGFADEHEIGHT = 0.60; // background fog fade height - fades background fog into actual sky.
const vec3  FOGCOLOR = vec3(0.839, 1, 0.980);
const float FOGDENSITY = 0.009; 
const float FOGPOWER = 3.0;

const vec3  SUNDIRECTION = normalize(vec3(-1.0, -0.6, -0.9)); //direction of the sunlight
const vec3  SUNCOLOR =vec3(0.885, 0.980, 0.980); // sun color? 
const vec3  SKYCOLOR = vec3(0.513, 0.882, 0.945);

// colors for scene objects...
const vec3 TILE1_COLOR  = vec3(0.6);
const vec3 TILE2_COLOR  = vec3(0.8);

const vec3 CLOUD_ALBEDO = vec3(1.0);
const float CLOUDTRACE_NUMSTEPS = 20.0; // number of cloud samples. 
const float CLOUDTRACE_STEPSIZE = 0.1;  // spacing of cloud samples.


struct TraceResult {
    bool  hit;
    float rayt;
    float materialID;
};

// transformation funcs
mat3 rotateY(float n) {
    float a = cos(n);
    float b = sin(n);
    return mat3( a, 0.0, b, 0.0, 1.0, 0.0, -b, 0.0, a );
}

mat3 rotateX(float n) {
    float a = cos(n);
    float b = sin(n);
    return mat3( 1.0, 0.0, 0.0, 0.0, a, -b, 0.0, b, a );
}

mat3 rotateZ(float n) {
    float a = cos(n);
    float b = sin(n);
    return mat3(a, -b, 0.0, b, a, 0.0, 0.0, 0.0, 1.0);
}

// NOISE FUNCTIONS
float random(in vec3 st) { 
    return fract(sin(dot(st,vec3(12.9898,78.233,19.124)))*43758.5453);
}

float noise(in vec3 st) {
    vec3 i = floor(st);
    vec3 x = fract(st);
    vec3 u = x*x*(3.0-2.0*x);

    float a = random(i);
    float b = random(i + vec3(1.0, 0.0, 0.0));
    float c = random(i + vec3(0.0, 1.0, 0.0));
    float d = random(i + vec3(1.0, 1.0, 0.0));
    float e = random(i + vec3(0.0, 0.0, 1.0));
    float f = random(i + vec3(1.0, 0.0, 1.0));
    float g = random(i + vec3(0.0, 1.0, 1.0));
    float h = random(i + vec3(1.0, 1.0, 1.0));
    float fa = mix(a, b, u.x);
    float fb = mix(c, d, u.x);
    float fc = mix(e, f, u.x);
    float fd = mix(g, h, u.x);
    float fe = mix(fa, fb, u.y);
    float ff = mix(fc, fd, u.y);
    float fg = mix(fe, ff, u.z);
    return clamp(2.0*fg-1.0, -1.0, 1.0);
}

float fbm(vec3 p, int oct) {
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 1.5;

    for (int i = 0; i < oct; i++) {
        value += amplitude * noise(p*frequency);
        p *= 2.;
        amplitude *= 0.45;
        frequency *= 1.2;
    }
    return value;
}

// Some primitives and CSG ops.
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,vec3(0.0)));
}
float sdPlane(vec3 p, vec4 n) { return dot(p, n.xyz) - n.w; }
float opAdd(float d1, float d2) { return min(d1, d2); }
float opSubtract( float d1, float d2 ) { return max(-d1,d2); }

float clouds(vec3 p) {
    float f;
    f = (sdBox(p, vec3(9000.0, 1.35, 9000.0)));
    f -= noise(p * 0.4)*0.5+0.5;
    f = opSubtract(noise((p+vec3(iTime*0.8, 0.0, 0.0))*0.12+iTime*0.02)*0.8, f);
    f -= fbm(p*0.7+iTime*0.1, 2) *0.15;
    return f;
}

float map(vec3 p, out float mattype) {
    mattype = 1.0;
    float cl = clouds(p);
    float pl = sdPlane(p, vec4(0.0, 1.0, 0.0, -20.0));
    float f = opAdd(cl,pl);
    if (f == pl) mattype = 2.0;
    return f;
}

float map(vec3 p) { float unused; return map(p, unused); }

TraceResult trace(vec3 ro, vec3 rd) {
    TraceResult traceResult = TraceResult(false, 0.0, 0.0);
    float t = 0.02;
    float tmax = MAXT; 
    float mattype;

    for (; t < MAXT; ) {
        vec3 rp = ro + rd * t;
        float tr = map(rp, mattype);
        if (tr<0.0001) {
            traceResult = TraceResult(true, t, mattype);
            break;
        }
        t += tr;
    }
    traceResult.rayt = t;
    return traceResult;
}

//@ro - cloud starting position.
//@rd - view direction
//@ld - light direction 
vec3 traceCloud(vec3 ro, vec3 rd, vec3 ld) {
    float a = 1.118;  // transparency constant - each particle in the cloud will have this transparency.
    float b = 0.056;  // distance field- dependent transparency.
    float c = 0.896;  // air transparency;

    vec3 albedo = CLOUD_ALBEDO; 
    // phase functions for forward scattering and eye scattering, respectively
    float ph1 = 3.0/4.0*(cos(PI)*cos(PI)+1.0);
    float ph2 = 3.0/4.0*(1.0+dot(ld,rd)*dot(ld,rd));


    vec3 Ik = SUNCOLOR; 
    vec3 Ek = vec3(0.0); // eye scattering, initially no light is scattered...


    const float numsteps = CLOUDTRACE_NUMSTEPS;
    const float stepsize = CLOUDTRACE_STEPSIZE;

    for (float i = stepsize; i <= stepsize*numsteps; i+=stepsize) {
        // forward scattering
        vec3 rp = ro+(i-stepsize)*ld;
        float m = clouds(rp);
        float transparency = (m<0.1) ? 1.0/(a+abs(m)*b) : c;
        vec3 gk = albedo*transparency*Ik*ph1/(4.0*PI);
        Ik = gk+Ik*transparency;

        // compute eye scattering 
        rp = ro+stepsize*ld; 
        m = clouds(rp);
        transparency = (m<0.01) ? 1.0/(a+abs(m)*b) : c;
        vec3 sk = albedo*transparency*Ik*ph2/(4.0*PI);
        Ek = sk+transparency*Ek;
    }

    // not sure if averaging is correct...! 
    return clamp((Ik+Ek)*0.5, 0.0, 1.0); 
}

vec3 calcNormal(vec3 p) {
    vec2 eps = vec2(0.001,0.0);
    float x = map(p+eps.xyy)-map(p-eps.xyy);
    float y = map(p+eps.yxy)-map(p-eps.yxy);
    float z = map(p+eps.yyx)-map(p-eps.yyx);
    return normalize(vec3(x,y,z));
}

// softshadow function by iq. 
float traceShadow(vec3 ro, vec3 rd) {
    float numsteps = 30.0;
    float stepsize = 1.6;
    float res = 1.0;
    const float k = 200.0;

    for (float i = 0.0; i < numsteps *stepsize; i+=stepsize) {
        float d = map(ro+i*rd);
        if (d < 0.001) { return 0.0; }
        res = min( res, k*d/i );
    }
    return res;
}

float phongDiffuseFactor(vec3 l, vec3 n) {
    return max(0.0, dot(l,n));
}

float phongSpecularFactor(vec3 l, vec3 n, vec3 v, float k) {
    vec3 r = normalize(reflect(l, n));
    return pow(max(0.0, dot(r, v)), k);
}

float fog(float dist, float d, float p) {
    return  1.0 - 1.0/exp(pow(dist*d, p));
}

vec3 sky(vec3 ro, vec3 rd) {
    vec3 color = SKYCOLOR; 
    float d = dot(-SUNDIRECTION, rd); // sun??
    if (d > 0.0)          
        color = mix(color, vec3(1.0), pow(d, 150.0));
    if (rd.y < FOGFADEHEIGHT)     
        color = mix(FOGCOLOR, color, (rd.y-FOGHEIGHT)/(FOGFADEHEIGHT-FOGHEIGHT));
    if (rd.y < FOGHEIGHT) 
        color = FOGCOLOR;
    return clamp(color, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    vec2 st = fragCoord.xy / iResolution.xy;
    float finv = tan(90.0 * 0.5 * PI / 180.0);
    float aspect = iResolution.x / iResolution.y;
    st.x = st.x * aspect;
    st = (st - vec2(aspect * 0.5, 0.5)) * finv;


    vec3 ro = vec3(iMouse.x*0.05, -18.0+iMouse.y*0.075, 0.0);
    vec3 rd = normalize(vec3(st, 1.0));
    rd=rotateX(0.1)*rd;
    rd=rotateY(-0.7)*rd;
    rd = normalize(rd);

    vec3 color = sky(ro, rd);

    TraceResult traceResult = trace(ro, rd);
    if (traceResult.hit) {
        vec3 rp = ro+traceResult.rayt*rd;
        vec3 n = calcNormal(rp);

        if (traceResult.materialID == 1.0) {
            // get to cloud's start tracing position.
            vec3 crp = rp + (-SUNDIRECTION)*CLOUDTRACE_NUMSTEPS*CLOUDTRACE_STEPSIZE*1.001;
            color = 0.1+traceCloud(crp, rd, SUNDIRECTION);
        }

        if (traceResult.materialID == 2.0) {
            vec3 tiles = (sin(rp.z) + sin(rp.x) <= 0.0) ? TILE1_COLOR : TILE2_COLOR;
            vec3 sp = rp + 0.1*n;
            float ph = phongDiffuseFactor(-SUNDIRECTION, n) 
                     + phongSpecularFactor(-SUNDIRECTION, n, rd, 10.0);
            color = 0.5*SUNCOLOR + ph * traceShadow(sp, -SUNDIRECTION);
            color = tiles * color;
        }

        float fg = fog(traceResult.rayt, FOGDENSITY, FOGPOWER);
        vec3 fc = FOGCOLOR;
        color = mix(color, fc, fg);
    }

    color = clamp(color, 0.0, 1.0);
    fragColor = vec4(color, 1.0); 
}

#define epsilon 1e-4
#define mint .001

#define sphere( p,r)  ( length(p) - r )

float box( vec2 p, vec2 b )
{
  vec2 d = abs(p) - b;
  return min(max(d.x,d.y),0.) + length(max(d,0.));
}

float sdf(vec2 pos)
{
    return min(
        box(   pos,vec2( .1,  .2 ) ),
    min(
        sphere(pos-vec2( .5,  0.), .1),
        sphere(pos-vec2(-.3, -.3), .1)
    ));
}

float shadow(vec2 light, vec2 pos, float hard)
{
    vec2  d = light - pos;
	float l = length(d),
        res = 1.;

    for( float t=mint, h; t < l; t+= h)
    {
        h = sdf(pos + d/l*t);
        if( h < epsilon ) return 0.;
        res = min( res, hard*h/t );
    }
    return res;
}

void mainImage( out vec4 O, vec2 U )
{
    vec2  R = iResolution.xy,
         uv = (    U      -.5* R ) / R.y,
       mpos = ( iMouse.xy -.5* R ) / R.y;

    O = sdf(uv) > 0. ? vec4(0) : vec4(1, 0, 0, 1);

    float shad1 = shadow(vec2(3.*sin(iTime), -4.), uv, 30.),
          shad2 = shadow(mpos, uv, 30.);

    O += (shad1+shad2)/2.;
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}
float fbm2(vec2 p) {
    float r = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for(int i = 0; i < 5; i++) {
        r += amp * noise(freq*p);
        amp *= 0.5;
        freq *= 2.0;
    }
    return r;
}


float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}
float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}
float fbm3(vec3 p) {
    float r = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for(int i = 0; i < 5; i++) {
        r += amp * noise(freq*p);
        amp *= 0.5;
        freq *= 2.0;
    }
    return r;
}


float sdSphere(vec3 p, float r) {
    return length(p) - r;
}
float sdPlane(vec3 p, vec4 n) {
    return dot(p, n.xyz) + n.w;
}


vec2 dmin(vec2 d1, vec2 d2) {
    if(d1.x < d2.x) {
        return d1;
    }
    else {
        return d2;
    }
}


vec2 DF(vec3 p) {
    vec3 q = p;
    q.xz = mod(q.xz, 4.0) - 2.0;
    vec2 s1 = vec2(sdSphere(q, 1.0), 1);
    vec2 s2 = vec2(sdPlane(p, vec4(0, 1, 0, 1)), 2);
    return dmin(s1, s2);
}


vec3 calcNormal(vec3 p) {
    float eps = 0.001;
    return normalize(vec3(
        DF(p + vec3(eps, 0, 0)).x - DF(p + vec3(-eps, 0, 0)).x,
        DF(p + vec3(0, eps, 0)).x - DF(p + vec3(0, -eps, 0)).x,
        DF(p + vec3(0, 0, eps)).x - DF(p + vec3(0, 0, -eps)).x
    ));
}


struct Ray {
    bool hit;
    vec3 hitPos;
    vec3 hitNormal;
    int steps;
    float t;
    int hitObj;
};
const int maxSteps = 100;
Ray trace(vec3 from, vec3 rayDir) {
    bool hit = false;
    vec3 hitPos = vec3(0);
    vec3 hitNormal = vec3(0);
    int steps = 0;
    float t = 0.0;
    int hitObj = 0;
    for(int i = 0; i < maxSteps; i++) {
        vec3 p = from + t*rayDir;
        float d = DF(p).x;
        if(d < 0.001) {
            hit = true;
            hitPos = p;
            hitNormal = calcNormal(p);
            steps = i;
            hitObj = int(DF(p).y);
            break;
        }
        t += d;
    }
    return Ray(hit, hitPos, hitNormal, steps, t, hitObj);
}


float detailedAO(vec3 hitPos, vec3 hitNormal, float k) {
    float ao = 0.0;
    for(int i = 1; i <= 5; i++) {
        float d1 = float(i)/float(5) * k;
        vec3 p = hitPos + d1*hitNormal;
        ao += 1.0/pow(2.0, float(i)) * (d1 - DF(p).x);
    }
    return 1.0 - clamp(ao, 0.0, 1.0);
}


float softShadow(vec3 hitPos, vec3 lightPos, float k) {
    vec3 lightDir = normalize(lightPos - hitPos);
    float ss = 1.0;
    float t = 0.1;
    for(int i = 0; i < 100; i++) {
        vec3 p = hitPos + lightDir*t;
        float d = DF(p).x;
        if(d < 0.001) {
            return 0.0;
        }
        ss = min(ss, k * d/t);
        t += d;
    }
    return ss;
}



vec3 phong(vec3 hitPos, vec3 hitNormal, vec3 rayDir, vec3 lightPos, vec3 dc, vec3 sc) {
    vec3 lightDir = normalize(lightPos - hitPos);
    float diffuse = max(dot(lightDir, hitNormal), 0.0);
    float specular = pow(max(dot(reflect(-lightDir, hitNormal), -rayDir), 0.0), 8.0);
    return 0.5*diffuse*dc + 0.5*specular*sc;
}


vec3 marble(float u, float v) {
    vec2 p = vec2(u, v);
    vec2 q = vec2(0);
    vec2 r = vec2(0);
    q.x = fbm2(p + vec2(1, 1));
    q.y = fbm2(p + vec2(2, 2));
    r.x = fbm2(p + 2.0*q + vec2(1, 1));
    r.y = fbm2(p + 2.0*q + vec2(2, 2));
    return fbm2(p + 4.0*r) * vec3(0, q.x + r.x, q.y + r.y);
}
vec3 checkerboard(float u, float v, float interval) {
    u = floor(u/interval*2.0);
    v = floor(v/interval*2.0);
    float p = mod(u + v, 2.0);
    return vec3(0.1 + 0.9*p);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;

    float t = 0.4*iTime;
    vec3 camPos = 3.0 * vec3(sin(t), 0.5, cos(t));
    vec3 camFront = -normalize(camPos);
    vec3 camRight = cross(camFront, vec3(0, 1, 0));
    vec3 camUp = cross(camRight, camFront);
    vec3 rayDir = normalize(1.5*camFront + uv.x*camRight + uv.y*camUp);

    vec3 lightPos = vec3(2, 5, 0);

    Ray tr = trace(camPos, rayDir);
    vec3 color = vec3(0);
    if(tr.hit) {
        float sAO = 1.0 - float(tr.steps)/float(maxSteps);
        float dAO = detailedAO(tr.hitPos, tr.hitNormal, 1.2);
        float fog = exp(-0.2*tr.t);

        float ss = softShadow(tr.hitPos, lightPos, 12.0);

        vec3 mat = vec3(1);
        vec3 brdf = vec3(1);
        if(tr.hitObj == 1) {
        	float u = atan(tr.hitNormal.y, length(tr.hitNormal.xz)) + 3.14;
        	float v = atan(tr.hitNormal.z, tr.hitNormal.x) + 3.14;
            mat = marble(u, v);
            brdf = phong(tr.hitPos, tr.hitNormal, rayDir, lightPos, mat, vec3(1));
        }
        else if(tr.hitObj == 2) {
            mat = checkerboard(tr.hitPos.x, tr.hitPos.z, 1.0);
            brdf = phong(tr.hitPos, tr.hitNormal, rayDir, lightPos, mat, vec3(1));
        }

        float geo = 100.0/pow(distance(tr.hitPos, lightPos), 2.0);

        color = 0.7 * ss * geo * brdf + 0.3*dAO*mat;
        color *= fog;
    }

    fragColor = vec4(color, 1.0);
}

