		float secCoreDif2 = 0.7 * (1.0 + sin(vTime * 0.6 + sin(vTime * 0.4 + fSpec + fNumber * 0.35) + fNumber * 0.4));
		corePos2 = mix(corePos, vec2(sin(secCoreDif1 + vTime * 0.64 + fNumber * 0.71 + fSpec * rotateOnAction), 
									 cos(secCoreDif2 + vTime * 0.73 + fNumber * 1.2 + fSpec * rotateOnAction2)) 
									 * (0.2 + 0.8 * fSpec) * 0.18 * clamp(secCoreDif1 + secCoreDif2, -0.5, 1.0),
					  0.5 * (1.0 + cos(fPi + fPi * fSecCore)));
		fCore = max(fCore, smoothstep(-0.1, -0.04, -length(pMid + corePos2)));
		corePos = (corePos + corePos2) * 0.5;
	}

	float fOuterCore = smoothstep(-0.2, -0.05, -length(pMid + corePos * 0.75) + 2.0 * modifyBorder * fGrain);



	// Cell
	float fShapeVisible = 0.0;
	fShapeVisible = (0.6 + 0.45 * fSpec ) * (1.0 - 0.05 * someNoise - fGrain * 0.1)
						 * smoothstep(-0.42, -0.30 - fSpec * 0.05, -distanceToMid + modifyBorder - min(0.0, fGrain * 0.05))
						 * (1.0 - 0.35 * smoothstep(0.20 - fSpec * 0.0, 0.38, distanceToMid + modifyBorder - min(0.0, fGrain * 0.1))) ;

	float fModifiedDist = distanceToMid * (1.0 - 0.7 * modifyBorder);
	fShapeVisible += fOuterCore * (0.15 - fSpec * 0.45);


	// cell core structure
	if (fCore > 0.0)
	{
		fCore += 0.5 * fGrain;
	}

	// coloring
	vec3 colSpec = mix(vec3(0.45), mix(colMain1, colMain2, fNumber / 40.0), fSpec * 1.0);

	vec3 colSpec2 = mix(vec3(0.25), mix(colDetail1, colDetail2, fNumber / 40.0), fSpec * 1.1);
	colSpec = mix(colSpec, colSpec2, min(1.0, max(0.0, -someNoise * 1.0) * fSpec * fShapeVisible * 1.5));

	// cell core is fading to grey on amplitude
	colSpec = mix(colSpec, vec3(0.1) + vec3(0.25) * fSpec, fCore);



	vec3 colBorder = mix(colSpec * 0.5, vec3(0.2) + vec3(0.1) * fSpec, smoothstep(0.3, 0.6, fModifiedDist))
					* (0.2 + 0.2 * fSpec)
					* smoothstep(-0.9, -0.4, -fModifiedDist)
					* smoothstep(0.3, 0.4, fModifiedDist);
	vec3 colDot = mix(colBorder, colSpec * fShapeVisible, fShapeVisible);




	// 3D Light Effect Test
	fAngle = ((fAngle / fPi) + 0.5) * 3.0 - 0.25 * fSpec * (1.0 + rotateOnAction);

	fAngle *= 1.0 - fSpec * 0.3;
	float fLight = smoothstep(0.0, 1.0, fAngle)
				 * smoothstep(-2.0, -1.0, -fAngle);
	float moveIn = 0.025 * fSpec;
	fLight *= smoothstep(-0.3 - moveIn, -0.2 - moveIn, -fModifiedDist)
			* smoothstep(0.1 + moveIn, 0.2 + moveIn, fModifiedDist);
	colDot += vec3(0.3) * (1.0 - 2.0 * fSpec) * (1.0 - 2.0 * modifyBorder) * fLight;



	fragColor = vec4(colDot, smoothstep(-0.9, -0.38, -fModifiedDist));
}

// Draw a square using polar coordinates
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Fit UV to view height and translate to center
	vec2 uv = fragCoord.xy / iResolution.y - vec2(.5,.5);

    // Convert UV into polar coordinates
    float phi = atan(uv.y,uv.x);

    // Find distance scaling factor for angle - I call it the squarify formula
    float sq = 1.0/cos(mod(phi + 0.785398,1.5708)-0.785398);

    // Scale UV, pushing corners out to form a square
    // Animated using Fabrice Neyret's excellent suggestion
    uv /= mix( 1., sq, .5+.5*sin(iTime));

    // Give us a nice anti-aliased fill
    float val = smoothstep(0.30,0.305,length(uv));

	fragColor = vec4(val,val,val,1.0);
}

#define TAU 6.28318530718

#define FAR 50.
#define ITER 80
#define QUA .001
#define NORK 5e-3

#define REF 0.8
#define RITER 32
#define RRFB .05

#define BRG .4

mat3 rx(float a){ float sa=sin(a), ca=cos(a); return mat3(1.,0.,0.,0.,ca,sa,0.,-sa,ca); }
mat3 ry(float a){ float sa=sin(a), ca=cos(a); return mat3(ca,0.,sa,0.,1.,0.,-sa,0.,ca); }
mat3 rz(float a){ float sa=sin(a), ca=cos(a); return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.); }

float box(vec3 p){ p=abs(p); return max(max(p.x,p.y),p.z); }

float map(vec3 p)
{
    //float s = 10. - box(p);
    float s = 10. - length(p);
    float c = box(p) - 1.;
    return min(s, c);
}

vec3 normal(vec3 p, float k)
{
	vec2 e = vec2(.0,k);
	return normalize(vec3(
		map(p) - map(p - e.yxx),
		map(p) - map(p - e.xyx),
		map(p) - map(p - e.xxy)
	));
}

float tracer(vec3 ro, vec3 rd)
{    
    float t = .0, d;
    for(int i=0; i < ITER; i++){        
        d = map(ro + rd*t);
        if (d < QUA*t || t > FAR) break;
        t += d;
    } 
    return t;
}

float hard_shadow(vec3 sp, vec3 lp, int iter)
{   
	vec3 p, ld = normalize(sp - lp);

	float t = .0;
	for(int i = 0; i < iter; i++)
	{
		p = lp + ld * t;
		float m = map(p);
		if (m < .0 || t > FAR) break;
		t += m;
	}

	return max(1. - distance(p, sp), 0.);
}

vec3 color(vec3 sp, vec3 sn)
{   
    vec3 lp = vec3(-3, 2, -4);
	vec3 ld = normalize(lp - sp);

    float diff = max(dot(sn,ld),0.);
    float shd = hard_shadow(sp, lp, 30);

    vec3 col = (sn.xyz+1.)/2.;
    return col * diff * shd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    mat3 rm = ry(iTime/3.) * rx(iTime/4.);

	vec3 ro = rm * vec3(vec2(.0), -5.);
    vec3 rd = rm * normalize(vec3(uv,2.));

    float t = tracer(ro,rd);
	vec3 sp = ro + rd*t;
	vec3 sn = normal(sp,NORK);

    vec3 col = color(sp, sn);

    for(int i=0; i<RITER; i++)
    {
        if(t > FAR) break;

        rd = normalize(reflect(rd, sn));
        ro = sp + rd * RRFB;

        t = tracer(ro, rd) - RRFB;

        sp = ro + rd * t;
		sn = normal(sp,NORK);

        col += color(sp, sn) * pow(REF, float(i) + 1.);
    }

	fragColor = BRG * vec4(col,1.);
}

