	vec4 color2 = vec4(sin(iTime)*sin(iTime)*0.884, sin(iTime)*sin(iTime)/2.0*0.850+0.5, sin(iTime)*sin(iTime)*0.648,0.1);
	vec4 color3 = vec4(sin(iTime)*sin(iTime)*0.348, sin(iTime)*sin(iTime)*0.555, sin(iTime)*sin(iTime)/2.*0.641+0.6,0.1);
	vec4 pixel;

	if ( r.y*cos(2.5*iTime) < 1.0/3.0) {
