// Computes the inverse of a given quaternion
vec4 QtnInverse(vec4 qtn)
{
    return vec4(qtn.xyz * -1.0, qtn.w);
}

// Compute the product of two quaternions
vec4 QtnProduct(vec4 qtnA, vec4 qtnB)
{
    vec3 vecA = qtnA.w * qtnB.xyz;
    vec3 vecB = qtnB.w * qtnA.xyz;
    vec3 orthoVec = cross(qtnA.xyz, qtnB.xyz);

    return vec4(vecA + vecB + orthoVec,
                 (qtnA.w * qtnB.w) - dot(qtnA.xyz, qtnB.xyz));
}

// Perform rotation by applying the given quaternion to
// the given vector
vec3 QtnRotate(vec3 vec, vec4 qtn)
{
    vec4 qv = QtnProduct(qtn, vec4(vec, 0.0));
    return QtnProduct(qv, QtnInverse(qtn)).xyz;
}

float BaseSphereDF(vec3 coord, float r)
{
    return length(coord) - r;
}

float SphereDF(vec3 coord, float r, vec3 sphPos)
{
    coord -= sphPos;
    coord = QtnRotate(coord, normalize(vec4(normalize(vec3(0.0, 1.0, 1.0)) * sin(iTime) * 4.0, cos(iTime) * 4.0)));
    float noise = fract(length(coord / cos(1e4)) * sin(sin((length(iMouse) + 0.01) * 0.01)));
    float noise2 = fract(length(coord / cos(2e4)) * sin(sin((length(iMouse) + 0.02) * 0.03)));
    float noise3 = fract(length(coord / cos(3e4)) * sin(sin((length(iMouse) + 0.01) * 0.06)));
    return max(length(coord * vec3(noise / noise2, noise2, noise3 / noise2)) - r, BaseSphereDF(coord, r * 1.2));
}

vec3 GetNormal(vec3 samplePoint, float eps, vec3 sphPos, float r)
{
    float normXA = SphereDF(vec3(samplePoint.x + eps, samplePoint.y, samplePoint.z), r, sphPos);
    float normXB = SphereDF(vec3(samplePoint.x - eps, samplePoint.y, samplePoint.z), r, sphPos);

    float normYA = SphereDF(vec3(samplePoint.x, samplePoint.y + eps, samplePoint.z), r, sphPos);
    float normYB = SphereDF(vec3(samplePoint.x, samplePoint.y - eps, samplePoint.z), r, sphPos);

    float normZA = SphereDF(vec3(samplePoint.x, samplePoint.y, samplePoint.z + eps), r, sphPos);
    float normZB = SphereDF(vec3(samplePoint.x, samplePoint.y, samplePoint.z - eps), r, sphPos);

    return normalize(vec3(normXA - normXB,
                          normYA - normYB,
                          normZA - normZB));
}

vec3 RayDir(float fovRads, vec2 viewSizes, vec2 pixID)
{
    vec2 xy = pixID - (viewSizes / 2.0);
    float z = viewSizes.y / tan(fovRads / 2.0);
    return normalize(vec3(xy, z));
}

