#define PI 3.1415926535

vec2 orto(vec2 pv)
{
    return normalize(vec2(-pv.y, pv.x));
}

vec2 rot(vec2 p, float a)
{
    float ca = cos(a);
    float sa = sin(a);
    return vec2(ca*p.x - sa*p.y, ca*p.y + sa*p.x);
}

float sCircle(vec2 p, float r)
{
    float ds = 0.01*r;
    return smoothstep(-ds, ds , r*r - dot(p,p));
}


float sTriangle(vec2 p, vec2 p1, vec2 p2, vec2 p3)
{
    float ds = 0.005;
    vec3 dsv = vec3(ds, ds, ds);
    vec3 pev = vec3(dot( orto(p2 - p1), p - 0.5*(p1 + p2)), 
                   	dot( orto(p3 - p2), p - 0.5*(p2 + p3)),
        			dot( orto(p1 - p3), p - 0.5*(p3 + p1)));
    vec3 S = smoothstep(dsv, -dsv, pev);
    return S.x*S.y*S.z;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 2.2;

    // Colors
	vec3 blue  = vec3(65.0, 105.0, 225.0)/255.0;
    vec3 white = vec3(1.0, 1.0, 1.0); 
    vec3 gray = 0.8*vec3(0.1, 0.1, 0.1);
    vec3 black = vec3(0.0, 0.0, 0.0);
    vec3 brown = 0.1*vec3(255.0, 128.0, 0.0)/255.0 + 0.05*white;

    // Parameters
	int N = 27;  		// Change this to for example 100 ;)    
    float L = 1.0;
    float sft = 0.1;

    // Derived Parameters
    float alpha = PI/float(N);
    float dw = L*tan(alpha);
    float R = L - sft;
    vec2 shift = vec2(0.0, sft);

    // Blade reference vertices (triangle)
    vec2 p1 = vec2(0.0, L) - shift;
    vec2 p2 = vec2( dw, 0.0) - shift;
    vec2 p3 = vec2(-dw, 0.0) - shift;

    // Parameterization for the animation
    float A = 0.9;
    float B = 1.5;
    float st = 0.5*(A + B) + 0.5*(B-A)*tanh(4.0*sin(1.5*iTime));


    // Shading
    float blades = 0.0; 
    float nails = 0.0;

   	for (int i = 0; i < N; i++)
    {
        float a = 2.0*PI*float(i)/float(N);

        vec2 p = R*vec2(cos(a), sin(a));

        blades += sTriangle(uv, 
                            rot(p1, a + st) + p, 
                            rot(p2, a + st) + p, 
                            rot(p3, a + st) + p);

    	nails += sCircle(uv - p, 0.025);   
    }

    float dR = 0.1;
    float sRing = sCircle(uv, R + dR) - sCircle(uv, R - dR);
    //sRing = 0.0;  //uncomment to remove ring

    vec3 c = gray;
    c += (blue - gray)*(blades - sRing*blades) + (brown)*(sRing-nails) + blue*nails  ;
    c += 0.2*blue*(1.0 - smoothstep(+2.0, -2.0, 2.0 - dot(uv,uv)));
    c += 0.2*blue*(1.0 - smoothstep(+0.5, -0.5, 0.5 - dot(uv,uv)));

    fragColor = vec4(c, 1.0);
}

// Trying out ESM using float->vec4 encoding:
// https://stackoverflow.com/questions/7059962/how-do-i-convert-a-vec4-rgba-value-to-a-float/7237286#7237286

vec4 encode32(float f) {
    float e =5.0;

    float F = abs(f); 
    float Sign = step(0.0,-f);
    float Exponent = floor(log2(F)); 
    float Mantissa = (exp2(- Exponent) * F);
    Exponent = floor(log2(F) + 127.0) + floor(log2(Mantissa));
    vec4 rgba;
    rgba[0] = 128.0 * Sign  + floor(Exponent*exp2(-1.0));
    rgba[1] = 128.0 * mod(Exponent,2.0) + mod(floor(Mantissa*128.0),128.0);  
    rgba[2] = floor(mod(floor(Mantissa*exp2(23.0 -8.0)),exp2(8.0)));
    rgba[3] = floor(exp2(23.0)*mod(Mantissa,exp2(-15.0)));
    return rgba / 255.0;
}

float decode32(vec4 rgba) {
    rgba *= 255.0;
    float Sign = 1.0 - step(128.0,rgba[0])*2.0;
    float Exponent = 2.0 * mod(rgba[0],128.0) + step(128.0,rgba[1]) - 127.0; 
    float Mantissa = mod(rgba[1],128.0)*65536.0 + rgba[2]*256.0 +rgba[3] + float(0x800000);
    float Result =  Sign * exp2(Exponent) * (Mantissa * exp2(-23.0 )); 
    return Result;
}

