    float t = iTime - 10.0;

    float dist = length(p);
    float angle = atan(p.y, p.x) + PI;

    // create, subdivide vortex
    float angle_2 = angle * RAYS / PI + cos(dist * 15.0) * cos(t * 0.5) * (0.5 / (dist + 0.1));

    float cell_angle = mod(floor(angle_2), RAYS * 2.0);
    float cell_dist = pow(dist, 0.6) * 10.0 - (t + 0.5) * (mod(cell_angle, 2.0) - 0.5) * (0.4 + 0.6 * random(vec2(cell_angle + 0.1)));

    float s = abs(floor(cell_dist));
    float c = length(vec2(abs(fract(angle_2) - 0.5),
                          abs(fract(cell_dist)) - 0.5));
    // anti-aliasing
    float eps = 10.0 / (dist * PI * iResolution.y);

    float mask = 1.0 - smoothstep(SIZE - eps, SIZE + eps, c);
    mask *= step(random(vec2(s, cell_angle)), PROB);

    // rainbow
    float col_ang = cell_angle * PI / RAYS;
	vec3 col = cos(vec3(col_ang) + vec3(0.0, 2.0/3.0, 4.0/3.0) * PI) * 0.5 + 0.5;

    // normal
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 n2 = rot * vec2(fract(angle_2) - 0.5, fract(cell_dist) - 0.5);
    vec3 norm = normalize(vec3(n2, cos(c / SIZE * 0.5 * PI)));

    // lights
    vec3 l1 = clamp(dot(normalize(vec3(-1.0, 0.0, 0.0)), norm), 0.0, 1.0) * vec3(0.6, 0.9, 0.95);
    vec3 l2 = pow(clamp(dot(normalize(vec3(0.0, -1.0, 1.0)), norm), 0.0, 1.0), 16.0) * vec3(0.95, 0.9, 0.6);

	fragColor = vec4(mix(vec3(1.0), col + 0.5 * l1 + l2, mask), 1.0);

    // gamma correction
    fragColor.xyz = pow(fragColor.rgb, vec3(1.0/2.2));
}

//// Definitions

#define CAMERA_SPEED 0.5

#define MAX_DIST 50.0
#define EPSILON 0.0001

#define SHADOWS
#define SHADOW_THRESHOLD 0.01
#define SOFT_SHADOWS
#define SHADOW_HARDNESS 50.0

#define LIGHT_INTENSITY 25.0
#define AMBIENT_LIGHT 0.05

#define MAX_REFLECTIONS 5
#define REFLECTION_THRESHOLD 0.01

#define FOG
#define FOG_DISTANCE 25.0
#define FOG_COLOR vec3(0.2, 0.25, 0.3)

#define MATERIAL_LIGHTGRAY Material(vec3(0.9, 0.9, 0.9), 0.5, 8.0, 0.4)
#define MATERIAL_GREEN Material(vec3(0.258824, 0.956863, 0.607843), 0.8, 8.0, 0.7)
#define MATERIAL_BLUE Material(vec3(0.258824, 0.52549, 0.956863), 0.8, 4.0, 0.4)
#define MATERIAL_PURPLE Material(vec3(0.878431, 0.4, 1.0), 1.0, 8.0, 0.0)
#define MATERIAL_YELLOW Material(vec3(1.0, 0.843137, 0.0), 1.0, 8.0, 0.2)

//// Structs

struct Material {
    vec3 color;
    float diffuse;
    float specular;
    float reflectivity;
};

struct MapInfo {
    Material material;
    // Distance of the point we got info from to the map
    float hit;
};

struct Ray {
    // Origin of the ray
    vec3 origin;
    // Ray's direction
    vec3 direction;

    // Where the ray is aiming at
    vec3 target;

    // Current position along the ray
    float length;
    // Just so we don't have to calculate it multiple times \_()_/
    vec3 position;
};

//// Ray initializer

Ray initRayToTarget(vec3 origin, vec3 target) {
    Ray ray;

    // Init ray values
    // Calculate ray direction
    ray.direction = normalize(target - origin);

    // Start a bit away from the origin so we don't hit whatever is at the start
    ray.origin = origin + ray.direction * EPSILON;

    ray.target = target;
    ray.position = origin;

    return ray;
}

Ray initRayToDirection(vec3 origin, vec3 direction) {
    Ray ray;

    // Init ray values
    ray.direction = direction;

    ray.origin = origin + ray.direction * EPSILON;
    ray.position = origin;

    return ray;
}

//// Distance functions

MapInfo plane(vec3 origin, Material material) {
    return MapInfo(material, -origin.y);
}

MapInfo sphere(vec3 origin, float rad, Material material) {
    return MapInfo(material, length(origin) - rad);
}

MapInfo box(vec3 origin, vec3 size, Material material) {
    return MapInfo(material, length(max(abs(origin) - size, 0.0)));
}

//// Distance operations

MapInfo opUnion(MapInfo o1, MapInfo o2) {
    // min(o1, o2)
    if (o1.hit < o2.hit) return o1;
    return o2;
}

MapInfo opSubtract(MapInfo o1, MapInfo o2) {
    // max(-o1, o2)
    if (-o1.hit > o2.hit) return MapInfo(o1.material, -o1.hit);
    return o2;
}

MapInfo opIntersect(MapInfo o1, MapInfo o2) {
    // max(o1, o2)
    if (o1.hit > o2.hit) return o1;
    return o2;
}

//// The main mapping function

MapInfo map(vec3 origin) {
    // red plane at origin
    MapInfo mapInfo = plane(origin, MATERIAL_LIGHTGRAY);
    // blue sphere at 1.5, 1.0, 1.5 with radius 1.0
    mapInfo = opUnion(mapInfo, sphere(origin + vec3(1.5, 1.0, 1.5), 1.0, MATERIAL_BLUE));
    // green box at -1.5, 1.0, 1.5 with size 1.0 x 1.0 x 1.0
    mapInfo = opUnion(mapInfo, box(origin + vec3(-1.5, 1.0, 1.5), vec3(1.0), MATERIAL_GREEN));
    mapInfo = opUnion(mapInfo, opSubtract( // subtract sphere from box
        sphere(origin + vec3(-1.5, 1.0, -1.5), 1.2, MATERIAL_PURPLE),  // purple sphere at -1.5, 1.0, -1.5 with radius 1.2
        // purple box at -1.5, 1.0, -1.5 with size 1.0 x 1.0 x 1.0
        box(origin + vec3(-1.5, 1.0, -1.5), vec3(1.0), MATERIAL_PURPLE))
    );
    mapInfo = opUnion(mapInfo, opIntersect( // intersect sphere with box
        box(origin + vec3(1.5, 1.0, -1.5), vec3(1.0), MATERIAL_YELLOW), // yellow box at 1.5, 1.0, -1.5 with size 1.0 x 1.0 x 1.0
        // yellow sphere at 1.5, 1.0, -1.5 with radius 1.2
        sphere(origin + vec3(1.5, 1.0, -1.5), 1.2, MATERIAL_YELLOW))
    );
    return mapInfo;
}

//// Raymarching functions

vec3 calcNormal(vec3 position) {
    // Step around the point and see how far we are from the map at each position
    // and do some math to figure out what the normal is
    // I use a value of 0.02 because I find that's the best balance between accuracy and
    // avoiding those nasty rings on some objects
    vec2 eps = vec2(0.0, 0.005);
    return normalize(vec3(
        map(position + eps.yxx).hit - map(position - eps.yxx).hit,
        map(position + eps.xyx).hit - map(position - eps.xyx).hit,
        map(position + eps.xxy).hit - map(position - eps.xxy).hit
    ));
}

MapInfo trace(inout Ray ray) {
    while(distance(ray.position, ray.origin) < MAX_DIST) {
        // Get info about our position in relation to the map
        MapInfo mapInfo = map(ray.position);
        // If we hit something, return the info about our position on the map
        if(mapInfo.hit < EPSILON) return mapInfo;

        // Step forward along the ray, as far as our distance to the map
        ray.position += ray.direction * mapInfo.hit;
    }
    // Return fog if we didn't hit anything
    return MapInfo(Material(FOG_COLOR, 0.0, 0.0, 0.0), 1.0);
}

float softshadow(inout Ray ray, float softness) {
    // While we're not past the target, do the stuff
    // Subtract EPSILON * 2 so we don't get close enough to the original object to trigger the shadow
    float penumbra = 1.0;
    while(distance(ray.origin, ray.position) < distance(ray.origin, ray.target) - EPSILON * 2.0) {
        // Get info about our position in relation to the map
        MapInfo mapInfo = map(ray.position);
        // If we hit something, make the color black (shadow)
        if(mapInfo.hit < EPSILON) return 0.0;

        #ifdef SOFT_SHADOWS
            penumbra = min(penumbra, softness * mapInfo.hit / distance(ray.position, ray.target));
        #endif

        if(mapInfo.hit > SHADOW_THRESHOLD) ray.position += ray.direction * mapInfo.hit;
        // Move a bit closer to the target
        else ray.position += ray.direction * SHADOW_THRESHOLD;
    }
    // If we don't hit anything, the point is not in shadow so the shadow multiplier is 1.0
    return penumbra;
}

//// Main function

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //// Setup the viewport
    // Screen coords go from -1.0 to 1.0
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    // Account for screen ratio
    uv.x *= iResolution.x / iResolution.y;

    //// Setup the camera
    // Init camera 5.0 up, rotating in a circle, looking at 0.0, 0.0, 0.0
    // Axes are flipped here I guess? Note the -vec3s
    Ray cameraRay = initRayToTarget(-vec3(
        sin(iTime * CAMERA_SPEED) * 5.0,
        5.0,
        cos(iResolution * CAMERA_SPEED) * 5.0), -vec3(0.0, 0.0, 0.0)
    );
    // Convert to screen coords
    // Global up direction
    vec3 globalUp = vec3(0.0, 1.0, 0.0);
    // Right direction in relation to the camera
    vec3 cameraRight = normalize(cross(globalUp, cameraRay.origin));
    // Up direction in relation to the camera
    vec3 cameraUp = cross(cameraRay.direction, cameraRight);
    // Set the camera ray direction to point from the screen coordinate to our target
    cameraRay.direction = normalize(cameraRight * uv.x + cameraUp * uv.y + cameraRay.direction);

    vec4 reflections[MAX_REFLECTIONS + 1];

    // Background color
    float reflectivity = 1.0;
    for(int i = 0; i <= MAX_REFLECTIONS; i++) {
        // Trace the ray's path
        MapInfo mapInfo = trace(cameraRay);

        // If the camera ray hit something
        if(mapInfo.hit < EPSILON) {
            //// Lighting

            // Setup the light source
            // Init light source at 5.0, 5.0, 5.0
            // Again, axes need to be flipped, note the -vec3
            Ray lightRay = initRayToTarget(-vec3(5.0, 5.0, 5.0), cameraRay.position);

            #ifdef SHADOWS
                // Trace shadows
                float shadow = softshadow(lightRay, SHADOW_HARDNESS);
            #else
                float shadow = 1.0;
            #endif

            // Light stuff
            // Calculate the normal of the position on the map
            vec3 normal = calcNormal(cameraRay.position);
            // Light fades by the inverse square of distance
            float distanceFade =  LIGHT_INTENSITY / pow(distance(lightRay.origin, lightRay.target), 2.0);
            // Multiply diffuse by shadow and distance fade
            float diffuse = max(0.0, dot(-lightRay.direction, normal))
                * mapInfo.material.diffuse * shadow * distanceFade;
            // Specular lighting factor
            float specular = pow(diffuse, mapInfo.material.specular);

            // Get color from map info
            vec3 hitColor = mapInfo.material.color;

            // Add lighting values
            hitColor *= diffuse + specular + AMBIENT_LIGHT;

            #ifdef FOG
                // Add fog
                float fogAmount = 1.0 - exp(-max(distance(cameraRay.origin, cameraRay.position) - FOG_DISTANCE, 0.0) * 0.15);
                hitColor = mix(hitColor, FOG_COLOR, fogAmount);
            #endif

            reflections[i] = vec4(hitColor, mapInfo.material.reflectivity);

            // Set the camera ray to the reflection off the surface, and repeat
            cameraRay = initRayToDirection(
                cameraRay.position + normalize(reflect(cameraRay.direction, normal)) * EPSILON,
                normalize(reflect(cameraRay.direction, normal))
            );
        } else {
            reflections[i] = vec4(mapInfo.material.color, 0.0);
            break;
        }
    }

    fragColor = vec4(reflections[MAX_REFLECTIONS].rgb, 1.0);

    for(int i = MAX_REFLECTIONS - 1; i >= 0; i--) {
        fragColor = mix(vec4(reflections[i].rgb, 1.0), fragColor, reflections[i].a);
    }

    // Gamma correction
    fragColor = vec4(pow(clamp(fragColor.xyz, 0.0, 1.0), vec3(0.4545)), 1.0);
}

#define PI 3.14159265359

float random(vec2 uv) {
 return fract(sin(dot(uv,
                         vec2(12.9898,78.233)))*
        49958.5453123);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float val = cos(.78539816339*iTime)*8. + 20.;
    uv*= vec2(val);

    vec2 fraction = fract(uv);
    vec2 position = floor(uv);

    float myTime = clamp(cos(.78539816339*iTime)*2.+1.75, 0.0,4.0);

    float velocity = 5.0 * myTime * (random(vec2(floor(uv.y),10.0)) - 0.5);

	position = floor(vec2(uv.x + velocity,uv.y));

    vec3 color = vec3(1.0);
    color.r = random(position.xx);
    color.g = random(position.xx);
    color.b = random(position.xx);

    color *= smoothstep(0.0,myTime/8.,fraction.y);

	fragColor = vec4(color,1.0);

}

#define _SnowflakeAmount 100	// Number of snowflakes
#define _BlizardFactor 0.2		// Fury of the storm !

vec2 uv;

float rnd(float x)
{
    return fract(sin(dot(vec2(x+47.49,38.2467/(x+2.3)), vec2(12.9898, 78.233)))* (43758.5453));
}

float drawCircle(vec2 center, float radius)
{
    return 1.0 - smoothstep(0.0, radius, length(uv - center));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord.xy / iResolution.x;

    fragColor = vec4(0.808, 0.89, 0.918, 1.0);
    float j;

    for(int i=0; i<_SnowflakeAmount; i++)
    {
        j = float(i);
        float speed = 0.3+rnd(cos(j))*(0.7+0.5*cos(j/(float(_SnowflakeAmount)*0.25)));
        vec2 center = vec2((0.25-uv.y)*_BlizardFactor+rnd(j)+0.05*cos(iTime+sin(j)), mod(sin(j)-speed*(iTime*0.5*(0.1+_BlizardFactor)), 0.65));
        fragColor += vec4(0.09*drawCircle(center, 0.001+speed*0.012));
    }
}

float sphere(vec3 p, vec3 c, float r) {
   return length(p-c) - r;
}

float cylinder(vec3 p, vec3 c, float r, float h) {
   vec3 d = p-c;
   return max(length(d.xz - c.xz) - r, abs(d.y) - h);
}

float box(vec3 p, vec3 b) {
   vec3 d = abs(p) - b;
   return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float plane(vec3 p, vec3 c, vec3 n) {
   return dot(p-c, n);
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(b-a)/k, 0.0, 1.0);
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sdf(vec3 p) {
   return smin(cylinder(vec3(mod(p.x, 1.1)-0.5*1.1,
                             p.y,
                             mod(p.z, 1.1)-1.1/2.0), vec3(0), 0.17, .3),
               plane(p, vec3(0,-0.2,0), vec3(0.0,1.0,0.0)), 0.4);
}


#define MAXSTEP 300
#define EPSILON 0.001f

float march(vec3 eye, vec3 dir) {
   float depth = 0.0f;
   for (int s = 0; s < MAXSTEP; ++s) {
      float dist = sdf(eye + depth * dir);
      if (dist < EPSILON) {
         return depth;
      }
      depth += dist;
   }
   return 0.0;
}

vec3 normal(vec3 p) {
   vec3 normal = vec3(sdf(p + vec3(EPSILON,0.0,0.0)) - sdf(p - vec3(EPSILON,0.0,0.0)),
                      sdf(p + vec3(0.0,EPSILON,0.0)) - sdf(p - vec3(0.0,EPSILON,0.0)),
                      sdf(p + vec3(0.0,0.0,EPSILON)) - sdf(p - vec3(0.0,0.0,EPSILON)));
   return normalize(normal);
}

float phong(vec3 eye, vec3 p, vec3 n, vec3 l) {
   vec3 ld = normalize(l-p);
   vec3 cd = normalize(eye-p);

   float ka = 0.3;
   float kd = 3.0;
   float ks = 1.0;
   vec3 r = 2.0*dot(ld,n)*n - ld;
   r = normalize(r);
   float ds = dot(r, cd);
   float res = ka + kd*dot(ld, n);
   return res + ks*pow(clamp(dot(r, cd),0.0,1.0), 50.0);
}

mat3 view(vec3 eye, vec3 c, vec3 up) {
   vec3 f = normalize(c - eye);
   vec3 s = normalize(cross(f, up));
   vec3 u = cross(s, f);
   return mat3(s,u,f);
}

void mainImage(out vec4 fragcolor, in vec2 fragcoord) {
   float w = iResolution.x;
   float h = iResolution.y;
   float x = fragcoord.x / w;
   float y = fragcoord.y / h;

   float ar = float(w)/h;
   x *= ar;

   x = -ar + x*2.0;
   y = -1.0 + y*2.0;

   vec3 eye = vec3(0,0,-3);
   vec3 px = vec3(x, y, -0.2);
   vec3 dir = normalize( px - eye );

   float t = iTime * 0.3;

   vec3 cam = vec3(0,0.3,0) + vec3(cos(t),0.1*sin(iTime), sin(t));
   mat3 v = view(cam, vec3(0), vec3(0,1,0));
   eye = v * eye;
   dir = v * dir;

   vec3 l = vec3(-1,2,-3);

   fragcolor = y*vec4(0.03,0.08,0.08,1);
   float d = march(eye, dir);
   vec3 p = eye + dir*d;
   vec3 n = normal(p);
   if (d > 0.0f) {
      fragcolor = 0.5*vec4(0.1+0.1*pow(cos(1.3*iTime), 3.0),0.1,0.2, 1);
      fragcolor *=  phong(eye, p, normal(p), l);
      fragcolor *= 5.0/d;
      if (march(p + n*EPSILON, normalize(l-p)) > 0.0f) {
         fragcolor *= 0.3;
      }
   }
}

#define EPS 0.001
#define CAM_NEAR 0.1
#define CAM_FAR 10.

float dSphere(vec3 p, float r)
{
    return length(p) - r;
}

vec3 distort(vec3 p)
{
    return p + 0.253 * sin(p * 3.827 + iTime);
}

float dScene(vec3 p)
{
    vec3 offset = vec3(0., 0., 4.);
    float sp = dSphere(distort(p) + offset, 1.);
    return sp;
}

vec3 estimateNormal(vec3 p)
{
    vec3 e = vec3(EPS, 0., 0.);
    return normalize(vec3(
        dScene(p + e.xyy) - dScene(p - e.xyy),
        dScene(p + e.yxy) - dScene(p - e.yxy),
        dScene(p + e.yyx) - dScene(p - e.yyx)));
}

vec4 custom_lighting(vec3 n, vec3 ld, vec3 v)
{
    float li = max(dot(n, ld), 0.);
    float vi = max(dot(n, v), 0.);
    vec3 x = vi * vec3(11., 13., 17.);
    vec3 c = sin(x);
    return vec4(li * (0.5 * c + 0.5), 1.);
}

vec4 render(vec3 ro, vec3 rd)
{
    float t = CAM_NEAR;
    vec4 color = vec4(0.);
    vec3 light = vec3(-1., 1.5, -2.0);
    for (int i = 0; i < 256; ++i) {
        vec3 p = ro + rd * t;
        float d = dScene(p);
        if (d <= EPS) {
            vec3 n = estimateNormal(p);
            color = custom_lighting(n, normalize(light - p), -rd);
            break;
        }
        if (d > CAM_FAR) {
            break;
        }
        t += d;
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - .5* iResolution.xy) / iResolution.x;
    vec3 ro = vec3(0., 0., 1.);
    vec3 rd = normalize(vec3(uv, 0.) - ro);
    fragColor = render(ro, rd);
}

#define VIEW_WIDTH 6.0
#define CAM_DIST 6.0
#define EPSILON 0.001
#define MAX_MARCH_LENGTH 256
#define SHAPE_N 2

struct RayState {
    float d;
    vec3 base_col;    
};


// ---- SHAPES ----

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float udPlane(vec3 p) 
{
    return abs(p.y);
}

float udWaves(vec3 p)
{
    return abs(p.y-(cos(length(p.xz-vec2(1.0))*20.0+iTime))*0.01);
}
// ---- OPERATIONS ----

float opU( float d1, float d2 )
{
    return min(d1,d2);
}

vec3 opTrans( vec3 p, vec3 t)
{
    return p - t;
}

RayState opUrs(RayState o1, RayState o2)
{
    if (o1.d <= o2.d) return o1;
    else return o2;
}


// ---- MAP SCENE ----
RayState map(vec3 p)
{

    if (SHAPE_N == 0)
    {
        vec3 q = fract(p) * 3.0 - 1.0;
        q = abs(q);
        return RayState(
            q.x + q.y + q.z - 0.35,
            vec3(0.3)
        );
    }
    if (SHAPE_N == 1)
    {
        return RayState(
            sdSphere(p, 0.25),
            vec3(0.2)
        );
    }
    if (SHAPE_N == 2)
    {
        RayState box = RayState(
            udRoundBox(opTrans(p,vec3(-cos(iTime*1.4)*0.5,0.2,sin(iTime*1.4))), vec3(0.1, 0.1, 0.1), 0.05),
            vec3(0.3, 0.2, 0.6)
        );

        RayState plane = RayState(
            udWaves(p),
            vec3(0.0,0.0,0.0)
        );

        RayState sphere = RayState(
            sdSphere(opTrans(p,vec3(0.0,cos(iTime*1.2),sin(iTime*1.2)*0.5)),0.1),
            vec3(0.1,0.5,0.1)
        );

        RayState sphere2 = RayState(
            sdSphere(opTrans(p,vec3(sin(iTime*1.2)*0.5,-cos(iTime*1.2),0.0)),0.1),
            vec3(0.7,0.5,0.2)
        );

        return opUrs(opUrs(opUrs(box, plane),sphere), sphere2);   
    } 
}


vec3 computeNormal(vec3 p) 
{

    vec3 eps = vec3(0.001, 0.0, 0.0);
    vec3 nor = vec3(
        map(p + eps.xyz).d - map(p - eps.xyz).d,
        map(p + eps.zxy).d - map(p - eps.zxy).d,
        map(p + eps.yzx).d - map(p - eps.yzx).d
    );
    return normalize(nor);
}



// ---- LIGHTS ----

vec3 dir_shadow(vec3 origin, vec3 light_dir)
{
    float t = 0.01;

    if(dot(-light_dir,computeNormal(origin))<0.01) {
    	return vec3(0.0);
    }

    for(int i=0; i<16; i++)
    {
        vec3 pos = origin - light_dir * t;
        RayState rs = map(pos);

        if(rs.d < EPSILON) 
        {
            return vec3(-0.1/t);   
        }
		if(rs.d < 0.0)
        {
            return vec3(0.0);
        }

        t += rs.d * 0.8;
    }

    return vec3(0.0);
}

vec3 dir_light(vec3 normal, vec3 light_dir) 
{  
    return vec3(-dot(normal,light_dir));
}

vec3 ambient_light(vec3 base_col)
{
    return base_col;
}

vec3 black_fog(float depth)
{
    return vec3(-depth/(float(MAX_MARCH_LENGTH/3)));
}

vec3 light(RayState rs, vec3 p, vec3 normal, float depth)
{
    vec3 light_dir = vec3(cos(iTime), -1.0, sin(iTime));
    return dir_light(normal, light_dir)*0.4 + ambient_light(rs.base_col) + black_fog(depth)*0.0 + dir_shadow(p, light_dir);
}


// ---- RAYMARCH ----




vec3 trace(vec3 origin, vec3 rayDir)
{
    float t = 0.0;
    for(int i=0; i<MAX_MARCH_LENGTH; i++)
    {
        vec3 pos = origin + rayDir * t;
        RayState rs = map(pos);

        if(rs.d < EPSILON) 
        {
            vec3 normal = computeNormal(pos);

            return light(rs, pos, normal, t);

        }

        t += rs.d * 0.8;
    }

    return vec3(0.0); 
}

