void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Camera
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 target = vec3(0.0);

    float angleX = iMouse.z > 0.0 ? 6.28 * mouse.x : 3.14 + iTime * 0.25;
    float angleY = iMouse.z > 0.0 ? (mouse.y * 6.28) - 0.4 : 0.2;
    vec3 cameraPos = (vec3(sin(angleX)*cos(angleY), sin(angleY), cos(angleX)*cos(angleY))) * 3.0;

    vec3 cameraFwd = normalize(target - cameraPos);
    vec3 worldUp = vec3(0.0,sign(cos(angleY)),0.0);
    vec3 cameraLeft = normalize(cross(cameraFwd, worldUp));
    vec3 cameraUp = normalize(cross(cameraLeft, cameraFwd));

    float viewHeight = VIEW_WIDTH * iResolution.y / iResolution.x;

    // Ray
    vec2 rawPercent = (fragCoord.xy / iResolution.xy); 
    vec2 percent = rawPercent - vec2(0.5,0.5);

    vec3 rayTarget = (cameraFwd * vec3(CAM_DIST,CAM_DIST,CAM_DIST))
        - (cameraLeft * percent.x * VIEW_WIDTH)
        + (cameraUp * percent.y * viewHeight);
    vec3 rayDir = normalize(rayTarget);    


    fragColor = vec4(trace(cameraPos, rayDir), 1.0);
}

