    return q - vec2(g, g);
}

float noise(vec2 q, vec2 p, float z) {
    vec2 h = hash(q, z);
    vec2 d = unskew(q) - p;
    float d2 = d.x * d.x + d.y * d.y;
    return pow(max(0.0, R2 - d2), 4.0) * dot(d, h);
}

