        float k = 0.0;
        float o = 1.0;
        for (float z = 0.0; z < N; ++z) {
            k += noise(p / o, 0.5, z) * o;
            o *= 2.0;
        }
        float m = T;
        if (N < 1.0) m = 1.0;
        k += m * noise(p / o, 0.5, N) * o;
        k = 0.5 + pow(2.0, (1.0 - pT) * 8.96578428466) * 0.1 * k;
        fragColor = vec4(k, k, k, 1.0);
