//
//////////////////////////////////////////////////////////////////////////////

float distanceApprox(vec2 a, vec2 b) {
    vec4 t = vec4(abs(a- .5* (b+ a)),0,0);
    t.xy = vec2(t.y >= t.x ? t.x : t.y,t.y >= t.x ? t.y : t.x);
    t = t.x* vec4(1.0234375,0.5546875,1.296875, 0.1796875)+
        t.y* vec4(1.71875,  1.921875, 1.5234375,1.9921875);
    t.xy = vec2(t.x >= t.z ? t.x : t.z,t.y >= t.w ? t.y : t.w);
    return t.x >= t.y ? t.x : t.y;
}
// By TechEpic @ https://www.shadertoy.com/view/ldfBz2
float distanceArctan(vec2 a, vec2 b) {
    return abs(a.x- b.x)/ abs(sin(atan(a.x- b.x,a.y- b.y)));
}
void mainImage(out vec4 fragColor, vec2 fragCoord) {
    int band = int(fragCoord.y* 3./ iResolution.y);
	vec2 uv = mod(fragCoord.xy,iResolution.xy/ vec2(1,3))/ iResolution.x;
    vec2 mo = mod(iMouse.xy,iResolution.xy/ vec2(1,3))/ iResolution.x;
    vec2 ex = vec2(129,1)* sin((iTime+ fragCoord.x/ 60.)* 0.125);
    float mult = exp2(ex.x);
    uv *= mult;
    mo *= mult;
    float d3 = distanceApprox(uv, mo);
    float d2 = distanceArctan(uv, mo);
    float d1 = distance(uv, mo);
    float d = band == 0 ? d1 : band == 1 ? d2 : d3;
    d3 /= mult;
    d2 /= mult;
    d1 /= mult;
    d /= mult;
    vec3 colors = band == 0 ? vec3(1,d,d) : band == 1 ? vec3(d,1,d) : vec3(d,d,1);
    //colors = clamp(colors*(2.0+ex.y),0.,1.);
    colors *= smoothstep(1.,3.,abs(fragCoord.y- iResolution.y* 0.45* (1.125+ ex.y)));
    colors *= 0.5+0.5*smoothstep(-0.99,-0.98,cos(d*32.*ex.y*radians(180.)));
	fragColor = vec4(colors,1);
}

