    vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float circles = 0.0;
    float lines = 0.0;
    float mask = S((diameter + radius) - length(uv));

    for (int i=0; i<items; i++) {
        float phase = fraction * float(i);
        vec2 coord = uv * rotate2d(phase);

        float angle = (iTime * speed) + phase;
		vec2 pos = vec2(cos(angle), 0.0) * diameter;
        circles += circle(coord, pos, radius);
        lines += S(abs(coord.y));
    }
    fragColor = vec4(circles + 0.5*lines + mask);
}
