// Analytic projection of a sphere to screen pixels. 

// Spheres in world space become ellipses when projected to the camera view plane. In fact, these
// ellipses can be analytically determined from the camera parameters and the sphere geometry,
// such that their exact position, orientation and surface area can be compunted. This means that,
// given a sphere and a camera and buffer resolution, there is an analytical formula that 
// provides the amount of pixels covered by a sphere in the image. This can be very useful for
// implementing LOD for objects based on their size in screen (think of trees, vegetation, characters
// or any other such complex object).

// This shaders implements this formula, and provides too the center and axes of the ellipse

// More info, here: http://www.iquilezles.org/www/articles/sphereproj/sphereproj.htm

// ---------------------------------------------------------------------------------------------

struct ProjectionResult
{
    vec2  center;
    float axisMinor;
    float axisMajor;	
	float a, b, c, d, e, f;
};

ProjectionResult projectSphere( /* sphere        */ in vec4 sph, 
				                /* camera matrix */ in mat4 cam,
							    /* projection    */ in float fle )
{
    // transform to camera space	
	vec3  o = (cam*vec4(sph.xyz,1.0)).xyz;

    float r2 = sph.w*sph.w;
	float z2 = o.z*o.z;	
	float l2 = dot(o,o);


    float l = length(o.xy);

	// axis
	float axa = fle*sqrt(-r2*(r2-l2)/((l2-z2)*(r2-z2)*(r2-z2)))*l;
	float axb = fle*sqrt(-r2*(r2-l2)/((l2-z2)*(r2-z2)*(r2-l2)))*l;


	// center
	vec2  cen = fle*o.z*o.xy/(z2-r2);

	return ProjectionResult(
							 cen, min(axa, axb), max(axa, axb), 
                     /* implicit ellipse f(x,y) = aÂ·xÂ² + bÂ·yÂ² + cÂ·xÂ·y + dÂ·x + eÂ·y + f = 0 */
                     /* a */ r2 - o.y*o.y - z2,
                     /* b */ r2 - o.x*o.x - z2,
                     /* c */ 2.0*o.x*o.y,
                     /* d */ 2.0*o.x*o.z*fle,
                     /* e */ 2.0*o.y*o.z*fle,
                     /* f */ (r2-l2+z2)*fle*fle );
}


vec2 toNormalizedCoords(vec2 pos){
	return vec2(-iResolution.xy + 2.0*pos) / iResolution.y;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = toNormalizedCoords(fragCoord);

	float fov = 1.0;

	float an = 12.0 + 0.5*iTime + 10.0*iMouse.x/iResolution.x;
	vec3 ro = vec3( 3.0*cos(an), 0.0, 3.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
	vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	vec3 rd = normalize( p.x*uu + p.y*vv + fov*ww );
	mat4 cam = mat4( uu.x, uu.y, uu.z, 0.0,
					 vv.x, vv.y, vv.z, 0.0,
					 ww.x, ww.y, ww.z, 0.0,
					 -dot(uu,ro), -dot(vv,ro), -dot(ww,ro), 1.0 );

	vec4 sph1 = vec4(-2.0, 1.0,0.0,1.1);
	vec4 sph2 = vec4( 3.0, 1.5,1.0,1.2);
	vec4 sph3 = vec4( 1.0,-1.0,1.0,1.3);


	vec3 col = vec3(0.0);

    //-------------------------------------------------------

	ProjectionResult res = projectSphere( sph3, cam, fov );

	//-------------------------------------------------------

    float cellX = floor(fragCoord.x / 32.) * 32.;
    float cellY = floor(fragCoord.y / 32.) * 32.;

    vec2 cellCenter = toNormalizedCoords( vec2(cellX,cellY) + vec2(16));
    float cellBoundingRadius = sqrt(8. / iResolution.y);
    float cellToSphereCenterDist = length(res.center - cellCenter);


	// Draw conservative inside region

    if(cellToSphereCenterDist < cellBoundingRadius+res.axisMajor){
        col += vec3(0.7,0,0);   
    }

	// Draw trivial accept region
    if(cellToSphereCenterDist < 8./iResolution.x + res.axisMinor){
        col = mix(col, vec3(0.7,1.7,1.0), 0.5);   
    }

    // Draw inner circle
    if(abs(length(p - res.center) - res.axisMinor) < 0.005){
     	col = vec3(1.3,1.0,1.3);   
    }

    // Draw outer circle
    if(abs(length(p - res.center) - res.axisMajor) < 0.005){
     	col = vec3(1.,0.3,0.3);   
    }

    float v = res.a * p.x * p.x + res.b * p.y * p.y + res.c * p.x * p.y + res.d * p.x + res.e * p.y + res.f;
	if(v>0.){
		col = mix(col, vec3(0.6,0.6,1.7), 0.5);
	}

    // Draw grid
    if( (abs(fragCoord.x - cellX) < 1.) ||
        (abs(fragCoord.y - cellY) < 1.)){
        col = mix(col, vec3(0.6), 0.5);

    }

    fragColor = vec4( col, 1.0 );
}

vec2 random2( vec2 p ) {
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*(43758.5453 + iTime));
}

float random( vec2 p ) {
	return fract(sin(dot(p,vec2(269.5,183.3)))*(43758.5453 + iTime / 5.0));
}

mat2 rotate2d(float _angle){
	return mat2(cos(_angle),-sin(_angle),
				sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 pos = vec2(0.5, 0.5) - uv;
    pos.x *= iResolution.x / iResolution.y;
	float r = length(pos) * 16. - cos(iTime) / 2.0;
	float a = atan(pos.y,pos.x);
	float currentLine = floor(r);
	vec2 sphericalPos = vec2(cos(a), sin(a)) / 2.0;
	sphericalPos = sphericalPos * rotate2d(random(vec2(currentLine,0)) * sin(iTime) * 2.);
	float value = step(1., mod(currentLine, 2.0));
	float color = step(0.15, abs(random(vec2(floor((sphericalPos.x + sphericalPos.y) * 36. * currentLine), currentLine))));
	vec4 col = vec4(color, color, color, 1) / 2.0;
	vec4 modifier = vec4(abs(cos(iTime)), uv * rotate2d(random(vec2(currentLine,0))), 1);
	col += modifier;
	if(currentLine < 2.) col = modifier;
	fragColor = col;
}

