// Derived from Simple_animated_Truchet by @Shane
// [url]https://www.shadertoy.com/view/llfyWX[/url]

// Mouse control direction


#define PI     3.14159265359
#define TWO_PI (PI*2.)

// vec2 to float hash function taken from @Shane - Based on IQ's original.

float hash21(vec2 p){ return fract(sin(dot(p, vec2(141.213, 289.867)))*43758.5453); }

// Standard 2D rotation formula.

mat2 rot(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

// polynomial smooth min

float smin( float a, float b, float k )
{
    float h = clamp( 0.5 + 0.5 * (b - a) / k, 0.0, 1.0 );
    return mix( b, a, h ) - k * h * (1.0 - h);
}

//distance field circle

float df_circle(vec2 pos, float rad){

    return (length(pos)-rad);  
}





void mainImage( out vec4 c, in vec2 uv )
{

    // I   //  Screen coordinates is formated to go from -1. to 1.

	vec2 p = -1.+ 2.* uv / iResolution.xy;
    p.x *= iResolution.x / iResolution.y;



    // II  //  Moove & Rescale screen space

    p *= 1. + dot(p, p)*.05;        // fish eye
    p += vec2(.0, iTime/12.);       // moove along Y axis
    p *= .7*floor(iResolution.x/200.); // rescale space


 	// III //  Make truchet lines
           //  store tile id & Divide screen into grid 
    	   //  & store direction in a checkerboard way.

    vec2 id = floor(p);

           // replacing Shane's float dir = sign(mod(id.x + id.y, 2.) - .5);
           // The sign() seems to be dispensable

    float dir = mod(id.x + id.y, 2.)-.5;  
    p = fract(p) - .5;  // or // p -= id + .5; 

           //  Truchet tiles orientation randomisation

	p.y *= hash21(id) > .5 ? 1. : -1.;

           //  Applying symetry on diagonal axis to avoid drawing two arcs
           //  Tricks form @shane

    p *= sign(p.x + p.y);

           //  Drawing the two arc's strokes directly with a tickness of t
           //  The tricks to draw a contour was taken from a @FabriceNeyret2 
           //  comment in this shader https://www.shadertoy.com/view/MtXyWX

    p              -= .5; 
    float t         = .12; 
    float t2        = .05;    
    float line      = abs(length(p ) - .5) - t *1.5 ;
    float line_str  = abs(length(p ) - .5) - t2 * .3;
    float line_out  = max(line, -line -.01);
    float line_glow = abs(length(p ) - .5) - t2 * 2.;

           //  Adjusting the arcs clarity (I don't know how to call that)

    line     = 1. - smoothstep(.01,.1, sqrt(line));
    line_str = 1. - smoothstep(.01,.3, sqrt(line_str));
    line_glow = 1. - smoothstep(.01,.5, sqrt(line_glow));

    // IV  //  RENDER CHAIN

            //  Animate flow

    vec2 pos = p ;

    if(iMouse.z > 0.){
    	float m = smoothstep(.0,-1. + 2. * iResolution.x, -1. + 2. * iMouse.x);
        m*= 20.;
    	pos *= rot(m*dir);
    }else{
        pos *= rot(iTime*dir);
    }

            //  Part into cells  & convert into polar coordinates

    const float num = 16.;                       // partitions number
    float ang = atan(pos.y, pos.x);              // Pixel angle.               
    float ctr = floor(ang / TWO_PI * num ) + .5; // the cell center.

    pos  = rot(ctr  *(PI*2.) / num) *pos;        // Converting to polar coordinates

    pos.x -= .5;                                 // p.x = radius, p.y = angle.
                                                 // Translate coordinates

            //  render the objects in each slices


    float ring2 = smoothstep(0.0, .01, df_circle(pos, .165)); 



    ///////////////////////////////////////////////////////////////////////////////



    // V  //   Coloring

    float tex = .7 * sin(2. * cos(3. * uv.x + uv.y) - hash21(uv));     



    c = vec4(.0);

    c = .1*vec4(tex-.5);                                              // small amout off texture

    c += vec4(line -smoothstep(.0,.08,line_out*cos(sin(ang*45.))));   // base shape 

    c += mix(vec4(.2, .9, .8, 1.),c, .2);                             //base color
    c += mix(vec4(.2, .9, .8, 1.),c, .2); 

    c += .45*vec4(cos(sin(ang)));                                     // base lighting based on angle  
    c *= .45*vec4(cos(sin(ang))); //

    c *= mix(c,vec4(cos(sin(ang*5.)),cos(sin(ang*5.)),1.,1.),.5);     // more color based angle

    c += line * vec4(cos(sin(ang*2.)), .0, 0., 0.);                   // angle*2 is red
    c *= 1.-ring2+(line_str*cos(sin(ang*10.)));
    c *= .5 + vec4(.2,.9,.7,.0);                                      // color balance

    c+= (smoothstep(-.1,.7,tex*line_out));                            // backgground texture
    c -= .4*mix(c,vec4(-.1*cos(sin(ang*3.)),.1,-.1*cos(sin(ang*3.)),1.),.5); 
     vec2 uu  = 1. - 2. * uv / iResolution.xy;                        // vigneting
    float v  = 1. - smoothstep(.7, 1.4, length(uu*uu*uu)) * 2.;
    c *= vec4(v);   
    c = mix(c,vec4(.0,.0,.1,1.),.3);
    c += .4*vec4(smoothstep(.0,.9,line_glow*ring2));                  // a bit of glow
    c /= .7/pow(c,c);                                                 // lighten more

}

#define saturate(x) clamp(x, 0.0, 1.0)
#define PI 3.141592654
#define INV_PI 0.3183098862
#define DielectricSpec vec4(0.22, 0.22, 0.22, 0.779)
#define CellSize (1.0 / 3.0)
#define LightColor vec3(1.0, 1.0, 1.0)
// Border in pixels
#define Border 1.0
#define GridLayout 0
#define Ambient vec3(.2)

float Pow5(float x) {
    return x*x*x*x*x;
}
vec3 DiffuseAndSpecularFromMetallic (vec3 albedo, float metallic, out vec3 specColor, out float oneMinusReflectivity) {
    specColor = mix (DielectricSpec.rgb, albedo, metallic);
    float oneMinusDielectricSpec = DielectricSpec.a;
    oneMinusReflectivity = oneMinusDielectricSpec - metallic * oneMinusDielectricSpec;
    return albedo * oneMinusReflectivity;
}

float SmithJointGGXVisibility(float NdotL, float NdotV, float roughness) {
    // Approximation (simplify sqrt)
    float a = roughness;
    float lambdaV = NdotL * (NdotV * (1.0 - a) + a);
    float lambdaL = NdotV * (NdotL * (1.0 - a) + a);

    return 0.5 / (lambdaV + lambdaL + 1e-5);
}
float GGX(float NdotH, float roughness) {
    float a2 = roughness * roughness;
    float d = (NdotH * a2 - NdotH) * NdotH + 1.0;
    return INV_PI * a2 / (d * d + 1e-7);
}
vec3 Fresnel(vec3 F0, float cosA) {
    float t = Pow5(1.0 - cosA); // ala Schlick interpoliation
    return F0 + (1.0 - F0) * t;
}
vec3 FresnelLerp(vec3 F0, vec3 F90, float cosA) {
    float t = Pow5 (1.0 - cosA); // ala Schlick interpoliation
    return mix(F0, F90, t);
}

float DisneyDiffuse(float NdotV, float NdotL, float LdotH, float perceptualRoughness){
    float fd90 = 0.5 + 2.0 * LdotH * LdotH * perceptualRoughness;
    // Two schlick fresnel term
    float lightScatter   = (1.0 + (fd90 - 1.0) * Pow5(1.0 - NdotL));
    float viewScatter    = (1.0 + (fd90 - 1.0) * Pow5(1.0 - NdotV));

    return lightScatter * viewScatter;
}

vec3 Background(vec3 rd, vec3 light){
    vec3 col = vec3(0.0);

    float y = max(rd.y,0.0);
    col = vec3(pow(1.0-y,2.0), 1.0-y, 0.6+(1.0-y)*0.4);

    if (y < 0.1)
    	col = mix(col, vec3(0.5), clamp(-(y - .1) * 10.0, 0.0, 1.0));

    float sun = dot(rd, light);
    if (sun > 0.0)
        col += vec3(pow(sun, 5.0));

    return col;
}

vec3 BRDF(vec3 albedo, float metallic, float  smoothness, vec3 normal, vec3 view, vec3 light){
    float oneMinusReflectivity;
    vec3 specular;
    vec3 diffuse = DiffuseAndSpecularFromMetallic(albedo, metallic, specular, oneMinusReflectivity);

    vec3 halfdir = normalize(view + light);
    float nv = abs(dot(normal, view));

    float nl = saturate(dot(normal, light));
    float nh = saturate(dot(normal, halfdir));
    float lv = saturate(dot(light, view));
    float lh = saturate(dot(light, halfdir));

    float diff = DisneyDiffuse(nv, nl, lh, 1.0 - smoothness) * nl;
    float roughness = (1.0 - smoothness) * (1.0 - smoothness);

    roughness = max(.002, roughness);
    float V = SmithJointGGXVisibility(nl, nv, roughness);
    float D = GGX(nh, roughness);

    float spec = V * D * PI;
    spec = max(0.0, spec * nl);

    float surfaceReduction = 1.0 / (roughness * roughness + 1.0);
    vec3 grazing = vec3(saturate(smoothness + oneMinusReflectivity));

    // vec3 reflected = Background(reflect(view,normal), light); TODO

    return 
        diffuse * (Ambient + diff * LightColor)
        + spec * specular * LightColor * Fresnel(specular, lh);
        + surfaceReduction * specular * FresnelLerp(specular, grazing, nv);
}

vec2 RaySphere(in vec3 ro, in vec3 rd, in float rad) {
	float b = 2.0 * dot(ro, rd);
    float c = dot(ro,ro) - rad*rad;
    float det = b*b - 4.0 * c;
    if (det > 0.0){
        det = sqrt(det);
        vec2 i = vec2(0.5 * (-b - det), 0.5 * (-b + det));
    	if (i.x > i.y) return i.yx;
        return i.xy;
    }
    return vec2(-1.0, -1.0);
}
vec3 trace(vec3 ro, vec3 rd, float radius, vec3 albedo, float metallic, float smoothness){
    vec3 light = normalize(
        vec3(
            sin(iTime),
            cos(iTime),
            -(1.0-sin(iTime * 1.2 + .5) * cos(iTime * .5)*.5+.5)));

    vec2 intersect = RaySphere(ro, rd, radius);

    if (intersect.x > 0.0 && intersect.y > 0.0){
        vec3 normal = normalize(ro + rd * intersect.x);

        return BRDF(albedo, metallic, smoothness, normal, rd, light);
    }

    return Background(rd, light);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 coord = fragCoord;
    vec2 size;
    float metallic, smoothness;

    vec3 albedo = vec3(
        sin(iTime),
        cos(iTime* 1.1),
        sin(iTime * .8 - .5)) * .5 + .5;

    #if GridLayout
    size = iResolution.xy * CellSize;
    vec2 cell = floor(coord / size);

    metallic = cell.x * CellSize;
    smoothness = 1.0 - cell.y * CellSize

    coord = mod(coord, size);


    if (coord.x < Border || coord.x > size.x - Border || coord.y < Border || coord.y > size.x - Border){
        fragColor = vec4(albedo, 1.0);
        return;
    }
    #else
    size = iResolution.xy;
    metallic = 1.0 - iMouse.y / iResolution.y;
    smoothness = iMouse.x / iResolution.x;
    #endif

	vec3 rd = vec3(coord / size, 0.0);
    rd = rd * 2.0 - 1.0;
    rd.y *= size.y / size.x;
    rd = normalize(vec3(rd.xy, 1.0));

    vec3 color = trace(vec3(0.0, 0.0, -2.5), rd, 1.0, albedo, metallic, smoothness);

    fragColor = vec4(color, 1.0);
}

// ============
const float R0 = 600e3;
const float Ra = 670e3;
const int steps = 2;
const int stepss = 2;
const float g = .76;
const float g2 = g * g;
const float Hr = 15e3;
const float Hm = 2.6e3;
const vec3 I = vec3(10.0);

#define t iTime

const vec3 C = vec3(0., -R0, 0.);
const vec3 bM = vec3(31e-6);
const vec3 bR = vec3(5.8e-6, 13.5e-6, 33.1e-6);
vec3 Ds = normalize(vec3(0., .09, -1.));


void densities(in vec3 pos, out float rayleigh, out float mie) {
	float h = length(pos - C) - R0;
	rayleigh =  exp(-h/Hr);// + exp(-max(0.0, (h - 35e3)) /  5e3) * exp(-max(0.0, (35e3 - h)) / 15e3) * 0.2;
	mie = exp(-h/Hm);
}

float escape(in vec3 p, in vec3 d, in float R) {
	vec3 v = p - C;
	float b = dot(v, d);
	float c = dot(v, v) - R*R;
	float det2 = b * b - c;
	if (det2 < 0.) return -1.;
	float det = sqrt(det2);
	float t1 = -b - det, t2 = -b + det;
	return (t1 >= 0.) ? t1 : t2;
}

// this can be explained: http://www.scratchapixel.com/lessons/3d-advanced-lessons/simulating-the-colors-of-the-sky/atmospheric-scattering/
vec3 scatter(vec3 o, vec3 d) {
	float L = escape(o, d, Ra);	
	float mu = dot(d, Ds);
	float opmu2 = 1. + mu*mu;
	float phaseR = .0596831 * opmu2;
	float phaseM = .1193662 * (1. - g2) * opmu2 / ((2. + g2) * pow(1. + g2 - 2.*g*mu, 1.5));

	float depthR = 0., depthM = 0.;
	vec3 R = vec3(0.), M = vec3(0.);

    float u0 = -(L - 100.0) / (1.0 - exp2(float(steps)));

	for (int i = 0; i < steps; ++i) {
        float dl = u0 * exp2(float(i));
		float l = - u0 * (1.0 - exp2(float(i + 1)));
		vec3 p = o + d * l;

		float dR, dM;
		densities(p, dR, dM);
		dR *= dl; dM *= dl;
		depthR += dR;
		depthM += dM;

		float Ls = escape(p, Ds, Ra);
		if (Ls > 0.) {
			float dls = Ls / float(stepss);
			float depthRs = 0., depthMs = 0.;
			for (int j = 0; j < stepss; ++j) {
				float ls = float(j) * dls;
				vec3 ps = p + Ds * ls;
				float dRs, dMs;
				densities(ps, dRs, dMs);
				depthRs += dRs;
				depthMs += dMs;
			}
            depthRs *= dls;
            depthMs *= dls;

			vec3 A = exp(-(bR * (depthRs + depthR) + bM * (depthMs + depthM)));
			R += A * dR;
			M += A * dM;
		} else {
			return vec3(0.);
		}
	}

	return I * (R * bR * phaseR + M * bM * phaseM);
}

void ACEStonemap(inout vec3 color) {
	const float a = 2.51f;
	const float b = 0.03f;
	const float c = 2.43f;
	const float d = 0.59f;
	const float e = 0.14f;
	color = (color*(a*color+b))/(color*(c*color+d)+e);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	Ds = normalize(vec3(iMouse.x / iResolution.x - .5, sin(t * 0.5), cos(t * 0.5)));

	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
	uv.x *= iResolution.x / iResolution.y;

	vec3 O = vec3(0., 0., 0.);
	vec3 D = normalize(vec3(uv, -2.));

	float att = 1.;
    if (D.y < 0.0) {
        D.y = 0.0016 / (-D.y + 0.04) - 0.04;
    }

    vec3 color = att * scatter(O, D);
	ACEStonemap(color);
	fragColor = vec4(pow(color, vec3(.454545)), 1.);
}

const int NUM_BUCKETS = 32;
const int ITER_PER_BUCKET = 1024;
const float HIST_SCALE = 8.0;

const float NUM_BUCKETS_F = float(NUM_BUCKETS);
const float ITER_PER_BUCKET_F = float(ITER_PER_BUCKET);


//note: uniformly distributed, normalized rand, [0;1[
float nrand( vec2 n )
{
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}
//note: remaps v to [0;1] in interval [a;b]
float remap( float a, float b, float v )
{
	return clamp( (v-a) / (b-a), 0.0, 1.0 );
}
//note: quantizes in l levels
float truncate( float a, float l )
{
	return floor(a*l)/l;
}

// each dim is defective when zero
uint hash(uint x, uint y)
{
    #define W0 0x3504f333u   // 3*2309*128413 
	#define W1 0xf1bbcdcbu   // 7*349*1660097 
	#define M  741103597u    // 13*83*686843

  	x *= W0;   // x' = Fx(x)
  	y *= W1;   // y' = Fy(y)
  	x ^= y;    // combine
    x *= M;    // MLCG constant
  	return x;
}
float hash_st( vec2 uv )
{
    uvec2 p = uvec2( uv * iResolution.xy );
  	uint h = hash( p.x, p.y );

  	uint h_lo = (h & 0x0000ffffu);
  	uint h_hi = (h & 0xffff0000u)>>16;

    float f0 = float(h_lo) / 65536.0;
    float f1 = float(h_hi) / 65536.0;

    return f0+f1-1.0;
}
float n2rand_split( vec2 n )
{
	float t = fract( iTime );
    return hash_st( n + 83.0*t)*0.5+0.5;
}


