#define M_PI 3.141592

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float turns = 4.0 ;
    float speed = -0.333;
    bool invert = false;


	vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;

    float angle = atan(uv.y, uv.x) / (2.*M_PI);
    angle = fract(angle + (iTime * speed));


    float dist = length(uv);
    float waves = fract( dist  * turns) ;

    float spiral = (abs(angle - waves)  - 0.5) * 2.0;
    spiral = (spiral*spiral * spiral * spiral);

    if(!invert)
        spiral = 1.0 - spiral;

    spiral *= clamp(dist * (turns / 2.0), 0.0,1.0);
	fragColor = vec4(spiral);
}


