// Gaussian primes: http://mathworld.wolfram.com/GaussianPrime.html
//
// Regular primes: https://www.shadertoy.com/view/4slGRH

bool isPrime( int x )
{
	if( x==1 ) return false;
	if( x==2 ) return true;
	if( x==3 ) return true;
	if( x==5 ) return true;
	if( x==7 ) return true;
	if( (x&1)==0 ) return false;
	if( (x%3)==0 ) return false;
	if( (x%5)==0 ) return false;

    int xm = 1 + int(sqrt(float(x)));

	int y = 7;
	for( int i=0; i<100; i++ ) // count up to 3000 (100*30). See  http://www.iquilezles.org/blog/?p=1558
	{
		         if( (x%y)==0 ) return false;
		y += 4;  if( y>=xm    ) return true;
		         if( (x%y)==0 ) return false;
		y += 2;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 4;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 2;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 4;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 6;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 2;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 6;  if( y>=xm )    return true;
	}

	return true;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float s = exp2( -(2.0 + 2.0*cos(6.2831*iTime/20.0)) );
    ivec2 p = ivec2( floor((fragCoord-iResolution.xy*0.5)*s) );

    p = abs(p);


    bool isPr = false;

    if( p.x==0 ) 
    {
        isPr = isPrime(p.y) && ((p.y&3)==3);
    }
    else if( p.y==0 ) 
    {
        isPr = isPrime(p.x) && ((p.x&3)==3);
    }
    else
    {
        isPr = isPrime(p.x*p.x+p.y*p.y);
    }

    if( p.x==1 && p.y==1 ) isPr = true;

    fragColor = vec4( isPr?vec3(0.5,1.0,0.0):vec3(0.0), 1.0 );
}

const float radius = .1, EPS = .001;

float getNLP(float x){

    return (fract(x)<.1)? floor(x) : ceil(x);
}

vec3 getNLP(vec3 p){

    return vec3(getNLP(p.x), getNLP(p.y), getNLP(p.z));
}

float sphereDist(vec3 p){

    return length(p - getNLP(p)) - radius;
}

float distFunc(vec3 p){

    return sphereDist(p);
}

vec3 normal(vec3 p){

    return normalize(vec3(
        distFunc(p + vec3(EPS, 0., 0.)) - distFunc(p),
        distFunc(p + vec3(0., EPS, 0.)) - distFunc(p),
        distFunc(p + vec3(0., 0., EPS)) - distFunc(p)
    ));
}

vec3 trace(vec3 origin, vec3 ray){

    ray = normalize(ray);

    vec3 p = origin;

    for(int i = 0; i < 32; ++i)
         p += distFunc(p) * ray;

    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime/10.+10.;

    vec3 origin = vec3(t, 0., .1);
    vec3 ray = vec3(uv, 5.);
    vec3 light = normalize(vec3(-1., -1., 1.));

    vec3 p = trace(origin, ray);

    if(distFunc(p) < EPS){
        float d = dot(normal(p), -light);
        fragColor = vec4(1., vec3(d));
    }
    else
        fragColor =  vec4(0., 0., 0., 1.);
}

float f( float x, float y ){
	return sin(x * 0.5) + sin(y * sin(-iTime*0.1) * 0.5)+ 1.5+0.5 *sin(x*0.01)*6.0;
}


vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float distance, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float b = 23.6;
    float fogAmount = 1.0 - exp( -distance*b );
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  =  vec3(1.0,0.9,0.7);
    return mix( rgb, fogColor, fogAmount );
}


vec3 castRay( const vec2 uv, const vec3 ro, const vec3 rd )
{	

    const float delt = 0.01f;
    const float mint = 0.001f;
    const float maxt = 20.0f;
    for( float t = mint; t < maxt; t += delt )
    {
        vec3 p = ro + rd*t;
        if( p.y <  f( p.x, p.z ) )
        {
            vec3 rgb = texture(iChannel0, p.xz * 0.5 + vec2(sin(p.x*14.0), cos(p.x * 14.435)+ iTime*1.0)).xyz * (0.4+ p.y * 0.5) ;
			rgb = applyFog( rgb, delt, rd, vec3(0,0,-1.0));
			 return rgb;
        }
    }
    return vec3(0.3 + uv.x * 0.5, 0.6, 0.9) ;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 fp = 0.5-(uv-0.5);
	vec4 m = iMouse / iResolution.xxxx;

	vec3 o = vec3(3.0 + 4.0 * sin(iTime*0.4), 5.0,-20.0);

	vec3 ct = vec3(3.0 + m.x * 100.0, 0.0, 30.0 +  m.x*300.0);
    vec3 wup = vec3(0.0, 1.0, 0.0);
	vec3 cr = normalize(cross(wup, o));
    vec3 cd = normalize(ct - o);
	vec3 cu = cross(cd, cr);
    vec3 rayDir = normalize(cr * fp.x + cu * fp.y + cd);


	vec3 rc = castRay(uv, o,rayDir);

	fragColor = vec4(rc, 1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( 2.0*fragCoord.xy-iResolution.xy )/iResolution.y;
    float MAX_ITERATION = 25.0;
    float mx = iMouse.x/iResolution.x*2.0 - 0.5;
    float my = iMouse.y/iResolution.y*2.0 - 0.5;
    float c_r = mx*sin(iTime*0.5);    
    float c_i = my*cos(iTime*0.5);
    float z_r = uv.x;
    float z_i = uv.y;

    float l;
    float i;
    while ( l < 4.0 && i < MAX_ITERATION )
    { 
        float tmp = z_r;
        z_r = pow( z_r, 2.0 ) - pow( z_i, 2.0 ) + c_r;
        z_i = 2.0*z_i*tmp + c_i;

        l = pow(z_r, 2.0)+pow( z_i, 2.0 );
        i++;
    }

    if ( i < MAX_ITERATION )
    {        
    	fragColor = vec4( i / MAX_ITERATION, 0.0, 0.0, 0.0 ); 
    }
    else
    {
        fragColor = vec4( l*0.1, l*0.2, l, 0.0 ); 
    }
}

const float param1 = 128.f;
const float param2 = 16.f;
const float ln = 1.f;
const float d_u = 0.f;
const int mode = 0;
const int color = 0;
const int hole = 1;

const float PI = radians(180.f);
const float TAU = radians(360.f);

#define div(a, b) int((a) / (b))
#define sqr(a) ((a) * (a))
#define cub(a) (sqr(a) * (a))

#define map3(f, v) vec3(f((v).x), f((v).y), f((v).z))

int   idiv (int a, int b) { return a/b + (a%b>0?1:0); }
int   imod (int a, int b) { return a>=0 ? a%b : b-1+(a+1)%b; }
float add2 (vec2 a) { return a.x + a.y; }
float sub2 (vec2 a) { return a.x - a.y; }
bool  bxor2(bvec2 a) { return a.x ^^ a.y; }
vec2  flip2(vec2 a) { return vec2(a.y, a.x); }

vec2 cx_conj(vec2 a) { return vec2(a.x, -a.y); }
vec2 cx_inv (vec2 a) { return cx_conj(a) / add2(a*a); }
vec2 cx_mul (vec2 a, vec2 b) { return vec2(sub2(a*b), add2(a*flip2(b))); }
vec2 cx_div (vec2 a, vec2 b) { return cx_mul(a, cx_conj(b)); }

vec3 hsv2rgb(float h, float s, float v) {
    h = fract(h) * 6.f;
    float c = v * s;
    float x = c * (1.f - abs(mod(h, 2.f) - 1.f));
    float m = v - c;

    #define ret(a, b, c)\
        return vec3(a + m, b + m, c + m)

    switch (int(h)) {
        case 0: ret(c, x, 0.f);
        case 1: ret(x, c, 0.f);                                                                                                          
        case 2: ret(0.f, c, x);                                                                                                          
        case 3: ret(0.f, x, c);                                                                                                          
        case 4: ret(x, 0.f, c);                                                                                                          
        case 5: ret(c, 0.f, x);                                                                                                          
    }                                                                                                                                  

    #undef ret                                                                                                                         
}                                                                                                                                      

vec3 lab2xyz(float l, float a, float b) {                                                                                              
    const vec3 w = vec3(0.95047f, 1.f, 1.08883f);                                                                                          
    vec3 c = vec3((l + 16.f) / 116.f)                                                                                                      
           + vec3(a / 500.f, 0.f, -b / 200.f);                                                                                               
    #define f(t)\
        t > 6.f / 29.f\
            ? cub(t)\
            : (116.f * t - 16.f) * 27.f / 24389.f
    return w * map3(f, c);                                                                                                             
    #undef f                                                                                                                           
}                                                                                                                                      

vec3 lab2rgb(float l, float a, float b) {
    const mat3 m = mat3(
         3.2404542f, -1.5371385f, -0.4985314f,
        -0.9692660f,  1.8760108f,  0.0415560f,
         0.0556434f, -0.2040259f,  1.0572252f
    );
    #define f(t)\
        t > 0.00304f\
            ? 1.055f * pow(t, 1.f / 2.4f) - 0.055f\
            : 12.92f * t
    vec3 c = lab2xyz(l, a, b) * m;
    return map3(f, c);
    #undef f
}

vec3 labhsv(float h, float s, float v) {
    h *= TAU;
    s *= 100.f;
    v *= 100.f;
    return lab2rgb(v, s * sin(h), s * cos(h));
}

float foo1(float a, float b)
{
    return sqr(a)+sqr(b);
}

float foo(float a, float b)
{
    float c = log(sqr(a) + sqr(b));
    if (hole == 0)
        c = abs(c);
    return log(c);
}

float mandel(vec2 c, int n)
{
    float res_r = 0.0f;
    float res_i = 0.0f;
    float res_r_tmp;
    int i = 0;
    while (foo1(res_r, res_i) < param2 && (i < n))
    {
        res_r_tmp = res_r;
        res_r = sqr(res_r) - sqr(res_i) + c.x;
        res_i = 2.0f * res_r_tmp * res_i + c.y;
        ++i;
    }
    if (ln == 1.f)
        return foo(res_r, res_i);
    else
        return float(i+1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pp = fragCoord.xy / iResolution.xy;
    pp.x = pp.x * 4.f - 8.f/3.f;
    pp.y = pp.y * 8.f/3.f - 4.f/3.f;
    float d = d_u == 0.f ? log(abs(sin(iTime)))/10000000.f : d_u;
    float m = mandel(pp, int(param1)) / param1;
    float mx = mandel(pp + vec2(d, 0.f), int(param1)) / param1;
    float my = mandel(pp + vec2(0.f, d), int(param1)) / param1;
    float grad = atan((m - mx) / d, (m - my) / d);
    if (color == 1)
    {
        m *= param1;
        mx *= param1;
        my *= param1;
    }
    vec3 c;
    if (mode == 0)
        c = labhsv(m, abs(m-mx)/d, abs(m-my)/d);
    else if (mode == 1)
        c = lab2rgb(m, 100.f*(m-mx)/d, 100.f*(m-my)/d);
    else
        c = labhsv(m, 0.8f, abs(grad)/PI);
    fragColor = vec4(c, 1);
}

