void mainImage(out vec4 O, vec2 U) {
    vec3 p = vec3( iTime, 3, 0 ) *9.,
         r = iResolution,
         d = vec3( ( U - .5*r.xy ) / r.y, 1 );
    float t = .2;
    for( d.yz *= mat2(4,-3,3,4)*t ; t>.1; t = min( p.y - 8.*t*t , .2 ) )
