float octaveNoise (vec2 st, int octaves, float persistence, vec2 frequency, float amplitude, vec2 speed) {
    float total = 0.0;
    vec2 freq = frequency;
    float amp = amplitude;

    for(int i=0;i<octaves;i++) {
        total += noise(vec2(st.x * freq.x-speed.x*iTime, st.y * freq.y+speed.y*iTime)) * amp;
        amp *= persistence;
        freq.x *= 2.4;
        freq.y *= 2.5;
    }

    return total;
}


const Sphere sphereMoon = Sphere(MOON_RADIUS, MOON_POSITION,
                             Material( vec3(0.05, 0.05, 0.05),
                                vec3(1.0, 1.0, 1.0),
                                0.1*vec3(1.0, 1.0, 1.0),
                                0.1));


const Sphere sphereOcean = Sphere(EARTH_RADIUS, vec3(0.0, -EARTH_RADIUS-2.5,0.0),
                             Material( vec3(0.07, 0.45, 0.4),
                                  vec3(0.07, 0.45, 0.4),
                                  vec3(1.0, 1.0, 1.0),
                                  0.6));

const Sphere sphereLand = Sphere(EARTH_RADIUS-1.22, vec3(0.0, -EARTH_RADIUS-2.5,0.0),
                             Material( vec3(0.55, 0.27, 0.1),
                                   0.75*vec3(0.55, 0.27, 0.1),
                                   0.1*vec3(1.0, 1.0, 1.0),
                                  0.05));

float moonSDF(vec3 samplePoint, Sphere sphere) {
    return length(samplePoint-sphere.center + vec3(sin(iTime)/3.0,1.2+sin(iTime)/6.0,cos(iTime)/4.0)) - sphere.radius;
}

float oceanSDF(vec3 samplePoint, Sphere sphere) {
    return length(samplePoint-sphere.center) 
        - sphere.radius -octaveNoise (samplePoint.xz, OCTAVE_OCEAN, PERSISTENCE_OCEAN, FREQUENCY_OCEAN, AMPLITUDE_OCEAN, SPEED_OCEAN);
}

float landSDF(vec3 samplePoint, Sphere sphere) {
    return length(samplePoint-sphere.center) 
        - sphere.radius -octaveNoise (samplePoint.xz, OCTAVE_LAND, PERSISTENCE_LAND, FREQUENCY_LAND, AMPLITUDE_LAND, SPEED_LAND);
}

float sceneSDF(vec3 samplePoint) {
    return min(min(oceanSDF(samplePoint, sphereOcean),landSDF(samplePoint, sphereLand)),moonSDF(samplePoint, sphereMoon));
}


float shortestDistanceToSurface(Ray ray) {
    float depth = MIN_DIST;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(ray.origin + depth * ray.direction);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= MAX_DIST) {
            return MAX_DIST;
        }
    }
    return MAX_DIST;
}

float shortestDistanceToLand(Ray ray) {
    float depth = MIN_DIST;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = landSDF(ray.origin + depth * ray.direction, sphereLand);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= MAX_DIST) {
            return MAX_DIST;
        }
    }
    return MAX_DIST;
}

vec3 calcNormal(vec3 p) {

    float ref = sceneSDF(p);
    return normalize(vec3(
        ref - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        ref - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        ref - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}


vec3 phongContribForLight(Ray ray, Intersect intersection, Light light) {
    vec3 N = intersection.normal;
    vec3 L = normalize(light.position - intersection.position);
    vec3 V = normalize(ray.origin - intersection.position);
    vec3 R = normalize(reflect(-L, intersection.normal));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        return vec3(0.0);
    } 

    float Ldist = length(light.position - intersection.position);
    float attenuation = 1.0 / (light.constant + light.linear * Ldist + 
                                   light.quadratic * (pow(Ldist,2.0)));

    vec3 diffuse = light.diffuse*intersection.material.diffuse * dotLN;

    if (shortestDistanceToLand(Ray(intersection.position + EPSILON*intersection.normal, L))!=MAX_DIST)
    {
        return 0.3*diffuse;
    }

    if (dotRV < 0.0) {
        return diffuse;
    }

    vec3 specular = light.specular*intersection.material.specular * pow(dotRV, intersection.material.shininess * 128.0);

    return (diffuse+specular)*attenuation;

}


vec3 phongIllumination(Ray ray, Intersect intersection) {

    vec3 ambient =  0.4*vec3(1.0)*intersection.material.ambient;

    vec3 color = ambient;

    Light light2 = Light( 
     MOON_POSITION+vec3(MOON_RADIUS, 15.0,-60.0),
     vec3(1.0, 1.0, 1.0),
     vec3(1.0, 1.0, 1.0),
     1.0,
     0.0000007,
     0.000000002);

    color += phongContribForLight(ray, intersection, light2);

    return color;
}

vec3 rayTracer(Ray ray)
{
    float dist = shortestDistanceToSurface(ray);

    if (dist > MAX_DIST - EPSILON) {
        return vec3(0.02,0.0,0.1);
    }

    vec3 p = ray.origin + dist * ray.direction;
    Intersect intersection = Intersect(dist, p, calcNormal(p), 
                                       Material(vec3(0.0), 
                                                vec3(0.0), 
                                                vec3(0.0),
                                                0.0));

    if (moonSDF(p, sphereMoon)<= EPSILON) intersection.material = sphereMoon.material;
    if (oceanSDF(p, sphereOcean)<=EPSILON) intersection.material = sphereOcean.material;
    if (landSDF(p, sphereLand)<=EPSILON) intersection.material = sphereLand.material;


    return phongIllumination(ray, intersection);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv    = fragCoord.xy / iResolution.xy - vec2(0.5);
         uv.x *= aspectRatio;

    vec3 eye = vec3(0.0, 0.25, -1.0);
    Ray ray = Ray(eye,normalize(vec3(uv.x, uv.y, 0.0)-eye));

    fragColor = vec4(rayTracer(ray), 1.0);
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
	U /= R;
    if (length(M)>10.) M/=R; else M = .5 + .5* cos(iTime*vec2(.5,1));
    O-=O;
    float s=0., w, l = 2.*M.y ;
    if (M.x > .5) l *= smoothstep(0.,1.,length(2.*U-1.) );  

    for (float i=0.; i<9.; i++) {
        w = pow(l,i);
        O += w * textureLod(iChannel0, U, i );
        s += w;
    }
    O = O/s;

    O += smoothstep(1./R.y, -1./R.y,  length( U - vec2(1, l/2.) ) - .02);
}

