/*
 The curtain forms seen in the aurora borealis (and australis) are presumably 
 due to wavelike fluctuations in the magnetosphere. Here the auroral effect is 
 achieved using the same technique as for ocean waves (rather than the more 
 complicated scheme in nimitz's "Auroras"). Reminds me of the view near Denali, 
 with enhanced brightness and some speedup (plus you don't have to go out in the 
 cold late at night to see it). Mousing encouraged.
*/

float Noisefv2 (vec2 p);
float Noisefv3 (vec3 p);
float Fbm1 (float p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec3 HsvToRgb (vec3 c);

float tCur;
const float pi = 3.14159;

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 t;
  float wFreq, wAmp, ht;
  wFreq = 1.;
  wAmp = 1.;
  ht = 0.;
  for (int j = 0; j < 3; j ++) {
    p *= qRot;
    t = 0.05 * tCur * vec2 (1., -1.);
    t4 = (p.xyxy + t.xxyy) * wFreq;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    ht += wAmp * dot (pow (1. - sqrt (v4.xz * v4.yw), vec2 (8.)), vec2 (1.));
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return ht;
}

vec4 AurCol (vec3 ro, vec3 rd)
{
  vec4 col, mCol;
  vec3 p, dp;
  float ar;
  dp = rd / rd.y;
  p = ro + (40. - ro.y) * dp;
  col = vec4 (0.);
  mCol = vec4 (0.);
  for (float ns = 0.; ns < 50.; ns ++) {
    p += dp;
    ar = 0.05 - clamp (0.06 * WaveHt (0.01 * p.xz), 0., 0.04);
    mCol = mix (mCol, ar * vec4 (HsvToRgb (vec3 (0.34 + 0.007 * ns, 1., 1. - 0.02 * ns)), 1.), 0.5);
    col += mCol;
  }
  return col;
}

vec3 SkyCol (vec3 rd)
{
  vec3 rds;
  rds = floor (2000. * rd);
  rds = 0.00015 * rds + 0.1 * Noisefv3 (0.0005 * rds.yzx);
  for (int j = 0; j < 19; j ++) rds = abs (rds) / dot (rds, rds) - 0.9;
  return 0.3 * vec3 (1., 1., 0.9) * min (1., 0.5e-3 * pow (min (6., length (rds)), 5.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 aCol;
  vec3 col;
  float dstWat, rFac;
  rFac = 1.;
  if (rd.y < 0.) {
    dstWat = - ro.y / rd.y;
    ro += dstWat * rd;
    rd = reflect (rd, VaryNf (3. * ro + 0.2 * tCur, vec3 (0., 1., 0.),
       0.5 * (1. - smoothstep (10., 30., dstWat))));
    rFac = 0.8;
  }
  if (rd.y < 0.04 * Fbm1 (32. * atan (rd.x, - rd.z)) + 0.01) col = vec3 (0.1, 0.1, 0.12);
  else {
    aCol = AurCol (ro, rd);
    col = rFac * ((1. - 0.5 * aCol.a) * SkyCol (rd) + 0.6 * aCol.rgb);
  }
  return pow (clamp (col, 0., 1.), vec3 (0.9));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 uv, ori, ca, sa;
  float el, az;
  uv = 2. * fragCoord.xy / iResolution.xy - 1.;
  uv.x *= iResolution.x / iResolution.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 0.8 * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
  } else {
    az += 0.5 * pi * sin (0.005 * pi * tCur);
    el += 0.05 * pi * sin (0.007 * pi * tCur);
  }
  el = clamp (el, -0.35 * pi, 0.1 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 2., -4.);
  rd = vuMat * normalize (vec3 (uv, 2.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p); 
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);  
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;  
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

/**** Constants ****/ 
#define MAXIMUM_RAY_STEPS 255
#define EPSILON 0.0001
#define MIN_DIST 0.001
#define MAX_DIST 300.0

struct PhongMaterial
{
    vec3 diffColor;
    float spec;
    bool receiveShadows;
};


/**** Eye Vector ****/
/**** uv -> [-1,1]^2, screenspace ****/
/**** fov -> R, angle in degree   ****/
/**** returns normalized direction vector through camera ****/
vec3 getEyeVector(vec2 uv, float fov)
{
    float theta = tan(radians(fov/2.0));
	return normalize(vec3(uv.x*theta, uv.y*theta, -1.0));
}

/**** View Matrix ****/
/**** https://people.freedesktop.org/~idr/glu3/glu3_8h.html ****/
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 fp = normalize(cross(f, up));
    vec3 u = cross(fp, f);
    return mat4(
        vec4(fp, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

/**** Distance field functions ****/
/*** http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm ***/
/**** Sphere SDF ****/
float sdfSphere(vec3 p, float s)
{
  return length(p)-s;
}

/**** RoundBox SDF ****/
float udRoundBox(vec3 p, vec3 b, float r)
{
  return length(max(abs(p)-b,0.0))-r;
}
/**** Smooth Interpolation ****/
/*** http://iquilezles.org/www/articles/smin/smin.htm ***/
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}
/**** Scene SDF ****/
float sdfScene(vec3 p)
{
    float s = sdfSphere(p,((sin(iTime)/2.0)+1.0) );
    float r = udRoundBox(p, vec3(.5), 0.01);
    float x = p.y+2.0;

  	float d = smin(r, s, 10.0);

    return min(d,x);
}

/**** Gradient estimated normal ****/ 
/*** https://en.wikipedia.org/wiki/Gradient ***/
vec3 gradientNormal(vec3 p) 
{
    return normalize(vec3(
        sdfScene( vec3( p.x + EPSILON, p.y, p.z )) - sdfScene( vec3( p.x - EPSILON, p.y, p.z )),
        sdfScene( vec3( p.x, p.y + EPSILON, p.z )) - sdfScene( vec3( p.x, p.y - EPSILON, p.z )),
        sdfScene( vec3( p.x, p.y, p.z + EPSILON )) - sdfScene( vec3( p.x, p.y, p.z - EPSILON ))
    ));
}

/**** Raymarching ****/
float rayMarch(vec3 o, vec3 dir)
{
    // Create array of 2 float that store [0] -> current distance value, [1] ->  last distance value
    vec2 dist = vec2(MIN_DIST,MIN_DIST);

    for(int i = 0; i < MAXIMUM_RAY_STEPS; i++)
    { 
        // Get last dist point on the direction array
        vec3 p = o + dir * dist.y;

        // Determine minimal distance from all objects in the scene
        dist.x = sdfScene(p);

        // Are we touching an object ?
        if(dist.x < EPSILON)
        {
           // Yes so return last depth
           return dist.y;
        }

        dist.y += dist.x;

        // Is there any object in the scene ?
        if(dist.y >= MAX_DIST)
        {
            return MAX_DIST;
        }
    }

    // All steps have been finished without object collision
    // Then return max distance
    return MAX_DIST;
}

/**** Determines Classic Phong lighting calculation ****/ 
/**** https://en.wikipedia.org/wiki/Phong_reflection_model ****/
vec3 phongIllumination(PhongMaterial mat, vec3 p, vec3 viewDir)
{
    /*** Ambient Light ***/
    vec3 k_a = vec3(1.0); // Ambient reflection constant
    vec3 c_a = vec3(0.08) * k_a; // Ambient intensity color

    /*** One non-directionnal light ***/
    vec3 p_1stLight = vec3(cos(iTime), 1.0, sin(iTime)) * 10.0; // 1st light position
    vec3 i_1stLight = vec3(.8, .8, .8); // 1st light intensity

    /*** Angular calculations ***/
    vec3 N = gradientNormal(p); // Calculate gradient normal
    vec3 l = p_1stLight - p; // Light direction
    vec3 L = normalize(l); // Normalized light direction
    vec3 V = normalize(-viewDir); // Vector p to cam
    vec3 R = normalize(reflect(-L, N)); // Reflect vector

    // Diffuse normal
    vec3 k_d = mat.diffColor;

    /*** Diffuse ***/
    float LN = clamp(dot(L, N), 0.0, 1.0);

    /*** Specular ***/
    float kSpec = 256.0;
    float RV = clamp(dot(R, V), 0.0, 1.0);
    float spec = mat.spec * pow(RV, kSpec);

    /*** shadows ***/
    if(mat.receiveShadows)
    {      
        vec3 ps = p + N * MIN_DIST * 200.0;
        float sDist = rayMarch(ps, normalize(p_1stLight - ps));
        if(sDist < length(ps - p_1stLight) ) k_d *= 0.2;
    }

    /*** Attenuation ***/
   	float dl = length(l);
    float c_1 = 0.1;
    float c_2 = 0.0005;
	float c_3 = 0.000007;

    float fatt = min(1.0 / (c_1 + c_2*dl + c_3*dl*dl), 1.0);

    return c_a + i_1stLight * LN * k_d + spec;
}

/**** Main Function ****/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    /*** Centering ***/
	vec2 uv = fragCoord.xy / iResolution.xy;
   	uv*=2.0;
    uv-=1.0;
    uv.x *= iResolution.x / iResolution.y;

    /*** Camera settings ***/
    vec3 viewDir = getEyeVector(uv, 45.0);
    vec3 camPos = vec3(0.68, 0.42, 0.60) * 5.0;

    /*** Camera Transformation ***/
    mat4 viewToWorld = viewMatrix(camPos, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));    
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    /*** Raymarching ***/
    float hitDist = rayMarch(camPos, worldDir);

    /*** Didn't hit anything ***/
    if ( hitDist > MAX_DIST - EPSILON ) {
        fragColor = vec4(0.0);
		return;
    }

    /*** Sphere mat ***/
    PhongMaterial sMat;
    sMat.spec = 0.8;    sMat.diffColor = vec3(0.0, 1.0, .4); sMat.receiveShadows = false;

    /*** Plane mat ***/
    PhongMaterial pMat;
    pMat.spec = .0;    pMat.diffColor = vec3(0.176, 0.6, 0.584); pMat.receiveShadows = true;


    /**** Lighting ***/
    vec3 p = camPos + worldDir * hitDist;

    vec3 color;

    PhongMaterial mat;
    if(p.y > -2.0+MIN_DIST)
    {
        mat = sMat;
    }
    else
    {
        mat = pMat;
    }
    color = phongIllumination(mat, p, worldDir);

    // vignetting
//    color *= 1.0-length(uv) * 0.15,

    fragColor = vec4(color,1.0); 
}

// Half-Space Frustum Tracing
// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// note that this version is very verbose and purposefully lacks 
// any form of optimization for the sake of education.

const vec2 min_frustum_half_size = vec2(1.77778,1.0)/30.0;
const vec2 max_frustum_half_size = vec2(1.77778,1.0)/6.0;

const float infinity = 1.0/0.0;

vec2 frustum_center;
vec2 frustum_half_size;
vec4 plane;
vec2 frustum_corners[4];
vec3 frustum_ray_dirs[4];
vec3 frustum_planes[4];
float plane_ray_hits[4]; // these are scalars along the ray direction
// range of surface
vec2 surface;
// are we inside the solid half-space?
bool inside;

// check only the horizontal frustum planes
float dFrustumH(vec3 p) {
    return max(
        dot(p, frustum_planes[0]),
        dot(p, frustum_planes[2]));    
}

// check only the vertical frustum planes
float dFrustumV(vec3 p) {
    return max(
        dot(p, frustum_planes[1]),
        dot(p, frustum_planes[3]));    
}

float dFrustum(vec3 p) {
    return max(
        dFrustumH(p),
        dFrustumV(p));
}

// versor (unit quaternion) from axis and angle
vec4 versor(vec3 axis, float angle) {
    float a = angle * 0.5;
    return vec4(axis * sin(a), cos(a));
}

// invert rotation
vec4 conjugate(vec4 q) {    
    return vec4(-q.xyz, q.w);
}

// rotate point by versor
// q (t) * V * q (t) ^-1
vec3 rotate(vec4 q, vec3 p) {
    vec3 t = cross(q.xyz,p) * 2.0;
    return p + q.w * t + cross(q.xyz, t);    
}

// assuming ray begins at origin
float iPlane(vec4 plane, vec3 ro, vec3 rd) {
    float D = dot(plane.xyz, rd);
    float t = -dot(plane, vec4(ro, 1.0)) / D;
    return t;
}

vec2 lissajous(float t, float a, float b) {
    return vec2(sin(a*t), sin(b*t));
}

void setup_globals(float t) {
    // move the frustum around
    frustum_center = lissajous(t*0.07, 2.0, 1.0)*0.1;
    // vary the frustum aperture
    frustum_half_size = mix(
        min_frustum_half_size,
		max_frustum_half_size,
        cos(t*0.13)*0.5+0.5);

    // transform our simplex a bit
    vec2 plane_offset = lissajous(t*0.2, 5.0, 4.0)*0.3;
    vec4 plane_rotation = versor(normalize(vec3(1.0)), t*0.2);
    vec3 plane_normal = rotate(plane_rotation, vec3(0.0, 0.0, 1.0));
    plane = vec4(plane_normal, -dot(plane_normal, vec3(plane_offset,3.0)));

    vec3 frustum_extents = vec3(frustum_half_size,-frustum_half_size.x);
    frustum_corners[0] = frustum_center + frustum_extents.xy;
    frustum_corners[1] = frustum_center + frustum_extents.zy;
    frustum_corners[2] = frustum_center - frustum_extents.xy;
    frustum_corners[3] = frustum_center - frustum_extents.zy;

    for (int i = 0; i < 4; ++i) {
        frustum_ray_dirs[i] = normalize(vec3(frustum_corners[i],1.0));
    }

    for (int i = 0; i < 4; ++i) {
		plane_ray_hits[i] = iPlane(plane, vec3(0.0), frustum_ray_dirs[i]);
    }

    frustum_planes[0] = normalize(cross(frustum_ray_dirs[1],frustum_ray_dirs[0]));
    frustum_planes[1] = normalize(cross(frustum_ray_dirs[2],frustum_ray_dirs[1]));
    frustum_planes[2] = normalize(cross(frustum_ray_dirs[3],frustum_ray_dirs[2]));
    frustum_planes[3] = normalize(cross(frustum_ray_dirs[0],frustum_ray_dirs[3]));

    vec2 c = frustum_center;
    vec2 s = frustum_half_size;
    float p = plane.x*c.x + plane.y*c.y + plane.z;
    float q = abs(plane.y*s.y) + abs(plane.x*s.x);
	float t0 = -plane.w / (p - q);
	float t1 = -plane.w / (p + q);

    inside = plane.w < 0.0;

    float tmin = min(t0, t1);
    float tmax = max(t0, t1);

    if (tmin < 0.0) {
        surface = vec2(tmax, 1.0/0.0);
    } else {
    	surface = vec2(tmin, tmax);
    }
}

