// isLight: true for light shade pattern, false for darker shade pattern
// uv: normalized uv coordinates
// cellSize: in pixel size (0, inf)
// screenRect: screen dimensions in pixels
// lineWidth: percentage (0 - 1.0)
// gradientWidth: percentage [0 - 1.0) w.r.t lineWidth
float twinShadeC(bool isLight, vec2 uv, float cellSize, vec2 screenRect, float lineWidth, float gradientWidth)
{
   vec2 cellDims = cellSize / iResolution.xy;    

   vec2 bckSlash = normalize(vec2(screenRect.x / screenRect.y, 1.));
   vec2 fwdSlash = normalize(vec2(-1, iResolution.y / iResolution.x));      

   vec2 bottomLeft = floor(uv / cellDims) * cellDims;          
   vec2 bottomRight = bottomLeft + vec2(cellDims.x, 0.);   

   vec4 d0, d1;   
   d0.xy = vec2(dot(fwdSlash, bottomLeft)); // fd0
   d0.zw = vec2(dot(bckSlash, bottomRight)); // bd0

   d1.x = dot(fwdSlash, bottomRight); // fd2
   d1.y = dot(fwdSlash, uv); // fd1

   d1.z = dot(bckSlash, bottomLeft); // bd2    
   d1.w = dot(bckSlash, uv); // bd1                                   

   vec4 d2 = abs(d1 - d0);    

   float percent = lineWidth * (0.5 - gradientWidth);

   vec4 dMinMax = d2.xzxz;
   dMinMax.xy *= percent;
   dMinMax.zw *= (1. - percent);    

   float cutoff = isLight 
       ? dMinMax.z
       : dMinMax.x;  

   vec3 compareLeft = vec3(d2.yw, cutoff);
   vec3 compareRight = vec3(dMinMax.xy, d2.y);

   bvec3 result = lessThanEqual(compareLeft, compareRight);

   float color = 0.;    
   if (any(bvec2(result.x, all(result.yz))))
   {
      color = 0.;
   }
   else
   {              
      vec2 halves = vec2(lineWidth, dMinMax.z + dMinMax.x) * 0.5;       

      vec2 lightMin = d2.xz * halves.x;   

      vec3 lightGrad = abs(vec3(dMinMax.x, lightMin.x, d2.y) - halves.y);

      vec3 low = vec3(lightGrad.x, dMinMax.x, dMinMax.y);
      vec3 high = vec3(lightGrad.y, lightMin.xy);
      vec3 value = vec3(lightGrad.z, d2.y, d2.w);

      vec3 blending = smoothstep(low, high, value);      


      vec4 shadeLeft = vec4(d2.yw, lightMin.xy);
      vec4 shadeRight = vec4(lightMin.xy, d2.yw);

      bvec4 shadeTest = greaterThanEqual(shadeLeft, shadeRight);


       float factor = 1.;       
       if (isLight)
       {                       
          factor = blending.x;                    
       }      
       else 
       {
          bvec3 darkBlend0 = bvec3(!result.x, shadeTest.zy);

          //if (!result.x && shadeTest.z && shadeTest.y)
          if (all(darkBlend0))
   	      {
       		 factor = blending.y; 
  		  }                                   

          bvec3 darkBlend1 = bvec3(!result.y, shadeTest.wx);

          //if (!result.y && shadeTest.w && shadeTest.x)
          if (all(darkBlend1))
   	      {
       		 factor = blending.z; 
  		  } 

          if (all(shadeTest.wz))
    	  // if (shadeTest.w && shadeTest.z)
    	  {         
    	     factor = clamp(blending.y * blending.z, 0., 1.0); 
          }            
       }              

       color = factor;
   }
   return color;
}


vec3 ComputeLighting(vec3 point, vec2 uv, vec3 lightDir, vec3 lightColor,
	float cellSize, vec2 screenRect, float lineWidth, float gradientWidth)
{
    vec3 color = vec3(AMBIENT);
    float diffuse = dot(EstimateNormal(point), normalize(lightDir));
    float colorIntensity = ceil(diffuse * SHADES) / SHADES;
    colorIntensity = max(colorIntensity, AMBIENT);    

    if (diffuse >= LIGHT_SHADE_CUTOFF)        
    	color = lightColor * colorIntensity;
    else if (diffuse >= DARK_SHADE_CUTOFF) 
    {
		float lightShade =twinShadeC(
            true,
            uv,
            cellSize,
            screenRect,
            lineWidth,
            gradientWidth
        );        
        color = lightShade * colorIntensity * lightColor;
    }
     else
     {
      	float darkShade = twinShadeC(
            false,
            uv,
            cellSize,
            screenRect,
            lineWidth,
            gradientWidth
        );     
        color = darkShade * colorIntensity * lightColor;
     }
    return color;
}

// SHOULD BE SQUARE ON SCREEN NOT RECTANGULAR
#define SUBDIVISION_LOOP_IN_SECS 18.0
#define NO_OF_DIVISIONS 4

#define MID_POINT 0.4
#define TWO_PI 6.283185307179586476925286766559
#define FREQUENCY 0.255
#define RANGE_OF_TRIG_FUNC 0.2

#define GRAD_MID_POINT 0.25
#define GRAD_FREQUENCY 0.155
#define GRAD_RANGE_OF_TRIG_FUNC 0.2

// NEW CODE HERE (END)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 viewDir = RayDirection(45.0, iResolution.xy, fragCoord);
    vec3 origin = vec3(sin(iTime) * 9.0, (sin(iTime * 2.0) * 4.0) + 6.0, cos(iTime) * 9.0);
    mat4 viewTransform = LookAt(origin, vec3(0.0), vec3(0.0, 1.0, 0.0));
    viewDir = (viewTransform * vec4(viewDir, 0.0)).xyz;

    float edgeLength = MAX_DIST;
    float dist = March(origin, viewDir, MIN_DIST, MAX_DIST, edgeLength);

    if (dist > MAX_DIST - EPSILON) // No hit
    {
        fragColor = vec4(0.6);
        return;
    }

    if (dist < EPSILON) // Edge hit
    {
        fragColor = vec4(0.0);
        return;
    }

    vec3 hitPoint = origin + (dist * viewDir);
    vec3 lightDir = vec3(sin(iTime * 2.0) * 6.0, 4.0, sin(iTime * 1.25) * 5.0);

    // SELECTING CELL SIZE
    float gapTimeStep = mod(iTime, SUBDIVISION_LOOP_IN_SECS) / SUBDIVISION_LOOP_IN_SECS;

    float sections[4] = float[](    
        4.,
        8.,
        12.,
        16.
   ); 

   float lineWidth = MID_POINT + RANGE_OF_TRIG_FUNC * sin( FREQUENCY * TWO_PI * iTime);    

   float gradientWidth = GRAD_MID_POINT + GRAD_RANGE_OF_TRIG_FUNC * sin( GRAD_FREQUENCY * TWO_PI * iTime);    

   float cellSize = sections[int(gapTimeStep * float(NO_OF_DIVISIONS))];     

   vec2 uv = fragCoord.xy / iResolution.xy; 
   vec3 color = ComputeLighting(hitPoint, uv, lightDir, TORUS_COLOR, cellSize, iResolution.xy, lineWidth, gradientWidth);

   fragColor = vec4(color, 1.0);
}

