const float PI = atan(1.0) * 4.0;
const float TAU = atan(1.0) * 8.0;

const float rad60 = (atan(1.0) * 4.0) / 3.0;
const float rad120 = (atan(1.0) * 4.0) / 1.5;
const vec2 vec000 = vec2(1.0, 0.0);
const vec2 vec060 = vec2(cos(rad60), sin(rad60));
const vec2 vec120 = vec2(cos(rad120), sin(rad120));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;
    float scale = 10.0;
    scale *= (cos(iTime * TAU / 8.0) + 1.0);

    fragColor = vec4(0.0);

    float polkadotMask = 0.0;

    polkadotMask += cos(abs(dot(pos, vec000)) * scale * TAU);
    polkadotMask += cos(abs(dot(pos, vec060)) * scale * TAU);
    polkadotMask += cos(abs(dot(pos, vec120)) * scale * TAU);


    fragColor.rg = polkadotMask * mouse;
}

vec2 coord;


float plot_sun(vec2 center, float radius) {
    return 1.0 - smoothstep(radius * 0.7, radius * 1.1, distance(coord, center));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    coord = fragCoord.xy/iResolution.yy;

    // Sea parameters
    bool is_sea = false;    
    float sea_line = 0.3;

    // Sun parameters
	float sun_size = 0.1;

    vec2 sun_pos;
    sun_pos.x = 0.9;
    sun_pos.y = 1.0 - fract(iTime * 0.1);

	vec3 sun_color;
    sun_color.r = 1.000;
    sun_color.g = clamp(sun_pos.y * 1.2, 0.3, 1.0);
    sun_color.b = clamp(sun_pos.y * 1.0, 0.2, 1.0);

    // Sky parameters
	vec3 sky_color = vec3(0.5, 0.6, 0.9);

    if (coord.y < sea_line) {
    	coord.y = sea_line + (sea_line - coord.y) / 4.0;   
        is_sea = true;
    }

    float ratio = pow(abs(1.0 - distance(coord, sun_pos)), 1.5);

    vec3 color;
    color = mix(sky_color, sun_color, ratio);
    color = mix(color,sun_color, plot_sun(sun_pos, sun_size));
    if (is_sea) color -= 0.02;

    fragColor = vec4(color,1.0);
}

const mat2 rot180 = mat2(cos(3.14159),sin(3.14159),-sin(3.14159),cos(3.14159));

