  return vec3(r,g,b)/256.;
}

float sineCurve(vec2 p){
  return p.x-sin(3.*p.y-8.*iTime);
}

vec2 gradSineCurve(vec2 p){
  vec2 h = vec2( 0.1, 0.0 );
    return vec2( sineCurve(p+h.xy) - sineCurve(p-h.xy),
                 sineCurve(p+h.yx) - sineCurve(p-h.yx) )/(2.0*h.x);
}

float boxRegion(vec2 p,float a, float b){
  return min(1.-abs(a*p.x),1.-abs(b*p.y));
}

float pseudoCircle(vec2 p,float r, float alpha){
  float f = pow(abs(p.x),alpha)+pow(abs(p.y),alpha);
  return r-pow(abs(f),1./alpha);
}

float triangle(vec2 p,float a,float b){
  return min(p.y,min(p.x-p.y+0.5,-p.x-p.y+0.5));
}

