  vec2 p = fragCoord.xy/iResolution.xy-0.5;
  p.x*=iResolution.x/iResolution.y;
  p*=0.5;	

  //Curve
  vec2 pcurve = 10.*p;
  float lineCurve = abs(sineCurve(pcurve));
  vec2 gradLineCurve = gradSineCurve(pcurve);
  lineCurve = lineCurve/length(gradLineCurve);
  lineCurve = smoothstep(0.025,0.04,lineCurve)+step(0.,-pcurve.y);
  lineCurve = clamp(lineCurve,0.,1.);

  //Base pencil
  vec3 colBasePencil;

  float height = cos(8.*iTime);
  height*=height;

  vec2 ppencil = (m*(p-vec2(0.0,0.05*height+0.07)));
  float basePencil = boxRegion(ppencil,15.,5.);
  basePencil = step(0.5,basePencil);
  basePencil = max(step(0.,pseudoCircle(30.*ppencil+vec2(0.,-3.),1.,2.5)),basePencil);

  float bandcircle = step(0.,length(ppencil)-0.05)*step(0.,0.07-length(ppencil));
  colBasePencil = vec3(1.0)*basePencil;
  colBasePencil = mix(colBasePencil,rgbcol(102.,204.,255.),bandcircle);
  colBasePencil = mix(colBasePencil,0.85*colBasePencil,step(0.02,-ppencil.x)*basePencil);

  //Corner pencil
  vec3 colCornerPencil;
  float cornerPencil = step(0.,triangle(vec2(15.,12.)*(rot180*ppencil+vec2(0.,-0.1)),1.,2.));

  colCornerPencil = rgbcol(244.,240.,194.)*cornerPencil;
  colCornerPencil = mix(colCornerPencil,rgbcol(102.,204.,255.),step(0.125,-ppencil.y)*cornerPencil);
  colCornerPencil = mix(colCornerPencil,0.85*colCornerPencil,step(0.0,-ppencil.x-0.6*ppencil.y-0.08)*cornerPencil);

  //Layer combination
  vec3 col = rgbcol(45.,53.,89.);
  col = mix(rgbcol(102.,204.,255.),col,lineCurve);
  col = mix(col,colBasePencil,basePencil);
  col = mix(col,colCornerPencil,cornerPencil);

  fragColor = vec4(col,1.0);
}

