const int MAX_RAY_SCALE = 255;
const float MAX_DIST = 100.0;
const float MIN_DIST = 0.0;
const float SCALE = 0.0001;

float cubeSDF(vec3 p){

    vec3 d = abs(p)-vec3(1.0,1.0,1.0);

    float insideDistance  = min(max(d.x,max(d.y,d.z)),0.0);

    float outsideDistance = length(max(d,0.0));

    return insideDistance+outsideDistance;

}


float sphereSDF(vec3 p){


   return length(p)-1.0;

}


float sceneSDF(vec3 p){


   return cubeSDF(p)-0.1;


}


float ShortestDistanceToObjSurface(vec3 eye, vec3 rayDir,float start,float end){

       float depth = start;


    for(int i = 0;i<MAX_RAY_SCALE;i++){

        float dist = sceneSDF(eye+rayDir*depth);

        if(dist<SCALE){

         return depth;        
        }
        depth+=dist;

        if(depth>=end){

        return end;

        }

    }

     return end;
}



vec3 RayDir(vec2 size,vec2 fragcood){

    vec2 xy = fragcood-size/2.0;

    float z = size.y+500.0;

    return normalize(vec3(xy,-z));




}


vec3 createNormal(vec3 p){

return normalize(vec3(

    sceneSDF(vec3(p.x+SCALE,p.y,p.z))-sceneSDF(vec3(p.x-SCALE,p.y,p.z)),
    sceneSDF(vec3(p.x,p.y+SCALE,p.z))-sceneSDF(vec3(p.x,p.y-SCALE,p.z)),
    sceneSDF(vec3(p.x,p.y,p.z+SCALE))-sceneSDF(vec3(p.x,p.y,p.z-SCALE))

));

}




vec3 PhongLight(vec3 k_d,vec3 k_s,vec3 eye,vec3 p,vec3 LightPos,vec3 LightColor,float alpha){

     vec3 N = createNormal(p);
     vec3 L = normalize(LightPos-p);
     vec3 V = normalize(eye-p);
     vec3 R = normalize(reflect(-L,N));

     float LdotN = max(dot(L,N),0.0);

     float RdotN = max(dot(R,N),0.0);

    if(LdotN<0.0){

       return vec3(0.0,0.0,0.0);

    }

    if(RdotN<0.0){


       return LightColor*LdotN*k_d;

    }




    return LightColor*LdotN*k_d+pow(RdotN,alpha)*k_s;




}


vec3 PhongLightPosColor(vec3 k_d,vec3 k_a,vec3 k_s,vec3 eye,vec3 p,float alpha){

    vec3 color = k_a*vec3(0.5,0.5,0.5);

    vec3 LightPos1 = vec3(4.0*sin(iTime),2.0,4.0*cos(iTime));

    vec3 LightColor1 = vec3(0.2,0.2,0.2);

    color+= PhongLight(k_d, k_s, eye, p, LightPos1, LightColor1, alpha);




return color;



}



mat4 ViewMatri(vec3 eye,vec3 center,vec3 up){
    vec3 f = normalize(center-eye);
    vec3 s = normalize(cross(f,up));
    vec3 u = cross(s,f);

return mat4(

    vec4(s,0.0),
    vec4(u,0.0),
    vec4(-f,0.0),
    vec4(0.0,0.0,0.0,1.0)



);

}





