vec4 alphaSub(vec4 color, vec4 colorBg, float dist, float size)
{

    float add = 0.0 + ((dist-(size/5.0))/(size/5.0));

   	float maxr = max(color.r, colorBg.r);
    float maxg = max(color.g, colorBg.g);
    float maxb = max(color.b, colorBg.b);


    if(maxr == colorBg.r)
    {
    	color.r += add; 
        if(color.r > colorBg.r)
        	color.r = colorBg.r;
    }

    else{
    	color.r -= add; 
        if(color.r < colorBg.r)
            color.r = colorBg.r;
    }


    if(maxg == colorBg.g)
    {
    	color.g += add; 
        if(color.g > colorBg.g)
        	color.g = colorBg.g;
    }

    else{
    	color.g -= add; 
        if(color.g < colorBg.g)
            color.g = colorBg.g;
    }

     if(maxb == colorBg.b)
    {
    	color.b += add; 
        if(color.b > colorBg.b)
        	color.b = colorBg.b;
    }

    else{
    	color.b -= add; 
        if(color.b < colorBg.b)
            color.b = colorBg.b;
    }

    return color;
}

