// get factor between 0. and 1. 
// increasing during first second 
// and decreasing during second second periodicaly
float getTimeWaveFactor () {
	return mod(iTime, 2.0) < 1. ? mod(iTime, 1.) : 1. - mod(iTime, 1.);
}

// ex: div(553, 100) = 500
float div (float a, float b) {
	return a - mod(a, b); 
}

// get the closest float from @coord
// with specified @space
// ex: closest(18., 5.) = 20.
float closest (float coord, float space) {
    return mod(coord, space) > space / 2. ? 
        div(coord, space) + space
        : div(coord, space);
}

// get the closest point from @point
// in a @grid
vec2 closestPointInGrid(vec2 point, vec2 grid) {
    return vec2(closest(point.x, grid.x), closest(point.y, grid.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // generate gri
    vec2 grid = vec2(1000., 1000.) / iTime;
    float patternSize = 500. / iTime;

    float timeWaveFactor = max(getTimeWaveFactor(), 0.01);
	float radius = timeWaveFactor * patternSize;

	// distance between the closest point and the current pixel
    vec2 closestPoint = closestPointInGrid(fragCoord.xy, grid);
	float distanceCPToPixel = distance(closestPoint, fragCoord.xy);

	// blue color factor
	float blue = (radius - distanceCPToPixel) / radius;
	fragColor = vec4(
        0., 
    	0., 
    	blue, 
        1.
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float shade = 1.0;

    vec2 bottomPoint = vec2( 0.0, 0.0 );

    float modifier = iTime * 4.0;
    float distanceToBottomPoint = length( pow( uv.x * modifier, uv.y * modifier ) - pow( uv.y * modifier, uv.x * modifier ) );
    float tolerance = 5.0;

    shade *= smoothstep( -tolerance, tolerance, distanceToBottomPoint );

    fragColor = vec4( shade, shade, shade, 1.0 );
}

#define rd(x) cos(x * 3.) + cos((x + .3) * 3.6) * 1.1

void mainImage( out vec4 c, in vec2 f )
{
    vec2 r = iResolution.xy;
    float t = iTime;
    vec2 s = (f / r.xy - .5) * 2. * 10.;
    s.y *= r.y / r.x;
    float m = sqrt(s.x * s.x + s.y * s.y);
    float x = rd(log(m) + t * -.4) * 4.;
    x += rd(m + t * .5);
    x += rd(log(abs(sin(atan(s.y, s.x))) + 3.) + t * .1) * 30.;
    x *= smoothstep(.2, 1., m); 
    c = mix(vec4(.7), vec4(.1), clamp(cos(x), 0., 1.));
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
