const vec3 planeNormal = normalize( vec3( 0.0, 1.0, 0.0 ) ) ;
const vec3 pointOnPlane = vec3( 0.0, -0.33, 0.0 );

const float areaLightIntensity = 2.0;
const vec4 areaLightColor = vec4( 0.97, 0.93, 0.11, 1.0 );
const vec4 ambientColor = vec4( 0.25, 0.25, 0.25, 1.0 );

const vec4 areaLightNormal = vec4( 0.0, 0.0, 1.0, 0.0 );

const vec4[] areaLightVertices = vec4[] 
(
    vec4( 0.0, 0.0, 0.0, 1.0 ),
	vec4( -0.500, -0.500, 0.0, 1.0 ),
	vec4( -0.500, 0.500, 0.0, 1.0 ),
	vec4( 0.500, 0.500, 0.0, 1.0 ),//3
	vec4( 0.500, -0.500, 0.0, 1.0 ),
	vec4( 2.0, 0.000, 0.0, 1.0 ),//5
	vec4( 0.000, 2.0, 0.0, 1.0 ),
	vec4( -2.0, 0.000, 0.0, 1.0 ),
	vec4( 0.000, -2.0, 0.0, 1.0 ),
	vec4( 0.000, 0.000, 0.0, 1.0 )//9
);

const int[] areaLightIndices = int[]
(
	5, 3, 9,
	3, 6, 9,
	6, 2, 9, 
	2, 7, 9, 
	7, 1, 9, 
	1, 8, 9, 
	8, 4, 9, 
	4, 5, 9     
);

