void getWorldRay(mat4 screenToWorldSpaceMatrix, vec2 fragCoord, out vec3 worldRayOrigin, out vec3 worldRayDir)
{
    vec4 screenPos1 = vec4( fragCoord.xy / iResolution.xy * 2.0 - 1.0, 0.0, 1.0 );
    vec4 screenPos2 = vec4( fragCoord.xy / iResolution.xy * 2.0 - 1.0, -0.5, 1.0 );

    vec4 worldPos1 = screenToWorldSpaceMatrix * screenPos1;
    vec4 worldPos2 = screenToWorldSpaceMatrix * screenPos2;

    worldPos1 /= worldPos1.w;
    worldPos2 /= worldPos2.w;

    worldRayOrigin = worldPos1.xyz;
    worldRayDir = normalize( worldPos2.xyz - worldPos1.xyz );
}

vec3 getPointInBasis(vec3 p, vec3 b0, vec3 bx, vec3 by, vec3 bz)
{
    vec3 pb = p - b0;
    return vec3( dot( pb, bx ), dot( pb, by ), dot( pb, bz ) );
}

float cross2(vec2 u, vec2 v)
{
    return u.x * v.y - u.y * v.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat4 cameraMatrix = translationMatrix( 0.0, 3.0, 6.0 ) * rotationMatrix( -0.2 - iMouse.y * 0.001, 0.0, 0.0 );    
    mat4 projectionMatrix = perspectiveMatrix(70.0, iResolution.x/iResolution.y, 0.01, 10.0 );
    mat4 screenToWorldSpaceMatrix = cameraMatrix * inverse( projectionMatrix );

    vec3 worldRayOrigin;   
    vec3 worldRayDir;

    getWorldRay( screenToWorldSpaceMatrix, fragCoord, worldRayOrigin, worldRayDir );

    // raycasting against plane

    vec3 worldPos1;
    bool planeRaycastResult = rayPlaneIntersection( planeNormal, pointOnPlane, worldRayOrigin, worldRayDir, worldPos1 );

    // raycasting against area light triangles

    mat4 areaLightMatrix = translationMatrix( 0.0, 3.0 + sin(iTime), -1.0 ) * 
        rotationMatrix( 0.5 * sin( iTime ), 0.0, 0.0 ) *
        rotationMatrix( 0.0, iTime * 0.25, 0.0 ) *
        rotationMatrix( 0.0, 0.0, iTime );
    mat4 invertedAreaLightMatrix = inverse( areaLightMatrix );

    bool areaLightRaycastResult = false;    
    vec3 worldPos2 = worldPos1;
    vec3 n = normalize( ( areaLightMatrix * areaLightNormal ).xyz );
    float normalCondition = dot( planeNormal, n );
    float polygonArea = 0.0;
    for( int i=0; i<8; i++ )
    {
        int i0 = areaLightIndices[i*3];
        int i1 = areaLightIndices[i*3+1];
        int i2 = areaLightIndices[i*3+2];

        vec3 v0 = ( areaLightMatrix * areaLightVertices[i0] ).xyz;        

        vec3 worldPos;
        bool triangleRaycastResult = false;
        if( rayPlaneIntersection( n, v0, worldRayOrigin, worldRayDir, worldPos ) )
        {        
        	vec4 localPos = invertedAreaLightMatrix * vec4( worldPos.xyz, 1.0 );

        	triangleRaycastResult = pointInsideTriangle( localPos.xy, areaLightVertices[i0].xy, areaLightVertices[i1].xy, areaLightVertices[i2].xy );
            if( triangleRaycastResult )
            {                
        		areaLightRaycastResult = true;
                worldPos2 = worldPos;
            }
        }

        // diffuse light contribution from triangle 

        if( !triangleRaycastResult && planeRaycastResult && normalCondition < 0.9999 )
        {
            vec3 v1 = ( areaLightMatrix * areaLightVertices[i1] ).xyz;
        	vec3 v2 = ( areaLightMatrix * areaLightVertices[i2] ).xyz;            

            vec3 c = (v0 + v1 + v2) / 3.0;
            vec3 bz = normalize( c - worldPos1 );

            float sideEffect = dot( bz, n );            
            if( sideEffect > 0.0 )
            {            
            	vec3 by = vec3( bz.y, bz.z, -bz.x );
            	vec3 bx = normalize( cross( by, bz ) );
            	by = normalize( cross( bz, bx ) );

            	vec3 lv0 = getPointInBasis( v0, worldPos1, bx, by, bz );
            	vec3 lv1 = getPointInBasis( v1, worldPos1, bx, by, bz );
            	vec3 lv2 = getPointInBasis( v2, worldPos1, bx, by, bz );

            	lv0.xy /= lv0.z;
            	lv1.xy /= lv1.z;
            	lv2.xy /= lv2.z;

            	polygonArea += 0.5 * cross2( lv1.xy-lv0.xy, lv2.xy-lv0.xy );                
            }
        }
    }

    float localIntensity = polygonArea * areaLightIntensity;
    vec4 planeColor = ambientColor + areaLightColor * localIntensity;

    vec4 areaLightColor = mix( 
        areaLightColor, 
        areaLightColor * 0.5, 
        step( 0.0, dot( n, worldRayDir ) )
    );

    if( planeRaycastResult )
    {
        if( areaLightRaycastResult )
        {
            float worldDist1 = distance( worldRayOrigin, worldPos1.xyz );
    		float worldDist2 = distance( worldRayOrigin, worldPos2.xyz );

            fragColor = mix(
        		planeColor,
        		areaLightColor,
        		float(worldDist1>worldDist2)
    		);
        }
        else
        {
            fragColor = planeColor;
        }
    }
    else if( areaLightRaycastResult )
    {
        fragColor = areaLightColor;
    }
    else
    {
        fragColor = vec4( 0, 0, 0, 1 );
    }
}

