float EPSILON = 0.001;
int MAX_STEPS = 100;
float MAX_DIST = 100.0;

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

float boxSDF(vec3 p, vec3 size, float r) {
    vec3 d = abs(p) - (size / 2.0) + r;
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance - r;
}

float cylSDF(vec3 p, float h, float r) {
    float a = abs(p.y) - (h/2.0);
    float b = length(p.xz)-r;
    if (b > 0.0 && a > 0.0) {
    	return length(vec2(a,b));
    }

    return max(a,b);
}

float coneSDF(vec3 p, float h, float r) {
    float r2 = ((h/2.0)-p.y)/h;
    r2 *= r;
    r2 = clamp(r2,0.0,r);
    return cylSDF(p,h,r2);
}

float sphereSDF(vec3 p, float size) {
    return length(p) - size;
}

float morph(float SDF1, float SDF2, float m) {
    return SDF1 + ((SDF2-SDF1)*m);
}

float sceneSDF(vec3 p) {
    float sdf = MAX_DIST;
    float a = max(sphereSDF(p-vec3(2.0,0.0,0.0),1.0),boxSDF((p-vec3(2.0,0.0,0.0)),vec3(1.4,1.4,1.4),0.0));
    float b = coneSDF(p-vec3(2.0,0.0,0.0),1.4,0.6);;
    float c = morph(b,a,(1.0+sin(iTime))*0.5);
    sdf = min(sdf,c);
    a = min(sdf,boxSDF(rotateZ(p.y*cos(iTime))*(p+vec3(2.0,0.0,0.0)),vec3(1.4,1.4,1.4),0.2));
    b = min(sdf,boxSDF(rotateY(p.y+sin(iTime*2.0))*(p+vec3(2.0,0.0,0.0)),vec3(1.4,1.4,1.4),0.2));
    c = morph(a,b,(1.0+sin(iTime))*0.5);
    sdf = min(sdf,c);
    a = boxSDF(p,vec3(1.4,1.4,1.4),0.0);
    b = cylSDF(p,1.4,0.6);
    c = morph(a,b,(1.0+sin(iTime))*0.5);
    sdf = min(sdf,c);
    return sdf;
}

float March(vec3 Dir, vec3 eye) {
    float depth = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        float dist = sceneSDF(eye + (Dir*depth));
        if (dist < EPSILON) {
            return depth;
        }
        depth += dist;
        if (depth >= MAX_DIST) {
            return MAX_DIST;
        }
    }
    return MAX_DIST;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

vec3 interp(vec3 a,vec3 b,float t) {
    return a + ((b-a)*t);
}

vec3 Light(vec3 p, vec3 eye, vec3 Amb, vec3 Diff, vec3 Spec) {
    float IntensitySpec = 0.4;
    float IntensityDiff = 0.4;
    float shine = 10.0;
    vec3 source = vec3(sin(2.0*iTime)*3.0,cos(2.0*iTime)*3.0,sin(iTime)*cos(iTime)*4.0);
    //source = vec3(0.0,0.0,0.0);
    vec3 N = estimateNormal(p);
    vec3 L = normalize(source - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));
    Amb = interp(N,Amb,(1.0+cos(iTime))/2.0);
    vec3 col = Amb * 0.5;
    if (dot(L,N) < 0.0) {
        return col;
    }
    if (dot(R,V) < 0.0) {
        col += (Diff * IntensityDiff * dot(L,N));
        return col;
    }
    col += (Diff * IntensityDiff * dot(L,N));
    col += (Spec * pow(dot(R,V),shine));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 eye = vec3(sin(iTime)*5.0,cos(iTime)*5.0,6.5);
    vec3 center = vec3(0.0,0.0,0.0);
    vec3 up = vec3(0.0,1.0,0.0);
    mat3 Matrix = viewMatrix(eye,center,up);
    vec3 dir = rayDirection(45.0,iResolution.xy,fragCoord);
    float mDist = March(Matrix * dir,eye);
    vec3 point = eye + (mDist * (Matrix * dir));
    vec3 light = Light(point, eye, vec3(0.5,0.0,0.0), vec3(1.0,1.0,1.0), vec3(1.0,1.0,1.0));
    if (mDist > MAX_DIST - EPSILON) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
        return;
    }
    fragColor = vec4(light, 1.0);
}

vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color){
    float d = length(pos - uv) - rad;
    float t = clamp(d, 0.0, 1.0);
    return vec4(color, 1.0-t);
}

